insert into arcade_description values (1, 1009, 1, 100901, '这是一个解答5个字空格谜题的猜谜游戏。\\n队伍里的5个人需要齐心协力，每人各自回答1个字吧。\\n谜题涉及的领域有很多种哦。');
insert into arcade_description values (2, 1009, 1, 100902, '为了提供更多乐趣，本次准备了两种游戏模式可供选择。\\n如果想以个人实力进行挑战，请选择与人机游玩的单人模式，\\n如果想与同伴合作游玩，请选择多人模式。');
insert into arcade_description values (3, 1009, 1, 100903, '系统会随机给出游戏提示。\\n如果想得到更多提示，请使用「提示按钮」功能。\\n提示功能的使用次数是有限的，不要随意浪费哦。');
insert into arcade_description values (4, 1009, 1, 100904, '想确认之前遇到过的谜题时，可以使用「档案」功能进行回顾。\\n收集档案，可以获得限定称号等报酬，\\n认真复习，尽情体验猜谜的乐趣吧。');
insert into arcade_description values (5, 1012, 1, 100905, '想教我学习？\u3000真没办法呢，用谜题的形式\\n快乐地以提高学习能力为目标吧！\u3000选择我或者铃奈\\n开启学习会吧。要跟铃奈挑战的话，你要好好教她哦？');
insert into arcade_description values (6, 1012, 1, 100906, '问题会从3种出题形式中随机选出。\\n“四选一问题”和“一问多答”可以使用有次数限定的提示哦！\\n在什么时候使用提示，会是提升成绩的关键呢。');
insert into arcade_description values (7, 1012, 1, 100907, '出现过的问题都会记录在“档案”里面！\u3000复习也能\\n通过这里很好地进行了呢。听说只要填满档案就能\\n获得称号，拿出干劲，以全部填满为目标吧！');
insert into arcade_description values (8, 1012, 2, 100908, '满足条件就会解锁的“勤勉模式”中，可以挑战\\n在规定时间内是否能答对很多问题！\\n这是成长后的你展示实力的好机会！敬请加油！');
