insert into dsb_story_data values (1010801, 10108, '忍耐度比试要适可而止\u200b\u3000火焰果温泉', '2022/12/31 12:00:00', 10108107, 8, 91002, 40, 106701, 106601, 1, '厄尔庇斯岛原产\u3000火焰果、大辣椒', '改善怕冷，促进代谢，保暖效果\\n※请注意不要长时间浸浴', '充分使用了厄尔庇斯岛的\\n类龙生物送的火焰果\\n所打造出来的热情温泉。浸泡其中，汗水\\n就会像瀑布一样喷涌而出。这份火热\\n牢牢抓住了想要挑战\\n自己极限的男男女女的好胜心。');
insert into dsb_story_data values (1010802, 10108, '令人心情舒畅的放松\u3000芳香温泉', '2023/01/01', 0, 8, 91002, 40, 101501, 101601, 2, '精灵之森产\u3000芬芳花朵的精华', '有美肤、放松、保香效果\\n※会引发酸甜的心情', '使用芬芳的鲜花与其香薰精华\\n打造出让人眼前一亮的温泉。令人\\n忘却时间的放松效果\\n符合繁忙都市人的需求。由于\\n极佳的美容效果，成为了年轻女性\\n之间瞩目的热点。');
insert into dsb_story_data values (1010803, 10108, '唤起童心的微炭酸\u3000蜜瓜苏打温泉', '2023/01/02', 0, 8, 91002, 40, 105101, 103601, 3, '女招待特制的碳酸泉、蜜瓜精华', '促进血液循环，去角质的效果\\n回归童心的效果', '充满柔和的微碳酸和甜瓜香气，\\n令人玩心大起的温泉。深月希望做一个让孩子们\\n喜欢的温泉，本温泉正是为了\\n实现这个想法而诞生。就算是平日里稳重的\\n优等生，一泡在浴池里，就会变回这个年龄\\n该有的样子，吵吵闹闹起来。');
insert into dsb_story_data values (1010804, 10108, '心跳流动电击☆\u3000战栗温泉', '2023/01/03', 0, 8, 91002, 40, 106701, 104501, 4, '小老板娘的魔法（电击）', '消除腰痛、肩膀酸痛，促进血液循环\\n战栗狂喜之旅（？）', '本旅馆引以为傲的源泉加上小老板娘的电击，\\n十分刺激的温泉。因能有效缓解\\n身体僵硬而深受欢迎。今天也是从上班族\\n到老人，都来这里缓解酸痛。\\n轻微的疼痛也是导致\\n上瘾的原因之一。');
insert into dsb_story_data values (1010805, 10108, '给你明天的活力\u3000恢复疲劳温泉', '2023/01/04', 0, 8, 91002, 40, 101501, 104801, 5, '精灵之森产\u3000治愈之叶的根', '超·恢复肉体疲劳，超·恢复神经疲劳\\n超·恢复精神疲劳', '以消除一切疲劳而闻名的温泉。\\n调合了精灵之森引以为傲的多种药草，提高了\\n效果的药汤，是疲惫不堪的人们的\\n休憩场所。世上勤劳的人们，都是在这里\\n泡温泉、工作，再泡\\n温泉、工作，如此周而复始。');
insert into dsb_story_data values (1010806, 10108, '粗暴排毒！\u3000火辣辣温泉', '2023/01/05', 0, 8, 91002, 40, 105101, 118001, 6, '女招待特制汉方、稀有辣椒', '脂肪燃烧效果，高排毒效果\\n※请注意不要长时间浸浴', '使用了大量因太过辛辣而无法\\n在市场上流通的辣椒，具有高排毒效果\\n的温泉。因为蒸汽也很刺激，所以有意者可租借\\n护目镜。（请自行向服务台申请）');
insert into dsb_story_data values (1010807, 10108, '高粘度的新感觉温泉\u3000啫喱怪温泉', '2023/01/06', 0, 8, 91002, 40, 106701, 106001, 7, '小老板娘特制的药剂、充满活力的啫喱怪', '恢复魔力，美肤效果，保湿效果\\n※有被温泉轻咬的可能', '这是将恢复魔力的药剂和啫喱怪\\n混合在一起的特色温泉。啫喱怪们是\\n小老板娘挖来的临时工。\\n这个奇妙温泉的魔力恢复效果，\\n在热爱新事物的魔法师和\\n和魔族之间成为了话题。');
insert into dsb_story_data values (1010808, 10108, '给身心清爽的治愈♪\u3000清凉温泉', '2023/01/07', 0, 8, 91002, 40, 101501, 102101, 8, '精灵之森产\u3000清凉草籽油', '清凉效果，喉咙·鼻子的保湿效果\\n※哼歌有可能传到旅馆内', '用具有清凉效果的药草调配而成的药温泉。\\n吸入蒸汽后，喉咙和鼻子都得到了保湿，\\n声音会变得特别动听，因此备受好评。\\n再加上泡完温泉后的清爽感，\\n回头客很多。歌手和偶像们\\n也会偷偷来造访。');
insert into dsb_story_data values (1010809, 10108, '啊啊玫瑰色的人生\u3000美肤温泉', '2023/01/08', 0, 8, 91002, 40, 105101, 100801, 9, '女招待特制超美肤药、大朵玫瑰', '美肤、保湿、保暖效果\\n玫瑰香味带来的放松效果', '总而言之追求让皮肤更美丽的\\n宝藏温泉。据说只要稍微泡一下，\\n就能感受到不同，目前正在探索\\n如何售卖使用此温泉水制作的化妆水。\\n顺带一提玫瑰只是用来增加香气的，\\n并没有美肤效果。');
