insert into login_bonus_message_data values (124, 80003, 0, 1, 0, 0, 105801, '佩可莉姆', '小凯露，其实是这样的！\\n一个对我们来说非常重要的节日\\n马上就要到了——', 2, 0, '');
insert into login_bonus_message_data values (125, 80003, 0, 1, 0, 0, 105801, '佩可莉姆', '那就是公主连结！\\nRe:Dive\\n2.5周年纪念日哦！\\n超棒的对吧☆', 3, 0, '');
