insert into login_bonus_message_data values (127, 80003, 2, 1, 0, 0, 106001, '凯露', '啊，说起来还真是，\\n不过就算是快到那个时候了，\\n也还有一段时间吧，\\n会不会太心急了？', 3, 0, '');
insert into login_bonus_message_data values (128, 80003, 1, 1, 0, 0, 105901, '可可萝', '不，凯露小姐，\\n这种庆典就是要从准备阶段开始\\n好好享受。对吧，佩可莉姆小姐。', 4, 0, '');
insert into login_bonus_message_data values (129, 80003, 0, 1, 0, 0, 105801, '佩可莉姆', '就是这样♪\u3000综上所述！\\n倒计时开始了哦！\\n大家一起热闹地庆祝2.5周年吧☆', 4, 0, '');
insert into login_bonus_message_data values (130, 80003, 2, 1, 0, 0, 106001, '凯露', '咚、咚咚啪啪～\\n你们让我都说些什么呢！\\n真是的，完全兴奋过头了啊。', 4, 0, '');
insert into login_bonus_message_data values (131, 80003, 1, 1, 0, 0, 105901, '可可萝', '呵呵。凯露小姐似乎也很期待呢。\\n那么，主人。在重要的纪念日到来之前，\\n就请好好地期待吧。', 5, 0, '');
insert into login_bonus_message_data values (132, 80003, 0, 2, 0, 0, 106001, '凯露', '说是要热闹地庆祝纪念日，\\n但到底要做点什么好呢？\\n我不太擅长做这些……', 5, 0, '');
insert into login_bonus_message_data values (133, 80003, 1, 2, 0, 0, 190801, '花凛', '哎呀，凯露小姐\\n怎么了？\\n看你似乎有什么烦恼。', 1, 0, '');
insert into login_bonus_message_data values (134, 80003, 0, 2, 0, 0, 106001, '凯露', '呃……这不是花凛嘛。\\n我没事！', 6, 0, '');
insert into login_bonus_message_data values (135, 80003, 1, 2, 0, 0, 190801, '花凛', '是这样吗？\\n毕竟是凯露小姐，\\n我还以为是在烦恼2.5周年要怎么庆祝呢……', 2, 0, '');
insert into login_bonus_message_data values (136, 80003, 0, 2, 0, 0, 106001, '凯露', '你这不是知道的嘛！？\\n不、不是的，不是这样的！\\n我可没有为这种事烦恼哦？', 7, 0, '');
