insert into login_bonus_message_data values (140, 80003, 1, 2, 0, 0, 190801, '花凛', '原来如此原来如此～\\n也就是说，凯露小姐由衷地\\n期待着【美食殿堂】2.5周年的\\n庆祝活动呢！', 4, 0, '');
