insert into login_bonus_message_data values (146, 80003, 0, 3, 0, 0, 105901, '可可萝', '是的，没错。\\n我也听主人说过，\\n除了【美食殿堂】的活动之外，\\n还有很多其他的经历呢。', 7, 0, '');
insert into login_bonus_message_data values (147, 80003, 0, 3, 0, 0, 105901, '可可萝', '我记得……好像是在兰德索尔\\n遇见了传闻中的魔法少女之类的。', 8, 0, '');
