insert into sjr_chara values (1, 122601, 1, 105, 160, 110, 1.5, 1, 101, 0.029, '和朋友一起享受夏天，夏日海边的魔法士。抱着想要改变自己的坚定决心，让障碍物无效化，继续奔跑。', 1, 0, 0);
insert into sjr_chara values (2, 122401, 3, 111, 120, 105, 2.5, 2, 102, 0.014, '比太阳还灿烂的夏日海边元气少女。\u3000凭借天生的身体能力，无论什么路线都能灵活应对，跑完全程。', 0, 1, 0);
insert into sjr_chara values (3, 122501, 2, 105, 350, 110, 2.5, 3, 103, 0.017, '在海浪中闪耀的夏日海边剑士。\u3000凭借每天锻炼的双腿，面对长距离也可以毫不减速地跑完。', 0, 0, 1);
insert into sjr_chara values (4, 107501, 3, 105, 130, 110, 1.5, 4, 104, 0.016, '热爱海鲜的海边公主。\u3000解放“皇家装备”的力量，不畏障碍物向前进。', 0, 1, 0);
insert into sjr_chara values (5, 107601, 1, 105, 160, 110, 2.5, 5, 105, 0.014, '在海边也要照顾主人的白沙滩巫女。用精灵的力量加速自身，比谁都快地冲向终点。', 1, 0, 0);
insert into sjr_chara values (6, 107801, 2, 103, 255, 120, 3.5, 6, 106, 0.029, '不好意思穿泳衣的傲娇猫咪少女。用擅长的魔法，妨碍对手的奔跑，让竞速赛变得对自己有利。', 0, 0, 1);
insert into sjr_chara values (7, 110601, 1, 103, 160, 120, 3.5, 7, 107, 0.013, '在沙滩上优雅奔跑，盛夏的真步公主。迈着优雅的步伐，飞跃重重障碍。', 1, 0, 0);
insert into sjr_chara values (8, 110401, 2, 103, 350, 110, 2.5, 8, 108, 0.014, '守护海滨和平的盛夏狼少女。\u3000\u3000凭借兽人强韧的耐力，在竞速赛\u3000后半段冲刺。', 0, 0, 1);
insert into sjr_chara values (9, 110501, 3, 100, 130, 105, 1.5, 9, 109, 0.012, '在海滨上劲舞，盛夏的南国少女。\u3000在竞速赛的喧闹声中也毫不动摇，后半段会奋起直追。', 0, 1, 0);
insert into sjr_chara values (10, 115701, 1, 103, 200, 105, 2.5, 10, 110, 0.014, '嗅到海滨事件气息，盛夏的\u3000\u3000名侦探。借高超的推理能力和魔法，准确打乱对手的节奏。', 1, 0, 0);
insert into sjr_chara values (11, 113601, 2, 103, 350, 105, 2.5, 11, 111, 0.071, '再次脱下铠甲，灼热的骑士团长。用实战中培养出来的判断力，瞬间展开屏障，使障碍物无力化。', 0, 0, 1);
insert into sjr_chara values (12, 107901, 3, 103, 130, 120, 1.5, 12, 112, 0.014, '怕水，海边随心所欲的猫少女。出现在海滨的美少女义贼，夺取了对战对手的速度。', 0, 1, 0);
insert into sjr_chara values (13, 108001, 2, 105, 200, 110, 1.5, 13, 113, 0.022, '休假也是效率第一的海之佣兵。在竞速赛中也追求最高效率，在任何情况下都表现得无懈可击。', 1, 0, 0);
insert into sjr_chara values (14, 110301, 1, 108, 160, 110, 2.5, 14, 114, 0.031, '与盛夏的阳光很相配的海边的\u3000咲恋妈妈。多才多艺，灵巧，无论什么样的\u3000障碍都能优雅地越过。', 1, 0, 0);
insert into sjr_chara values (15, 107701, 1, 105, 130, 110, 0.5, 15, 115, 0.036, '在海上呼风唤雨的夏日冒失女仆。平时就习惯面对灾难，面对\u3000小麻烦都不会动摇', 0, 1, 0);
insert into sjr_chara values (16, 113101, 2, 95, 350, 110, 3.5, 16, 116, 0.048, '享受潇洒夏天的初夏的大姐头。\u3000\u3000以流畅的身体动作，在开始、中途、结束，每重复一次连结爆发，就加速一次。', 0, 0, 1);
insert into sjr_chara values (17, 113301, 3, 105, 130, 105, 1.5, 17, 117, 0.043, '感受到夏日心动的御宅魔法少女。操纵多种魔法，一点点地持续降低其他跑者的速度。', 0, 1, 0);
insert into sjr_chara values (18, 113201, 2, 95, 130, 105, 1.5, 18, 118, 0.018, '降临海狱，盛夏之冥姬。\u3000\u3000在竞速赛后半段魔力完全释放，\u3000\u3000化作苍蓝疾风在海滨狂奔。', 0, 1, 0);
insert into sjr_chara values (19, 117001, 2, 108, 160, 110, 2.5, 19, 119, 0.021, '用火花点缀夏日傍晚，海滨破坏者。用“破坏狂”的绰号与极度热情给对手带来压力。', 1, 0, 0);
insert into sjr_chara values (20, 113501, 1, 105, 255, 110, 2.5, 20, 120, 0.036, '引领正值青春之人，夏日的老师。向着梦想迈进的圣母，保佑她避开各种困难。', 0, 0, 1);
insert into sjr_chara values (21, 113401, 3, 105, 350, 105, 2.5, 21, 121, 0.02, '笑容应援很耀眼，夏日的超能力\u3000辣妹。运用超能力，一跳就能越过任何障碍物。', 0, 0, 1);
insert into sjr_chara values (22, 110101, 1, 105, 130, 110, 2.5, 22, 122, 0.018, '在海边散发成熟魅力的夏日\u3000\u3000性感老师。被这种魅力所困住的对手，会乱了阵脚。', 0, 1, 0);
insert into sjr_chara values (23, 110001, 3, 111, 160, 105, 3.5, 23, 123, 0.03, '绝不会脱下泳衣，夏日的知名模特。跑起步来超有激情，速度也快得不行。', 1, 0, 0);
insert into sjr_chara values (24, 117201, 1, 103, 130, 110, 1.5, 24, 124, 0.027, '给南方岛屿带来活力，盛夏的偶像。即使天气恶劣也要保持笑容，后半场加快节奏跑完全程。', 0, 1, 0);
insert into sjr_chara values (25, 117301, 2, 108, 160, 105, 3.5, 25, 125, 0.02, '美妙歌声响彻大海，海滨的歌姬。\u3000凭借守护赞歌和精灵加护，\u3000保护自己不受障碍物影响。', 1, 0, 0);
insert into sjr_chara values (26, 117401, 3, 105, 350, 110, 2.5, 26, 126, 0.04, '夏天也要唱反调，平静的裁缝。用遍布的魔法线，剥夺其他跑者的自由。', 0, 0, 1);
insert into sjr_chara values (27, 117101, 1, 105, 350, 110, 3.5, 27, 127, 0.009, '和向日葵很相配，海边的\u3000\u3000\u3000姐姐。是秘密结社的一员，\u3000出众的身体能力是最好的武器。', 0, 0, 1);
insert into sjr_chara values (28, 122901, 1, 105, 255, 120, 3.5, 28, 128, 0.022, '在岸边温顺乖巧，夏日色彩的兔兔少女。\u3000兔子力量全开，在跑道上蹦蹦跳跳。', 0, 0, 1);
insert into sjr_chara values (29, 122801, 3, 105, 130, 110, 2.5, 29, 129, 0.028, '天真无邪，夏日色彩的恶作剧少女。即使在沙滩上，也准备好了恶作剧！ 竞速赛即将发生重大变故！？', 0, 1, 0);
insert into sjr_chara values (30, 122701, 2, 108, 130, 105, 3.5, 30, 130, 0.023, '在海边也很认真的夏日色彩优等生。\u3000\u3000释放提高的魔力，在一瞬间将其他跑者都抛在身后。', 1, 0, 0);
