insert into unit_enemy_data values (214801, '望的暗影', 214801, 0, 4, 450, 160, 1, 1.965, 0, 0, 0, '拥有望外貌的特殊魔物\\n\\n・【挑衅】小幅提升我方全体的物理攻击力，\\n\u3000大幅提升自身的物理防御力和魔法防御力。\\n\u3000并且对敌方全体进行挑衅。\\n・对前方范围内的所有敌人造成物理伤害（小），\\n\u3000并使其眩晕。\\n・中幅回复自身周围的所有我方角色的生命值。');
insert into unit_enemy_data values (214901, '矛依未的暗影', 214901, 0, 2, 450, 240, 1, 1.8, 0, 0, 0, '拥有矛依未外貌的特殊魔物\\n\\n・对前方一名敌人造成物理伤害（特大）。\\n\u3000直到技能值耗尽前会一直装备\\n\u3000天楼霸断剑。\\n・对前方范围内的所有敌人造成物理伤害（小），\\n\u3000使其眩晕并小幅度击飞敌人。\\n・小幅降低前方范围内的所有敌人的\\n\u3000物理防御力。');
insert into unit_enemy_data values (215001, '嘉夜的暗影', 215001, 0, 1, 450, 320, 1, 2.17, 0, 0, 0, '拥有嘉夜外貌的特殊魔物\\n\\n・对前方一名敌人造成物理伤害（特大）\\n\u3000战斗开始后，大幅提升第一次使用连结爆发\\n\u3000造成的伤害量。\\n・对前方一名敌人造成物理伤害（中）。\\n・大幅提升自身的物理攻击力和\\n\u3000物理暴击率。');
