insert into unit_enemy_data values (300100, '弥诺陶洛斯', 300100, 0, 3, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】获得强大力量的金牛座魔星兽。\\n其战斧的攻击超越了力量的极限，能将一切粉碎。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·弥诺陶洛斯的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n\\n·必杀技能会根据弥诺陶洛斯的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000烧伤状态。同时中幅提升\\n\u3000自身的物理攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最近的角色造成物理伤害（大），\\n\u3000将其击退的同时使其眩晕。\\n\u3000随后，当角色的生命值低于60%时，\\n\u3000则大幅提升自身的物理攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方3名角色造成魔法伤害（中），\\n\u3000并大幅降低其物理攻击力和魔法攻击力。');
insert into unit_enemy_data values (300101, '弥诺陶洛斯', 300101, 0, 3, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】获得强大力量的金牛座魔星兽。\\n其战斧的攻击超越了力量的极限，能将一切粉碎。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·弥诺陶洛斯的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n\\n·必杀技能会根据弥诺陶洛斯的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000烧伤状态。同时中幅提升\\n\u3000自身的物理攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最近的角色造成物理伤害（大），\\n\u3000将其击退的同时使其眩晕。\\n\u3000随后，当角色的生命值低于60%时，\\n\u3000则大幅提升自身的物理攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方3名角色造成魔法伤害（中），\\n\u3000并大幅降低其物理攻击力和魔法攻击力。\\n·对前方第一名、第二名敌人造成物理伤害（特大），\\n\u3000并使其眩晕。随后，\\n\u3000当两名角色的生命值都低于60%时，\\n\u3000大幅提升自身的物理攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (300102, '弥诺陶洛斯', 300102, 0, 3, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】获得强大力量的金牛座魔星兽。\\n其战斧的攻击超越了力量的极限，能将一切粉碎。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·弥诺陶洛斯的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n\\n·必杀技能会根据弥诺陶洛斯的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000烧伤状态。同时中幅提升\\n\u3000自身的物理攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最近的角色造成物理伤害（大），\\n\u3000将其击退的同时使其眩晕。\\n\u3000随后，当角色的生命值低于60%时，\\n\u3000则大幅提升自身的物理攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方3名角色造成魔法伤害（中），\\n\u3000并大幅降低其物理攻击力和魔法攻击力。\\n·对前方第一名、第二名敌人造成物理伤害（特大），\\n\u3000并使其眩晕。随后，\\n\u3000当两名角色的生命值都低于60%时，\\n\u3000大幅提升自身的物理攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·大幅提升自身和敌方全体的物理攻击力、魔法攻击力和行动速度，\\n\u3000并大幅降低物理防御力和\\n\u3000魔法防御力。并持续回复自身的\\n\u3000技能值。');
insert into unit_enemy_data values (300200, '利蝎巴鲁托', 300200, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '使用猛毒的尾棘散播灾厄的天蝎座魔星兽。\\n守护沙漠的神虫失去信仰后，变成了邪神。\\n\\n·对敌方全体造成大量物理伤害。\\n\u3000BOSS每次使用必杀技能，\\n\u3000都会提升对敌人造成的伤害。\\n\u3000这个效果最多可以累积三次。\\n·对前方范围内的敌人造成中等物理伤害，\\n\u3000并赋予中毒状态。\\n·解除自身的属性值降低效果，\\n\u3000并提升自己的物理防御力。');
insert into unit_enemy_data values (300201, '利蝎巴鲁托', 300201, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】被遗留在失去了信仰的神殿里的天蝎座魔星兽。\\n忘却了曾经获得过的虔诚祈祷，如今将带来毁灭。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·利蝎巴鲁托的所有伤害\\n\u3000都会使队伍的技能值回复量\\n\u3000小幅降低。\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（中）。\\n\u3000使用该技能后，每次攻击敌人，\\n\u3000都将提升利蝎巴鲁托下一次对该敌人\\n\u3000所造成的伤害。\\n\u3000这个效果最多可以累积3次。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·以最近一名角色为中心，对小范围内的敌人\\n\u3000造成物理伤害（中），并使其陷入中毒状态。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。\\n·中幅提升自身的物理攻击力、物理防御力\\n\u3000和魔法防御力。');
