insert into unit_enemy_data values (300302, '梦魇牡羊', 300302, 0, 7, 270, 1000, 2, 2.5, 0, 0, 0, '【魔法】获得强大力量的白羊座魔星兽。\\n在阴暗的沉睡监牢中将冒险者的灵魂吞噬殆尽。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·梦魇牡羊的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。对无法行动的角色\\n\u3000追加造成魔法伤害（大），并大幅降低\\n\u3000其技能值。\\n\u3000该技能所造成的追加伤害\\n\u3000不会使角色的技能值回复。\\n·根据角色的最大生命值，对前方范围内的敌人\\n\u3000造成一定比例的魔法伤害。\\n·无视敌方的挑衅，并使剩余生命值比例最低的一名敌人陷入束缚状态。当角色的生命值低于60%时，\\n\u3000以该角色为中心，在小范围内赋予束缚状态。\\n·解除敌方全体的屏障，\\n\u3000并根据角色的最大生命值，对敌方全体造成一定比例的魔法伤害，\\n\u3000同时使其陷入短暂的束缚状态。若角色的生命值低于\\n\u300060%，则使其陷入长时间的束缚状态。\\n·解除敌方全体的屏障，\\n\u3000并根据角色的最大生命值，对敌方全体造成一定比例的魔法伤害，\\n\u3000大幅降低其物理攻击力，\\n\u3000中幅降低魔法攻击力。随后特大幅度降低敌方全体的生命值吸收\\n\u3000和生命值回复量。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (300400, '摩羯教主', 300400, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '企图利用可疑邪教支配世界的摩羯座魔星兽。\\n会引诱沙漠里的迷途之人，用恶魔之仪式将其变为俘虏。\\n\\n·必杀技能对敌方全体造成魔法伤害（大）\\n\u3000另外降低物理攻击角色的物理攻击力\\n\u3000降低魔法攻击角色的魔法防御力。\\n·对物理攻击力最高的一名角色造成魔法伤害（特大），\\n\u3000并降低其技能值。\\n·对前方两名敌人造成魔法伤害（小），并\\n\u3000赋予沉默状态。\\n\u3000并对敌方全体造成魔法伤害（中）。');
insert into unit_enemy_data values (300401, '摩羯教主', 300401, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·摩羯教主的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会大幅降低敌方所有物理攻击角色的\\n\u3000物理攻击力、所有魔法攻击角色的\\n\u3000魔法攻击力。\\n·对最近的一名敌人造成魔法伤害（大），\\n\u3000并赋予沉默状态。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成魔法伤害（大），\\n\u3000并中幅降低其技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。');
insert into unit_enemy_data values (300402, '摩羯教主', 300402, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·摩羯教主的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会大幅降低敌方所有物理攻击角色的\\n\u3000物理攻击力、所有魔法攻击角色的\\n\u3000魔法攻击力。\\n·对最近的一名敌人造成魔法伤害（大），\\n\u3000并赋予沉默状态。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成魔法伤害（大），\\n\u3000并中幅降低其技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并赋予沉默状态。');
insert into unit_enemy_data values (300403, '摩羯教主', 300403, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\\n\\n·必杀技能对敌方全体造成魔法伤害（大）\\n\u3000并降低使用物理攻击角色的物理攻击力\\n\u3000降低使用魔法攻击角色的魔法攻击力。\\n·对前方一名敌人造成魔法伤害（特大）\\n\u3000并赋予沉默状态。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n·对物理攻击力最高的一名敌人\\n\u3000造成魔法伤害（大），并降低其技能值。\\n·当摩羯教主获得毒、猛毒、诅咒、烧伤\\n\u3000效果时，赋予自身陷入沉默状态，\\n\u3000并受到魔法伤害（大）。\\n\u3000没有获得上述效果时，则对敌方全体\\n\u3000赋予沉默状态，\\n\u3000并造成魔法伤害（大）。');
insert into unit_enemy_data values (300404, '摩羯教主', 300404, 0, 7, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】在邪教秘术的领域登峰造极的摩羯座魔星兽。\\n能随心所欲地操纵其庞大的魔力，剥夺敌人的自由。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·摩羯教主的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会大幅降低敌方所有物理攻击角色的\\n\u3000物理攻击力、所有魔法攻击角色的\\n\u3000魔法攻击力。\\n\u3000同时解除敌方全体的\\n\u3000属性值提升效果。\\n·对最近的一名敌人造成魔法伤害（大），\\n\u3000并赋予沉默状态。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成魔法伤害（大），\\n\u3000并中幅降低其技能值。\\n\u3000随后中幅回复自身的技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并赋予沉默状态。');
insert into unit_enemy_data values (300500, '蜥蜴怪人', 300500, 0, 4, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】自由操纵双刀，\\n用烟雾弹遮蔽\\n对方双眼的\\n巨蜥战士。\\n\\n·随机对一名角色给予\\n\u3000中等物理伤害。\\n\\n·以随机的敌人为中心，给予小范围\\n\u3000低等物理伤害，\\n\u3000并赋予黑暗状态。');
insert into unit_enemy_data values (300600, '攀缘花', 300600, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】从头上释放剧毒攻击\\n并用爬山虎进行范围物理攻击\\n一口气夺去靠近的冒险者体力的\\n恶魔之花。\\n\\n·对前方两名角色给予\\n\u3000中等物理伤害。\\n\\n·降低前方三名角色的物理防御力，\\n\u3000并赋予中毒状态。');
insert into unit_enemy_data values (300601, '针刺攀缘花', 300601, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】受到异常力量影响而变异的攀缘花。\\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\\n\\n·对前方范围内的敌人造成中等物理伤害，\\n\u3000并施加束缚状态。\\n·降低前方范围内的敌人的物理防御力，\\n\u3000并施加中毒状态。');
insert into unit_enemy_data values (300602, '针刺攀缘花', 300602, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】受到异常力量影响而变异的攀缘花。\\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·针刺攀缘花的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近的两名角色造成\\n\u3000物理伤害（中），并赋予束缚状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅降低最近的三名角色的物理防御力，\\n\u3000并赋予中毒状态。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。');
insert into unit_enemy_data values (300603, '针刺攀缘花', 300603, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】受到异常力量影响而变异的攀缘花。\\n使用带有无数尖刺的藤蔓缠绕敌人，令其动弹不得。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·针刺攀缘花的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近的两名角色造成\\n\u3000物理伤害（中）。\\n\u3000同时使其陷入束缚状态，大幅度降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅降低最近的三名角色的物理防御力，\\n\u3000并赋予中毒状态。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。');
insert into unit_enemy_data values (300604, '沙之攀缘花', 300604, 0, 2, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】栖息在广阔沙海的巨大食人植物\\n的魔物。会使用带有凶恶尖刺的藤蔓\\n捆住冒险者使其动弹不得，进行捕食。\\n\\n·必杀技能会对前方两名角色\\n\u3000造成物理伤害（中），\\n\u3000并使其陷入束缚状态。\\n\\n·会降低前方三名角色的物理防御力，\\n\u3000并赋予中毒状态。');
