insert into unit_enemy_data values (300700, '兽人首领', 300700, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】身为族长鼓舞群落，\\n用足以粉碎大地的连击\\n给敌人施加大量伤害的\\n顶级兽人。\\n\\n·以随机敌人为中心，\\n\u3000对三名角色造成物理伤害（中），\\n\u3000并提升物理防御力。\\n\\n·提升己方全体的物理攻击力。');
insert into unit_enemy_data values (300701, '兽人头目', 300701, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】受到异常力量影响而变异的兽人首领。\\n用巨大的盾牌发出攻防一体的一击。\\n\\n·容易受到物理攻击力下降的\\n效果影响。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·兽人头目的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据兽人头目的物理攻击力，\\n\u3000对前方三名角色造成固定的物理伤害，并使其陷入\\n\u3000眩晕状态。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·小幅降低前方两名角色的技能值，\\n\u3000并特大幅度降低其生命值吸收。同时小幅回复自身的技能值，\\n\u3000并大幅提升行动速度。\\n·普通攻击会根据兽人头目的\\n\u3000物理攻击力，对最近的一名敌人造成\\n\u3000一定比例的物理伤害。');
insert into unit_enemy_data values (300702, '兽人头目', 300702, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】受到异常力量影响而变异的兽人首领。\\n用巨大的盾牌发出攻防一体的一击。\\n\\n·容易受到物理攻击力下降的\\n效果影响。\\n·战斗开始时，中幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，对自身赋予\\n\u3000降低100%技能值的效果。\\n·兽人头目的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低兽人头目的\\n\u3000技能值回复量。\\n·必杀技能会根据兽人头目的物理攻击力，\\n\u3000对前方三名角色造成固定的物理伤害，并使其陷入\\n\u3000眩晕状态。此外，当使用该技能击倒敌人时，\\n\u3000特大幅度回复\\n\u3000自身的技能值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·小幅降低前方两名角色的技能值，\\n\u3000并特大幅度降低其生命值吸收。同时小幅回复自身的技能值，\\n\u3000并小幅提升物理防御力，\\n\u3000大幅提升行动速度。\\n·普通攻击会根据兽人头目的\\n\u3000物理攻击力，对最近的一名敌人造成\\n\u3000一定比例的物理伤害。');
insert into unit_enemy_data values (300703, '兽人头目', 300703, 0, 13, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】受到异常力量影响而变异的兽人首领。\\n用巨大的盾牌发出攻防一体的一击。\\n\\n·必杀技能会对前方三名角色造成\\n\u3000物理伤害（大），\\n\u3000并使其眩晕。\\n\\n·同时，大幅降低前方两名角色的\\n\u3000物理防御力。');
insert into unit_enemy_data values (300800, '长老角鹿', 300800, 0, 1, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】一旦爆发便无法对付，其嘶吼声据说能传到天际，传说中的狂野角鹿。\\n\\n·对敌方全体造成物理伤害（中），并小幅度击飞。\\n\\n·对前方三名角色造成物理伤害（中），并降低敌人的物理攻击力和物理防御力。');
insert into unit_enemy_data values (300801, '灵魂角鹿', 300801, 0, 1, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】受到异常力量影响而变异的长老角鹿。\\n在犄角中注入魔力，释放比打击更强力的一击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·灵魂角鹿的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会无视敌人的挑衅，对物理攻击力最高的\\n\u3000一名角色或魔法攻击力最高的一名角色\\n\u3000造成魔法伤害（大），\\n\u3000并使其陷入麻痹状态。同时特大幅度降低其物理攻击力，\\n\u3000中幅降低魔法攻击力\\n\u3000并每秒小幅降低其技能值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（小），大幅降低\\n\u3000物理攻击力，小幅降低魔法攻击力，\\n\u3000同时小幅提升自身的技能值。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (300802, '灵魂角鹿', 300802, 0, 1, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】受到异常力量影响而变异的长老角鹿。\\n在犄角中注入魔力，释放比打击更强力的一击。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·灵魂角鹿的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低灵魂角鹿的\\n\u3000技能值回复量。\\n·必杀技能会无视敌人的挑衅，对物理攻击力最高的\\n\u3000一名角色或魔法攻击力最高的一名角色\\n\u3000造成魔法伤害（大），\\n\u3000并使其陷入麻痹状态。同时特大幅度降低其物理攻击力，\\n\u3000中幅降低魔法攻击力\\n\u3000并每秒小幅降低其技能值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（小），大幅降低\\n\u3000物理攻击力，小幅降低魔法攻击力，\\n\u3000同时中幅提升自身的技能值。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (300900, '暴君寿龟', 300900, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】进化成如岩山般的巨大老龟。\\n强力无比的头槌可以毫不费力地撞碎大地。\\n\\n·必杀技能会对前方四名角色造成\\n\u3000物理伤害，并使其麻痹。\\n·降低前方三名角色的物理防御力。');
insert into unit_enemy_data values (301000, '海龙', 301000, 0, 14, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】用强大的冰之吐息瞬间将前卫冰封并咬碎其身体的凶猛深海龙。\\n\\n·对前三名敌人造成物理伤害（中）。\\n\\n·对前两名敌人造成物理伤害（中），并赋予冻结状态。');
insert into unit_enemy_data values (301001, '海龙', 301001, 0, 14, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】用强大的冰之吐息瞬间将前卫冰封\\n并咬碎其身体的凶猛深海龙。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海龙的所有伤害\\n\u3000都会造成队伍的技能值回复量\\n\u3000小幅降低。\\n·必杀技能会对前方三名角色造成物理伤害（大），\\n\u3000若角色生命值低于80%，则大幅降低行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方一名角色造成物理伤害（中），\\n\u3000并在短时间内使其冻结。若角色的生命值低于\\n\u300090%，则使其陷入长时间的冻结状态。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。');
insert into unit_enemy_data values (301002, '海龙', 301002, 0, 14, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】用强大的冰之吐息瞬间将前卫冰封\\n并咬碎其身体的凶猛深海龙。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海龙的所有伤害\\n\u3000都会造成队伍的技能值回复量\\n\u3000小幅降低。\\n·必杀技能会对前方三名角色造成物理伤害（大），\\n\u3000若角色生命值低于80%，则特大幅降低行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方两名角色造成物理伤害（中），\\n\u3000并在短时间内使其冻结。若角色的生命值低于\\n\u300090%，则使其陷入长时间的冻结状态。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。');
insert into unit_enemy_data values (301100, '野蛮魔熊', 301100, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】用不符体格的敏捷与咬噬攻击夺走对方的体力，用巨大的利爪撕裂敌人。残忍的森之统治者。\\n\\n·对前方四名角色造成物理伤害（大）。\\n\\n·对前方一名角色造成物理伤害（小），并降低其物理攻击力。');
insert into unit_enemy_data values (301101, '狂乱魔熊', 301101, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】受到异常力量影响而变异的野蛮魔熊。\\n充满愤怒的强壮手臂将敌人连同地面一齐撕裂。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·狂乱魔熊的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成物理伤害（特大），\\n\u3000并使其眩晕。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大），并中幅降低其\\n\u3000物理防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (301102, '狂乱魔熊', 301102, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】受到异常力量影响而变异的野蛮魔熊。\\n充满愤怒的强壮手臂将敌人连同地面一齐撕裂。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·狂乱魔熊的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成物理伤害（特大），\\n\u3000并使其眩晕。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大），并特大幅度降低其\\n\u3000物理防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
