insert into unit_enemy_data values (301104, '狂野魔熊', 301104, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】因塔的魔力而狂暴化的森林支配者。\\n陷入疯狂发出的斩击，可粉碎周围的一切。\\n\\n·战斗开始时，对自身使用伤害上限技能，\\n\u3000当一次行动所受到的伤害超过一定数值时，\\n\u3000伤害量将会减少。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·狂野魔熊的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成物理伤害（特大），\\n\u3000并使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大），并特大幅度降低其\\n\u3000物理防御力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (301200, '巨型魔像', 301200, 0, 13, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】能召唤出无限的魔像，以岩针同时削减敌人的体力与精神。并能以魔力将无数的魔像残骸重构起来的巨大魔像。\\n\\n·对前方四名敌人造成物理伤害（中），并降低其技能值。\\n\\n·召唤魔像。');
insert into unit_enemy_data values (301201, '水晶魔像', 301201, 0, 13, 270, 1000, 1, 1.3, 0, 0, 0, '【物理】以月神魔力的结晶包裹全身的\\n巨大魔像。附着于全身的结晶既如坚实的盾牌，\\n又如可刺穿一切的锐矛。\\n\\n·水晶魔像受到毒、猛毒、诅咒、烧伤\\n\u3000效果时，\\n\u3000行动速度大幅度降低。\\n·必杀技能会解除自身的所有减益效果，\\n\u3000并大幅提升物理防御力和\\n\u3000魔法防御力。同时中幅提升除自身以外的己方的\\n\u3000物理防御力和魔法防御力。\\n·对敌方全体造成物理伤害（中），并使其眩晕。\\n·对前方范围内的敌人造成物理伤害（大），\\n\u3000并小幅提升我方全体的物理攻击力和魔法攻击力。同时中幅回复自身技能值。\\n·当自身的生命值低于40%时，会展开\\n\u3000更为激烈凶暴的攻击。');
insert into unit_enemy_data values (301202, '寒冰魔像', 301202, 0, 13, 270, 1000, 1, 1.3, 0, 0, 0, '【物理】潜伏在冰封大地的洞穴里的\\n巨大魔像。附着于全身的结晶\\n既如坚实的盾牌，又如可穿刺一切的锐矛。\\n\\n·寒冰魔像受到毒、猛毒、\\n\u3000诅咒、烧伤效果时，\\n\u3000行动速度大幅度降低。\\n\\n·同时解除自身所有的减益效果，\\n\u3000并中幅度提升物理防御力和\\n\u3000魔法防御力。\\n\\n·对敌方全体造成\\n\u3000物理伤害（中）。\\n\\n·并对前方范围内的敌人造成\\n\u3000物理伤害（大）。');
insert into unit_enemy_data values (301203, '寒冰魔像', 301202, 0, 13, 270, 1000, 1, 1.3, 0, 0, 0, '【物理】潜伏在冰封大地的洞穴里的\\n巨大魔像。附着于全身的结晶\\n既如坚实的盾牌，又如可穿刺一切的锐矛。\\n\\n·寒冰魔像受到毒、猛毒、\\n\u3000诅咒、烧伤效果时，\\n\u3000行动速度大幅度降低。\\n\\n·同时解除自身所有的减益效果，\\n\u3000并中幅度提升物理防御力和\\n\u3000魔法防御力。\\n\\n·对敌方全体造成\\n\u3000物理伤害（中）。\\n\\n·并对前方范围内的敌人造成\\n\u3000物理伤害（大）。');
insert into unit_enemy_data values (301300, '暴食魔兽', 301300, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '天秤座魔星兽是恶名昭彰的暴食凶兽。\\n衡量夺走的灵魂的善恶，喜欢享受其味道的差异。\\n\\n·对来自敌人的攻击而造成的麻痹、眩晕、束缚、\\n\u3000降低行动速度等效果没有抗性。\\n·对敌方全体造成魔法伤害（大），并\\n\u3000特大幅度提升自身的物理攻击力与魔法攻击力。\\n·对后方范围内的敌人造成魔法伤害（中）。\\n·对前方范围内的敌人造成魔法伤害（中），\\n\u3000并小幅降低其技能值。\u3000同时中幅回复\\n\u3000自身技能值。');
insert into unit_enemy_data values (301301, '暴食魔兽', 301301, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】吞噬一切灵魂的天秤座魔星兽。\\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·暴食魔兽的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并大幅提升其自身的物理攻击力和\\n\u3000魔法攻击力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最远的两名角色造成魔法伤害（中）。\\n·对最近的三名角色造成魔法伤害（小），\\n\u3000并小幅降低其技能值。\\n\u3000同时中幅提升自身的技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n·玩家左侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。\\n·中央目标处于击破状态时，\\n\u3000自身的魔法攻击力特大幅度降低。\\n·右侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。');
insert into unit_enemy_data values (301302, '暴食魔兽', 301302, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】吞噬一切灵魂的天秤座魔星兽。\\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·暴食魔兽的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并大幅提升其自身的物理攻击力和\\n\u3000魔法攻击力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最远的两名角色造成魔法伤害（中）。\\n·对最近的三名角色造成魔法伤害（小），\\n\u3000并中幅降低其技能值。\\n\u3000同时中幅提升自身的技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n使最近的三名角色陷入束缚状态，并对其造成\\n\u3000魔法伤害（大）。\\n\u3000若自身的目标处于击破状态，\\n\u3000则特大幅度提升自身的魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·玩家左侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。\\n·中央目标处于击破状态时，\\n\u3000自身的魔法攻击力特大幅度降低。\\n·右侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。');
