insert into unit_enemy_data values (301305, '暴食魔兽', 301305, 0, 4, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】吞噬一切灵魂的天秤座魔星兽。\\n其天秤与严正公平相距甚远，更加倾向于暴食兽的食欲。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·暴食魔兽的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并大幅降低其物理攻击力和\\n\u3000魔法攻击力。\\n\u3000同时，大幅提升自身的物理攻击力和\\n\u3000魔法攻击力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最远的两名角色造成魔法伤害（中）。\\n·对最近的三名角色造成魔法伤害（小），\\n\u3000并中幅降低其技能值。\\n\u3000同时中幅提升自身的技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n使最近的三名角色陷入束缚状态，并对其造成\\n\u3000魔法伤害（大）。\\n\u3000若自身的目标处于击破状态，\\n\u3000则特大幅度提升自身的魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体每秒造成魔法伤害（小）。\\n\u3000若自身的目标没有处于击破状态，\\n\u3000则每秒小幅降低敌方全体的技能值。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·玩家左侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。\\n·中央目标处于击破状态时，\\n\u3000自身的魔法攻击力特大幅度降低。\\n·右侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。');
insert into unit_enemy_data values (301400, '美杜莎', 301400, 0, 3, 270, 1000, 1, 2.5, 0, 0, 0, '头发由无数毒蛇构成的处女座魔星兽。\\n悲恋的痛苦使其拒绝一切，并将眼前的敌人都变成石头。\\n\\n·对敌方全体造成魔法伤害（大），并对\\n\u3000前方范围内的敌人赋予石化状态。\\n·对前方范围内的敌人造成物理伤害，\\n\u3000大幅降低石化状态敌人的技能值。\\n·对物理攻击力最高的一名敌人造成魔法伤害（大），\\n\u3000并赋予中毒状态。');
insert into unit_enemy_data values (301401, '美杜莎', 301401, 0, 3, 270, 1000, 1, 2.5, 0, 0, 0, '【魔法】因无止境的爱憎而疯狂的处女座魔星兽。\\n以惊人的嫉妒与抗拒心，让所见的一切全部石化。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·美杜莎的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并使其在短时间内陷入石化状态。当敌人处于猛毒状态时，\\n\u3000使其陷入长时间石化状态。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成物理伤害（中），\\n\u3000并中幅降低其生命值回复量，大幅降低生命值吸收。当敌人处于猛毒状态时，\\n\u3000中幅降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以后方第二名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小），并赋予中毒状态。\\n\u3000当角色的生命值低于60％时，\\n\u3000赋予猛毒状态。该技能所造成的中毒与猛毒伤害，\\n\u3000无法回复技能值。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (301402, '美杜莎', 301402, 0, 3, 270, 1000, 1, 2.5, 0, 0, 0, '【魔法】因无止境的爱憎而疯狂的处女座魔星兽。\\n以惊人的嫉妒与抗拒心，让所见的一切全部石化。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·美杜莎的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并使其在短时间内陷入石化状态。当敌人处于猛毒状态时，\\n\u3000使其陷入长时间石化状态。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成物理伤害（中），\\n\u3000并大幅降低其生命值回复量，特大幅度降低生命值吸收。当敌人处于猛毒状态时，\\n\u3000中幅降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以后方第二名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小），并赋予中毒状态。\\n\u3000当角色的生命值低于60％时，\\n\u3000赋予猛毒状态。该技能所造成的中毒与猛毒伤害，\\n\u3000无法回复技能值。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·以后方第二名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小）。当敌人处于猛毒状态时，\\n\u3000赋予石化状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (301403, '美杜莎', 301403, 0, 3, 270, 1000, 1, 2.5, 0, 0, 0, '【魔法】因无止境的爱憎而疯狂的处女座魔星兽。\\n以惊人的嫉妒与抗拒心，让所见的一切全部石化。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000中幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·美杜莎的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低美杜莎的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并使其在短时间内陷入石化状态。当敌人处于猛毒状态时，\\n\u3000使其陷入长时间石化状态。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成物理伤害（中），\\n\u3000并大幅降低其生命值回复量，特大幅度降低生命值吸收。当敌人处于猛毒状态时，\\n\u3000中幅降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以后方第二名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小），并赋予中毒状态。\\n\u3000当角色的生命值低于60％时，\\n\u3000赋予猛毒状态。该技能所造成的中毒与猛毒伤害，\\n\u3000无法回复技能值。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·以后方第二名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小）。当敌人处于猛毒状态时，\\n\u3000赋予石化状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·召唤两名仆人，并大幅度降低自身的\\n\u3000物理防御力和魔法防御力。\\n·仆人对最近的一名角色造成物理伤害（中），\\n\u3000并赋予中毒状态。若角色的生命值低于\\n\u300070%，则赋予猛毒状态。\\n\u3000该技能造成的伤害，\\n\u3000会减少技能值回复量。该技能所造成的中毒与猛毒伤害，\\n\u3000无法回复技能值。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n若仆人的生命值降至一定值以下，则对美杜莎\\n\u3000造成物理固定伤害。');
