insert into unit_enemy_data values (301900, '虎鲸猎手', 301900, 0, 11, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】用敏锐的眼神搜寻\\n猎物与海浪的海之猎手。\\n用魔力产生无限的潮水\\n削弱敌人的武器与气力。 \\n\\n·对前方四名角色造成少量物理伤害\\n\u3000并降低物理攻击力。\\n\\n·对前方两名角色造成少量魔法伤害\\n\u3000并削减其技能值。');
insert into unit_enemy_data values (301901, '鲸王猎手', 301901, 0, 11, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】因月神魔力而变得凶恶的大海猎手。\\n其千锤百炼的身体能将水流化为刀刃进行攻击。\\n\\n·对前方四名敌人造成物理伤害（大）\\n\u3000并中幅降低其物理攻击力。同时，\\n\u3000小幅提升我方全体的技能值。\\n·对前方小范围的敌方造成魔法伤害（中），\\n\u3000并大幅降低其技能值。\\n·以后方第二名敌人为中心，造成\\n\u3000小范围的物理伤害（中），\\n\u3000并大幅降低其魔法攻击力。');
insert into unit_enemy_data values (302000, '野性狮鹫', 302000, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】在严酷的环境中进化而来的小型狮鹫。\\n能以龙卷风卷起猎物，在空中进行狩猎。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·野性狮鹫的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对前方三名角色造成\\n\u3000魔法伤害（中）。');
insert into unit_enemy_data values (302001, '野性狮鹫', 302001, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '在残酷环境中成长进化的狮鹫。\\n使用龙卷风将猎物卷起到空中进行狩猎。\\n\\n·对前方范围内的敌人造成魔法伤害（中）。');
insert into unit_enemy_data values (302002, '野性狮鹫', 302002, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】在严酷的环境中进化而来的小型狮鹫。\\n能以龙卷风卷起猎物，在空中进行狩猎。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·野性狮鹫的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低野性狮鹫的\\n\u3000技能值回复量。\\n·必杀技能会对前方三名角色造成\\n\u3000魔法伤害（中），并将其击退。\\n\u3000当角色的生命值小于70％时，\\n\u3000赋予眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000并中幅降低其魔法防御力。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (302003, '暴风狮鹫', 302003, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】生活在严苛环境中的\\n凶猛狮鹫。其壮硕翅膀扇出的强风，\\n可以轻易将冒险者吹飞。\\n\\n·必杀技能会对前方三名角色造成\\n\u3000魔法伤害（中），\\n\u3000并将其击退。若角色的生命值低于\\n\u300070%，则赋予\\n\u3000眩晕状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\\n·对前方三名角色造成魔法伤害（中），\\n\u3000并小幅降低\\n\u3000其魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (302100, '双足飞龙', 302100, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】利用巨大的皮翼在空中飞行的翼龙。\\n吐出巨大的火球会阻挡入侵者的去路。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会以最近的敌人为中心，\\n\u3000对小范围内最多三名角色\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (302101, '双足飞龙', 302101, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】利用巨大的皮翼在空中飞行的翼龙。\\n吐出巨大的火球会阻挡入侵者的去路。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会以最近的敌人为中心，\\n\u3000对小范围内最多三名角色\\n\u3000造成物理伤害（中），\\n\u3000并特大幅度降低其物理防御力，\\n\u3000这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·击退前方三名角色。');
insert into unit_enemy_data values (302102, '双足飞龙', 302102, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】利用巨大的皮翼在空中飞行的翼龙。\\n吐出巨大的火球会阻挡入侵者的去路。\\n·以前方第一名敌人为中心，对其小范围内的三名敌人\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (302103, '沙岩双足飞龙', 302103, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】盘踞在沙尘飞舞的高空的\\n翼龙。它支配着荒野的高空，在严酷的环境中\\n也不会放过任何猎物。\\n\\n·以前方第一名敌人为中心，对其\\n\u3000小范围内的最多三名角色造成\\n\u3000魔法伤害（中）。');
insert into unit_enemy_data values (302104, '沙岩双足飞龙', 302103, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】盘踞在沙尘飞舞的高空的\\n翼龙。它支配着荒野的高空，在严酷的环境中\\n也不会放过任何猎物。\\n\\n·以前方第一名敌人为中心，对其\\n\u3000小范围内的最多三名角色造成\\n\u3000魔法伤害（中）。');
insert into unit_enemy_data values (302105, '双足飞龙', 302105, 0, 11, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】利用巨大的皮翼在空中飞行的翼龙。\\n吐出巨大的火球会阻挡入侵者的去路。\\n\\n·战斗开始时，中幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量，\\n\u3000小幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%。\\n·双足飞龙的所有伤害都会中幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害都会大幅降低双足飞龙的\\n\u3000技能值回复量。\\n·必杀技能会以最近的敌人为中心，\\n\u3000对大范围内最多三名角色\\n\u3000造成物理伤害（大），\\n\u3000并中幅度降低其物理防御力，同时小幅提升\\n\u3000自身的物理防御力。\\n\u3000这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·击退前方三名角色，\\n\u3000并中幅度降低其行动速度。');
insert into unit_enemy_data values (302200, '依科托·格拉迪恩', 302200, 0, 16, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】因月神魔力而醒来的古代机械兵器。\\n其冷酷无情，会释放魔力阻挡入侵者。\\n\\n·对敌方全体造成魔法伤害（特大），\\n\u3000并大幅度降低其魔法防御力。\\n·对前方三名角色造成物理伤害（大）。\\n·对前方两名角色造成魔法伤害（大），\\n\u3000并使赋予麻痹状态。');
insert into unit_enemy_data values (302300, '聪慧猫头鹰', 302300, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】栖身于大树之顶的聪明巨鸟。\\n凝聚了森林万物的智慧，能巧妙地操纵魔法。\\n\\n·连结爆发时，对敌方全体造成\\n\u3000魔法伤害。\\n·随机对一名角色造成物理伤害，\\n\u3000并使其陷入麻痹状态。');
insert into unit_enemy_data values (302400, '方舟守护者', 302400, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】自古以来一直保护着遗迹的人工生命体。\\n通过造物主的秘术，拥有可一直运转的无限动力。\\n\\n·连结爆发时，对敌方全体造成\\n\u3000魔法伤害（大）。\\n·对前方范围内的敌人造成物理伤害。\\n·对敌方全体造成魔法伤害，\\n\u3000并使其沉默。');
insert into unit_enemy_data values (302500, '奇美拉', 302500, 0, 16, 270, 1000, 1, 1.4, 0, 0, 0, '【物理】镇坐孤塔之顶的暴虐混种魔兽。\\n以千变万化的猛攻，屠戮过众多挑战者。\\n\\n·连结爆发时，对前方范围内的敌人造成\\n\u3000物理伤害（大），并将其击退。\\n\\n·对前方三名角色造成物理伤害，\\n\u3000并使其陷入烧伤状态。\\n·对敌方全体造成魔法伤害，\\n\u3000并降低其行动速度、物理防御力和魔法防御力。\\n·剩余时间少于10秒时，对敌方全体\\n\u3000造成魔法伤害（极大），并特大幅度降低自身的技能值。');
insert into unit_enemy_data values (302600, '双子魔猪', 302600, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】获得强大力量的双子座魔星兽。\\n一边在前方防御，一边以登峰造极的招数进行攻击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·双子魔猪的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，并大幅降低\\n\u3000其物理攻击力和魔法攻击力。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对后方范围内最多三名角色造成物理伤害（中），\\n\u3000并使其陷入眩晕状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·对后方范围内最多三名角色造成物理伤害（中），\\n\u3000并小幅降低其技能值。随后\\n\u3000位于前方的双子魔猪会向前方移动。这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。');
insert into unit_enemy_data values (302601, '双子魔猪', 302601, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】获得强大力量的双子座魔星兽。\\n一边在前方防御，一边以登峰造极的招数进行攻击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·双子魔猪的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，并大幅降低\\n\u3000其物理攻击力和魔法攻击力。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对后方范围内最多三名角色造成物理伤害（中），\\n\u3000并使其陷入眩晕状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·对后方范围内最多三名角色造成物理伤害（中），\\n\u3000并小幅降低其技能值。随后\\n\u3000位于前方的双子魔猪会向前方移动。这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·对敌方全体造成物理伤害（小），并小幅回复\\n\u3000自身的技能值，大幅提升行动速度。');
insert into unit_enemy_data values (302602, '双子魔猪', 302602, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】获得强大力量的双子座魔星兽。\\n一边在前方防御，一边以登峰造极的招数进行攻击。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·双子魔猪的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低双子魔猪的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，并大幅降低\\n\u3000其物理攻击力和魔法攻击力。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对后方范围内最多三名角色造成物理伤害（中），\\n\u3000并使其陷入眩晕状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·对后方范围内最多三名角色造成物理伤害（中），\\n\u3000并中幅降低其技能值。随后\\n\u3000位于前方的双子魔猪会向前方移动。这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·对敌方全体造成物理伤害（小），并中幅回复\\n\u3000自身的技能值，大幅提升行动速度。\\n·展开使敌人行动速度中幅降低的领域。');
insert into unit_enemy_data values (302700, '巨钳魔蟹', 302700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】在船墓的入海口筑巢的巨蟹座魔星兽。\\n其巨大钳子造成的一击，击沉过无数船只。\\n\\n·对敌方全体造成魔法伤害（大），\\n\u3000并降低其物理防御力。\\n·对前方范围内的所有敌人造成物理伤害（中）\\n\u3000并赋予眩晕状态。\\n·对敌方全体造成物理伤害（中）。');
insert into unit_enemy_data values (302701, '巨钳魔蟹', 302701, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】击沉过无数船只的巨蟹座魔星兽。\\n大海的霸者可斩断、压垮、歼灭万物。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成魔法伤害（大）。\\n\u3000随后特大幅度提升自身的魔法攻击力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·以前方第一名角色为中心，对小范围内的敌人\\n\u3000造成物理伤害（大），\\n\u3000并使其眩晕。\\n\u3000该技能所造成的伤害\\n\u3000会降低敌方的技能值回复量。\\n·对敌方全体造成物理伤害（小），\\n\u3000并大幅降低物理攻击力和魔法攻击力。\\n\u3000该技能所造成的伤害\\n\u3000会降低敌方的技能值回复量。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
