insert into unit_enemy_data values (302800, '炎吼狮王', 302800, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '以草原霸主闻名四方的狮子座魔星兽。\\n灼热的咆哮能够燃尽一切向其举起刀刃的敌人。\\n\\n·无视防御力，对敌方全体造成与其最大生命值相应的\\n\u3000物理伤害（大），并对剩余生命值较低的敌人\\n\u3000赋予烧伤状态。\\n·对一名敌人造成物理伤害（大），\\n\u3000当目标处于烧伤状态时，追加伤害。\\n·对前方范围内的敌人造成物理伤害（中），\u3000当目标处于烧伤状态时，追加伤害。');
insert into unit_enemy_data values (302801, '炎吼狮王', 302801, 0, 16, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】君临于荒凉原野上的狮子座魔星兽。\\n其利爪可撕裂空气，喷出业火的咆哮可烧灼大地。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·炎吼狮王的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成物理伤害（中），\\n\u3000并在短时间内赋予其烧伤状态。\\n\u3000当角色的生命值低于70％时，\\n\u3000赋予长时间烧伤状态。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n\u3000该技能赋予的烧伤状态\\n\u3000不会回复技能值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方一名敌人造成物理伤害（特大），\\n\u3000若敌方处于烧伤状态下，\\n\u3000会追加造成固定的物理伤害。\\n\u3000该技能所造成的追加伤害\\n\u3000不会使角色的技能值回复。\\n·对前方范围内的敌人造成物理伤害（大），\\n\u3000若敌方处于烧伤状态下，\\n\u3000会追加造成固定的物理伤害。\\n\u3000该技能所造成的追加伤害\\n\u3000不会使角色的技能值回复。');
