insert into unit_enemy_data values (302803, '炎吼狮王', 302803, 0, 16, 270, 1000, 1, 3.0, 0, 0, 0, '【物理】君临于荒凉原野上的狮子座魔星兽。\\n其利爪可撕裂空气，喷出业火的咆哮可烧灼大地。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000中幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·炎吼狮王的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低炎吼狮王的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成物理伤害（中），\\n\u3000并在短时间内赋予其烧伤状态。\\n\u3000当角色的生命值低于70％时，\\n\u3000赋予长时间烧伤状态。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n\u3000该技能赋予的烧伤状态\\n\u3000不会回复技能值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方一名敌人造成物理伤害（特大），\\n\u3000若敌方处于烧伤状态下，\\n\u3000会追加造成固定的物理伤害。\\n\u3000该技能所造成的追加伤害\\n\u3000不会使角色的技能值回复。\\n·对前方范围内的敌人造成物理伤害（大），\\n\u3000若敌方处于烧伤状态下，\\n\u3000会追加造成固定的物理伤害。\\n\u3000该技能所造成的追加伤害\\n\u3000不会使角色的技能值回复。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的魔法伤害，并使其短时间内陷入眩晕状态。\\n\u3000当角色的生命值低于50％时，\\n\u3000使其陷入长时间的眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·无视敌人的挑衅，以前方第二名角色\\n\u3000为中心，对一定范围内的敌人持续造成\\n\u3000魔法伤害，并展开小幅降低其行动速度的领域。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (302900, '阿克艾利欧斯', 302900, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '被寄宿在神瓶中的魔力所吸收的水瓶座魔星兽。\\n沉溺于强大的力量，为了支配大海而肆虐。\\n\\n·必杀技能对敌方全体造成共计6次的\\n\u3000魔法伤害（小）。\\n\u3000当角色的生命值低于80%时造成击退效果，\\n\u3000当低于40%时进一步击退并\\n\u3000赋予眩晕状态。\\n·对眼前的三名敌方角色造成物理伤害（中）。\\n·解除自身的属性值降低效果和敌方全体的\\n\u3000属性值提升效果，并提升自身的技能值。');
