insert into unit_enemy_data values (303200, '冰雪夫人', 303200, 0, 6, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】与暴风雪一同出现，\\n召唤大军袭击扰乱地盘的人。\\n雪国第一华美夫人。\\n\\n·对前方四名角色造成魔法伤害（中）。\\n\\n·对随机两名角色造成魔法伤害（小）\\n\u3000并赋予冻结状态。');
insert into unit_enemy_data values (303300, '滴水嘴兽', 303300, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】留在腐朽的遗迹保护无主之地的神秘鸟型石像。如今街道和命令都消失成沙，不管是谁它都会喷出火焰袭击对方。\\n\\n·对前方5名角色造成魔法伤害（中），并赋予烧伤状态。同时降低敌人的魔法防御力。\\n\\n·对随机三名角色造成魔法伤害（小），并赋予黑暗状态。');
insert into unit_enemy_data values (303301, '暗黑滴水嘴兽', 303301, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】侍奉着暴食君主的近卫黑铁像。\\n徘徊于冥府，巧妙地操纵灵魂并将其奉献给主人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·暗黑滴水嘴兽的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（中），\\n\u3000并使其陷入烧伤状态，同时中幅降低\\n\u3000其魔法防御力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名角色造成魔法伤害（特大）。\\n\u3000并对敌方全体造成魔法伤害（中），\\n\u3000中幅降低其物理攻击力和魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (303302, '暗黑滴水嘴兽', 303302, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】侍奉着暴食君主的近卫黑铁像。\\n徘徊于冥府，巧妙地操纵灵魂并将其奉献给主人。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·暗黑滴水嘴兽的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（中），\\n\u3000并使其陷入烧伤状态，同时中幅降低\\n\u3000其魔法防御力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名角色造成魔法伤害（特大）。\\n\u3000并对敌方全体造成魔法伤害（中），\\n\u3000中幅降低其物理攻击力和魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (303303, '沙岩滴水嘴兽', 303303, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】散发着塔的魔力，寄宿着生命的沙岩像魔物。\\n在飞扬的尘土中守株待兔，口吐灼焰袭击敌人。\\n\\n·对前方范围内的敌人造成魔法伤害（大），\\n\u3000并使其陷入烧伤状态。同时中幅度降低其魔法防御力。\\n\\n·对前方三名角色造成魔法伤害（中），\\n\u3000并使敌方全体陷入黑暗状态。\\n·对眼前一名敌人造成物理伤害（大）。\\n\u3000·同时对敌方全体中处于烧伤状态的敌人\\n\u3000造成魔法伤害（大）。');
insert into unit_enemy_data values (303304, '石像鬼', 303304, 0, 13, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】守护沙漠大地的漆黑\\n恶魔像。喷出吐息将整片广阔\\n沙漠燃烧殆尽，让跋涉于这片灼热之地\\n的冒险者寸步难行。\\n\\n·必杀技能会对敌方全体造成\\n\u3000魔法伤害（中），并使其处于烧伤状态，\\n\u3000同时小幅降低其魔法防御力。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\\n·对前方一名敌人造成魔法伤害（中）。\\n\u3000并对敌方全体造成魔法伤害（中），\\n\u3000中幅降低其物理攻击力和\\n\u3000魔法攻击力。');
insert into unit_enemy_data values (303400, '沙之槌', 303400, 0, 13, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】君临生存竞争激烈的沙漠\\n顶端的巨大蜥蜴。\\n利用毒、活用沙子的战法是在\\n残酷的环境中习得的生存术。\\n\\n·对敌方全体造成中等物理\\n\u3000伤害，并降低其行动速度。\\n\u3000同时提升己方全体的物理\\n\u3000攻击力。\\n\\n·以前方一名角色为中心，对小范围\\n\u3000内的敌人造成少量物理伤害，\\n\u3000并赋予中毒状态。\\n\u3000同时，降低敌人的物理防御力。');
insert into unit_enemy_data values (303401, '巨型蜥蜴', 303401, 0, 13, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】带着露娜魔力而变异的巨蜥。\\n硬刺状的鳞片可以削弱敌人的体力和防御。\\n\\n·对敌方全体造成物理伤害（大），\\n\u3000同时削减技能值。\\n·对前方两名角色造成物理伤害（中），\\n\u3000并赋予中毒状态。\\n·对前方三名角色造成物理伤害（中）。\\n\u3000同时对前方一名角色造成物理伤害（大），\\n\u3000并中幅降低其物理防御力\\n\u3000和魔法防御力。');
insert into unit_enemy_data values (303402, '巨型蜥蜴', 303402, 0, 13, 270, 1000, 1, 1.9, 0, 0, 0, '【物理】带着露娜魔力而变异的巨蜥。\\n硬刺状的鳞片可以削弱敌人的体力和防御。\\n\\n·对敌方全体造成物理伤害（大），\\n\u3000同时削减技能值。\\n·对前方两名角色造成物理伤害（中），\\n\u3000并赋予中毒状态。\\n·对前方三名角色造成物理伤害（中）。\\n\u3000同时对前方一名角色造成物理伤害（大），\\n\u3000并中幅降低其物理防御力\\n\u3000和魔法防御力。');
insert into unit_enemy_data values (303500, '怒蛇', 303500, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】凭借持续吸收其他生物\\n的魔力来掌握妖术的魔物。\\n从敌人身上的伤口处夺取魔力，\\n进而获得更多的力量。\\n\\n·对敌方全体造成魔法伤害（中）\\n\u3000削减所有使用魔法攻击的\\n\u3000敌人的技能值。\\n\\n·以前方一名角色为中心，\\n\u3000对小范围敌人造成魔法伤害（小），\\n\u3000并降低魔法防御力。');
insert into unit_enemy_data values (303501, '怒蛇', 303501, 0, 13, 270, 1000, 2, 1.8, 0, 0, 0, '【物理】凭借持续吸收其他生物的魔力来掌握妖术的魔物。\\n从对敌人造成的伤口处夺取魔力，进而获得更多的力量。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·怒蛇的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000使小范围内的敌人陷入中毒状态。\\n\u3000同时，中幅降低敌方全体的物理攻击力，\\n\u3000大幅降低魔法攻击力。该技能所造成的中毒伤害，\\n\u3000无法回复技能值。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大）。');
insert into unit_enemy_data values (303502, '怒蛇', 303502, 0, 13, 270, 1000, 2, 1.8, 0, 0, 0, '【物理】凭借持续吸收其他生物的魔力来掌握妖术的魔物。\\n从对敌人造成的伤口处夺取魔力，进而获得更多的力量。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少100%\\n·怒蛇的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000使小范围内的敌人陷入中毒状态。\\n\u3000同时，中幅降低敌方全体的物理攻击力，大幅降低魔法攻击力，\\n\u3000并特大幅度降低生命值吸收。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大）。');
insert into unit_enemy_data values (303503, '怒蛇', 303503, 0, 13, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】凭借持续吸收其他生物的魔力来掌握妖术的魔物。\\n从对敌人造成的伤口处夺取魔力，进而获得更多的力量。\\n\\n·怒蛇的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000使小范围内的敌人陷入中毒状态。\\n\u3000同时，中幅降低敌方全体的物理攻击力，\\n\u3000大幅降低魔法攻击力。该技能所造成的中毒伤害，\\n\u3000无法回复技能值。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大）。');
insert into unit_enemy_data values (303600, '熔岩龙', 303600, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】凶猛的恐龙，使用强韧的脚\\n和锐利的爪子来瞬间捕获猎物。\\n在毁灭的大地上以岩浆为食，\\n孤高地生存着。\\n\\n·对敌方全体造成中等物理伤害\\n\u3000提升自身的行动速度。\\n\\n·对前方三名角色造成中等\\n\u3000物理伤害，并使其行动速度下降。');
insert into unit_enemy_data values (303601, '熔岩龙', 303601, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】使用强韧的脚和锐利爪子的凶猛恐龙。在毁灭的大地上以岩浆为食，孤高地生存着。\\n\\n·战斗开始时，对自身使用伤害上限技能，\\n\u3000当一次行动所受到的伤害超过一定数值时，\\n\u3000伤害量将会减少。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成物理伤害（中），\\n\u3000大幅提升自身的行动速度。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对前方三名角色造成物理伤害（中），\\n\u3000并中幅降低其行动速度。\\n·对前方一名角色造成物理伤害（特大）。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。');
insert into unit_enemy_data values (303602, '熔岩龙', 303602, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】使用强韧的脚和锐利爪子的凶猛恐龙。在毁灭的大地上以岩浆为食，孤高地生存着。\\n\\n·战斗开始时，对自身使用伤害上限技能，\\n\u3000当一次行动所受到的伤害超过一定数值时，\\n\u3000伤害量将会减少。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，大幅提升自身的物理攻击力。\\n·必杀技能会对敌方全体造成物理伤害（中），\\n\u3000大幅提升自身的行动速度。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对前方三名角色造成物理伤害（中），\\n\u3000并中幅降低其行动速度。\\n·对前方一名角色造成物理伤害（特大）。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。');
