insert into unit_enemy_data values (303606, '熔岩龙', 303606, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】使用强韧的脚和锐利爪子的凶猛恐龙。在毁灭的大地上以岩浆为食，孤高地生存着。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·熔岩龙的所有伤害\\n\u3000都会大幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对范围内的敌人造成物理伤害（特大），\\n\u3000并中幅降低敌方全体的行动速度，\\n\u3000同时特大幅度提升自身的行动速度。该技能的伤害效果\\n\u3000会根据范围内敌人的数量而降低。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，对范围内的敌人\\n\u3000造成物理伤害（中），并特大幅度降低其\\n\u3000物理防御力。防御力的降低量\\n\u3000会根据范围内敌人的数量而减少。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (303607, '熔岩龙', 303607, 0, 13, 270, 1000, 1, 1.7, 0, 0, 0, '【物理】使用强韧的脚和锐利爪子的凶猛恐龙。在毁灭的大地上以岩浆为食，孤高地生存着。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，对自身赋予\\n\u3000降低100%技能值的效果。\\n·熔岩龙的所有伤害\\n\u3000都会大幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低熔岩龙的技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对范围内的敌人造成物理伤害（特大），\\n\u3000并大幅降低敌方全体的行动速度，\\n\u3000同时特大幅度提升自身的行动速度。该技能的伤害效果\\n\u3000会根据范围内敌人的数量而降低。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，对范围内的敌人\\n\u3000造成物理伤害（中），并特大幅度降低其\\n\u3000物理防御力。防御力的降低量\\n\u3000会根据范围内敌人的数量而减少。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (303700, '地狱犬', 303700, 0, 14, 270, 1000, 1, 0.7, 0, 0, 0, '【物理】坠入火山后，全身缠绕着\\n地狱的火焰而复苏的双头魔犬。\\n与爱犬波尼一同在领地驰骋，\\n用灼热的火焰来招待入侵者。\\n\\n·对敌方全体给予中等物理伤害，\\n\u3000并赋予烧伤状态。同时回复\\n\u3000召唤的怪物的生命值。\\n\\n·召唤波尼。');
insert into unit_enemy_data values (303701, '熔岩狼', 303701, 0, 14, 270, 1000, 1, 0.7, 0, 0, 0, '【物理】通过塔的魔力让魔界之炎显现的魔狼。\\n在灼热之地与搭档波尼一起等待敌人的到来。\\n\\n·对敌方全体造成物理伤害（大），使其处于烧伤状态。\\n\u3000并回复波尼的生命值。\\n·召唤波尼。\\n·对敌方全体造成物理伤害（中），\\n\u3000并降低其行动速度。同时大幅提升我方全体的\\n\u3000物理攻击力。');
insert into unit_enemy_data values (303800, '阿尔玛', 303800, 0, 16, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】从远古开始守护迷宫的防卫兵器。静静等待跨越所有陷阱，侵入最深处的侵入者。\\n·根据角色的最大生命值，对敌方全体给予\\n\u3000中等魔法伤害。\\n·对前方范围内的敌人给予中等物理伤害。');
insert into unit_enemy_data values (303801, '阿尔玛', 303800, 0, 16, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】从远古开始守护迷宫的防卫兵器。静静等待跨越所有陷阱，侵入最深处的侵入者。\\n·根据角色的最大生命值，对敌方全体给予\\n\u3000特大魔法伤害。\\n·对前方范围内的敌人给予中等物理伤害。\\n·对残余生命值最高的敌人，根据其最大生命值，\\n\u3000给予中等魔法伤害并使其眩晕。\\n·当阿尔玛自身的生命值低于50％时，\\n\u3000它的攻击会变得更为凶暴。');
insert into unit_enemy_data values (303900, '独眼巨人', 303900, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】以自己的怪力为傲的独眼巨人。\\n往眼瞳里注入魔力，从而释放出强大的奇怪光线。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·独眼巨人的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并大幅降低\\n\u3000其物理攻击力，中幅降低其魔法攻击力，\\n\u3000小幅降低其技能值\\n\u3000同时解除属性提升效果。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·无视挑衅，以攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人为中心，对小范围内的敌人造成物理伤害（大），\\n\u3000并赋予其眩晕状态，\\n\u3000同时小幅降低其行动速度。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (303901, '独眼巨人', 303901, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】以自己的怪力为傲的独眼巨人。\\n充满魔力的眼瞳能释放出强大的奇怪光线。\\n\\n·对敌方全体造成魔法伤害（大）。\\n\u3000提升对生命值为100%的敌人造成的伤害\\n\u3000并降低其行动速度。\\n·对物理攻击力最高的一名敌人，根据其最大\\n\u3000生命值，造成物理伤害（中），并降低其技能值。');
insert into unit_enemy_data values (303902, '独眼巨人', 303902, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】以自己的怪力为傲的独眼巨人。\\n往眼瞳里注入魔力，从而释放出强大的奇怪光线。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少25%\\n·独眼巨人的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并大幅降低\\n\u3000其物理攻击力，中幅降低其魔法攻击力，\\n\u3000大幅降低其技能值\\n\u3000同时解除属性提升效果。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·无视挑衅，以攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人为中心，对小范围内的敌人造成物理伤害（大），\\n\u3000并赋予其眩晕状态，\\n\u3000同时中幅降低其行动速度。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (303903, '独眼巨人', 303903, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】如大山一般矗立于沙尘之中\\n的独眼巨人。独眼可射出超强力光线\\n收割冒险者的生命。\\n\\n·必杀技能会对敌方全体造成魔法伤\\n\u3000\\n害（大），并中幅降低其行动速度。\\n若角色剩余的生命值为100%，\\n则提升对该角色造成的伤害。\\n\\n·根据最大生命值，对物理攻击力最\\n高的一名角色造成一定比例的物理伤\\n害，并大幅降低其技能值。');
insert into unit_enemy_data values (304000, '暗黑独角兽', 304000, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】诞生于梦幻，徘徊于黑暗中的魔兽。\\n以恶梦的牢笼束缚猎物，津津有味地享用其活力。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·必杀技能会对物理攻击力最高的一名角色\\n\u3000造成魔法伤害（特大），\\n\u3000并特大幅度降低其技能值。\\n·无视敌人的挑衅，以距离最远的\\n\u3000一名敌人为中心，对范围内\\n\u3000所有敌人赋予诅咒状态。\\n\u3000该技能所造成的诅咒伤害\\n\u3000不会回复技能值。');
insert into unit_enemy_data values (304001, '暗黑独角兽', 304001, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】诞生于梦幻，徘徊于黑暗中的魔兽。\\n以恶梦的牢笼束缚猎物，津津有味地享用其活力。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少100%\\n·必杀技能会解除物理攻击力最高的一名角色的\\n\u3000属性提升效果，\\n\u3000随后对其造成魔法伤害（特大），\\n\u3000并特大幅度降低其技能值。\\n·无视敌人的挑衅，以距离最远的\\n\u3000一名敌人为中心，小幅降低一定范围内\\n\u3000所有敌人的技能值，并赋予诅咒状态。\\n\u3000该技能所造成的诅咒伤害\\n\u3000不会回复技能值。');
insert into unit_enemy_data values (304100, '泰坦陆龟', 304100, 0, 13, 270, 1000, 1, 2.5, 0, 0, 0, '又老又大的陆龟，龟壳上甚至有大树扎根。\\n持续忍耐敌人的攻击，施加愤怒的反击。\\n\\n·对全体敌人造成大量物理伤害。\\n·根据自己缩在龟壳期间受到的伤害量\\n\u3000来给予敌人相应的物理伤害。');
insert into unit_enemy_data values (304101, '泰坦陆龟', 304101, 0, 13, 270, 1000, 1, 0.1, 0, 0, 0, '【物理】又老又大的陆龟，龟壳上甚至有大树扎根。\\n持续忍耐敌人的攻击，施加愤怒的反击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·泰坦陆龟的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（大）。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·躲在龟壳内的期间，特大幅度提升\\n\u3000自身的物理防御力和魔法防御力。\\n\u3000并根据躲在龟壳期间所受到的伤害量，\\n\u3000对包括处于飞行状态中的角色在内的所有敌人造成物理伤害。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·当所有目标陷入击破状态时，\\n\u3000一定时间内无法行动。在无法行动期间，\\n\u3000大幅降低自身的物理防御力和魔法防御力，\\n\u3000无法被击破。');
insert into unit_enemy_data values (304102, '泰坦陆龟', 304102, 0, 13, 270, 1000, 1, 0.1, 0, 0, 0, '【物理】又老又大的陆龟，龟壳上甚至有大树扎根。\\n持续忍耐敌人的攻击，施加愤怒的反击。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·泰坦陆龟的所有伤害\\n\u3000都不会使队伍的技能值回复。\\n·敌人的所有伤害\\n\u3000都会大幅降低泰坦陆龟的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成物理伤害（大），\\n\u3000并大幅降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·躲在龟壳内的期间，特大幅度提升\\n\u3000自身的物理防御力和魔法防御力。\\n\u3000并根据躲在龟壳期间所受到的伤害量，\\n\u3000对包括处于飞行状态中的角色在内的所有敌人造成物理伤害。\\n·当所有目标陷入击破状态时，\\n\u3000一定时间内无法行动。在无法行动期间，\\n\u3000大幅降低自身的物理防御力和魔法防御力，\\n\u3000无法被击破。');
insert into unit_enemy_data values (304200, '卡尔古', 304200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】吞吃尸体的凶暴大型魔兽。\\n全身缠绕着强韧的尖刺，执着的捕获猎物。\\n·对全体敌人造成与其最大生命值相适应的\\n中等物理伤害。\\n·对后方范围造成中等物理伤害\\n\u3000并令其眩晕。');
insert into unit_enemy_data values (304201, '卡尔古', 304200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】吞吃尸体的凶暴大型魔兽。\\n全身缠绕着强韧的尖刺，执着的捕获猎物。\\n·对全体敌人造成与其最大生命值相适应的\\n中等物理伤害。\\n·对后方范围造成中等物理伤害\\n\u3000并令其眩晕。\\n·对前方第2个敌人造成中等物理伤害。\\n·卡尔古在生命值低于50%后会展开\\n\u3000更为激烈凶暴的攻击。');
insert into unit_enemy_data values (304300, '腾特帕斯', 304300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】君临海边的巨大软体魔兽。\\n强韧的触手和钢铁的大鱼叉可以彻底破坏一切。\\n·根据角色最大生命值，对全体敌人造成相应威力\\n\u3000的大量物理伤害。\\n·对后方范围造成中等魔法伤害，\\n\u3000令其陷入麻痹状态，另外特大幅降低物理攻击力、\\n\u3000魔法攻击力。\\n·经过一定时间后对单个敌人造成\\n\u3000超大量物理伤害。\\n·腾特帕斯自己的生命值低于40％后，\\n\u3000会展开更为激烈凶暴的攻击。');
