insert into unit_enemy_data values (304304, '滑滑章鱼', 304304, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】企图用塔的力量向人类复仇的软体魔兽。\\n年轻时被鱼叉刺到的怨恨比大海还深。\\n\\n·对敌方全体造成物理伤害（大）。\\n·对前方第三名及其后方的角色造成魔法伤害\\n（中），并中幅度降低其物理防御力和行动速度。\\n\u3000同时解除属性值提升效果。\\n·根据剩余的生命值，对剩余生命值最高的敌人\\n\u3000造成物理伤害（大），并中幅度降低其技能值。');
insert into unit_enemy_data values (304400, '岛鲸', 304400, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】吞噬了大量探险家的幻岛巨鲸。冷酷无情地送葬深陷欲望与幻想的愚者。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·对敌方全体造成魔法伤害（中），并极大幅度提升\\n\u3000岛鲸自身的物理防御力和魔法防御力。\\n·对前方三名角色造成魔法伤害（大），\\n\u3000并将其大幅度击飞。\\n·岛鲸自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
