insert into unit_enemy_data values (304800, '幽灵领主', 304800, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【魔法】冥府的高官，征服强者的灵魂，使其成为自己的手下亡灵。\\n将死者的执念附于刀刃之上，使对手的动作变得迟缓。\\n\\n·容易受到魔法攻击力下降的\\n效果影响。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·幽灵领主的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000根据幽灵领主的魔法攻击力，对中范围内的敌人\\n\u3000造成一定比例的魔法伤害。\\n\u3000随后根据造成的总伤害量\\n\u3000降低其最大生命值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，\\n\u3000根据幽灵领主的魔法攻击力造成伤害，\\n\u3000并使其陷入诅咒状态，同时特大幅度\\n\u3000降低其生命值回复量和生命值吸收。该技能所造成的诅咒伤害，\\n\u3000无法回复技能值。\\n·普通攻击会根据幽灵领主的\\n\u3000魔法攻击力，对最近的一名敌人造成\\n\u3000一定比例的魔法伤害。');
insert into unit_enemy_data values (304801, '幽灵领主', 304801, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【魔法】冥府的高官，征服强者的灵魂，使其成为自己的手下亡灵。\\n将死者的执念附于刀刃之上，使对手的动作变得迟缓。\\n\\n·容易受到魔法攻击力下降的\\n效果影响。\\n·战斗开始时，中幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·幽灵领主的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000根据幽灵领主的魔法攻击力，对中范围内的敌人\\n\u3000造成一定比例的魔法伤害。\\n\u3000随后根据造成的总伤害量\\n\u3000降低其最大生命值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，\\n\u3000根据幽灵领主的魔法攻击力造成伤害，\\n\u3000并使其陷入诅咒状态，同时特大幅度\\n\u3000降低其生命值回复量和生命值吸收。该技能所造成的诅咒伤害，\\n\u3000无法回复技能值。\\n·普通攻击会根据幽灵领主的\\n\u3000魔法攻击力，对最近的一名敌人造成\\n\u3000一定比例的魔法伤害。');
insert into unit_enemy_data values (304802, '噬魂怪', 304802, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因塔的魔力苏醒，渴求生命的玛那而\\n不断徘徊的恶灵。挥舞着满载幽鬼执念的大镰刀，无差别地\\n收割生者的灵魂。\\n\\n·对前方范围内的敌人造成魔法伤害（大），\\n\u3000特大幅度降低其物理攻击力。\\n·对前方范围内的敌人造成魔法伤害（中）。\\n\u3000对使用物理攻击的敌人造成追加的魔法伤害，\\n\u3000并降低其行动速度。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并解除其属性值提升效果。');
