insert into unit_enemy_data values (305204, '诅咒·奥拉姆', 305204, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因露娜之塔的魔力而暴走的古代兵器。\\n古老的守护之力化为了散布诅咒的可怕灾祸。\\n\\n·对敌方全体造成物理伤害（大），\\n\u3000并大幅降低自身的物理防御力和魔法防御力。\\n·以前方一名敌人为中心，对范围内的所有敌人\\n\u3000造成物理伤害（中），中幅降低其物理防御力。\\n\u3000同时使其眩晕。\\n·对敌方全体造成物理伤害（中），并赋予\\n\u3000诅咒状态。当角色的生命值在40％以下时，\\n\u3000诅咒的伤害提升至2倍。');
insert into unit_enemy_data values (305205, '奥拉姆', 305205, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】诞生于遥远古代的防卫兵器。\\n漫长的沉睡因恶党的阴谋而被画上了句号。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成物理伤害（大），\\n\u3000并大幅度降低自身的物理攻击力。\\n·对前方中范围内的敌人造成物理伤害（中），\\n\u3000并大幅度降低其物理攻击力。\\n·对前方大范围内的敌人造成物理伤害（中），\\n\u3000并使其麻痹。\\n\u3000角色的生命值低于70%时，\\n\u3000麻痹的持续时间变长。\\n·奥拉姆自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
