insert into unit_enemy_data values (305300, '高等食尸兽', 305300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】沐浴在月神魔力之下而变异的狰狞魔兽。强韧的爪牙能轻而易举地将身经百战的冒险者撕裂。\\n\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（大），并回复自身的生命值。\\n·对后方范围内的敌人造成物理伤害，使其\\n\u3000陷入眩晕。\\n\u3000同时中幅度降低敌方全体的物理防御力。\\n·对前方三名角色造成物理伤害，\\n\u3000并使其陷入中毒状态。');
insert into unit_enemy_data values (305301, '寒冰卡尔古', 305301, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】栖息于雪山中的狞恶魔狼。\\n强韧的爪牙能轻而易举地\\n将历练的冒险者变为冰块墓碑。\\n\\n·连结爆发时，对敌方全体\\n\u3000造成物理伤害（大）。\\n\\n·以最后方的敌人为中心，\\n\u3000对范围内的所有敌人造成\\n\u3000物理伤害（小），并使其眩晕。\\n\u3000同时中幅度降低敌方全体的\\n\u3000物理防御力。\\n\\n·对前方范围内的全体敌人造成\\n\u3000物理伤害（小），并赋予毒状态。');
