insert into unit_enemy_data values (305508, '迷雾追踪者', 305508, 0, 16, 270, 1000, 2, 3.0, 0, 0, 0, '【魔法】散发着塔的魔力的雾气实体化而成的魔物。\\n四处散播怨气，与分身一同伺机加害冒险者。\\n\\n·对敌方全体造成魔法伤害（大）。\\n\u3000在敌人处于诅咒状态时，提升造成的伤害，\\n\u3000若敌人处于毒咒状态，进一步提升造成的伤害。\\n·对敌方全体造成魔法伤害（中）。\\n\u3000若敌人处于诅咒状态，则使其陷入毒咒状态。同时，\\n\u3000当迷雾追踪者自身的生命值低于80%时，\\n\u3000将短时间大幅降低物理攻击力和魔法攻击力。\\n·召唤分身。\\n\u3000召唤时击退敌方全体。\\n·分身会对敌方全体造成物理伤害（中），\\n\u3000并使其陷入诅咒状态。\\n·分身的生命值低于30%时，将为我方魔法攻击力\\n\u3000最高的一名角色回复1000技能值。\\n·迷雾追踪者自身的生命值低于80%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (305600, '异界魔主', 305600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【魔法】渴望获得月神魔力而出现的异界魔物。\\n企图凭魔眼和自己率领的士兵征服露娜之塔。\\n\\n·对敌方全体造成魔法伤害（大），\\n\u3000并中幅度降低其魔法防御力。\\n·随机对一名角色造成\\n\u30006次魔法伤害（小）。\\n·对后方第三名角色造成魔法伤害（中），\\n\u3000并将其长时间拘束。\\n\\n·同时对敌方全体造成魔法伤害（小）。');
insert into unit_enemy_data values (305700, '巨型哥布林', 305700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】豪爽地挥舞战斧，身经百战的哥布林战士。\\n腋下夹着年幼的战士，为其展示千锤百炼的技艺。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会小幅提升自身的物理攻击力和\\n\u3000物理防御力，并对最近的一名敌人造成\\n\u3000物理伤害（特大）。该技能所造成的伤害\\n\u3000不会回复技能值。');
insert into unit_enemy_data values (305701, '巨型哥布林', 305701, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '豪爽地挥舞战斧，身经百战的哥布林战士。\\n腋下夹着年幼的战士，为其展示千锤百炼的技艺。\\n\\n·必杀技能中幅提升自身的物理攻击力，\\n\u3000小幅提升自身的物理防御力，同时\\n\u3000对前方一名敌人造成物理伤害（特大）。');
insert into unit_enemy_data values (305702, '巨型哥布林', 305702, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】豪爽地挥舞战斧，身经百战的哥布林战士。\\n腋下夹着年幼的战士，为其展示千锤百炼的技艺。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·巨型哥布林的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会小幅提升自身的物理攻击力和\\n\u3000物理防御力，并对最近的一名敌人造成\\n\u3000物理伤害（特大），\\n\u3000同时使其陷入眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成物理伤害（特大），\\n\u3000并使其陷入眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (305703, '蛮族首领', 305703, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】凭借强健的体魄及内心\\n在沙漠中艰难生存的蛮族战士。为\\n保护家族幼子，挥舞战斧战斗到底。\\n\\n·必杀技能会\\n\u3000中幅提升自身的物理攻击力，\\n\u3000小幅提升物理防御力。\\n\u3000并对前方一名角色造成物理伤害（特大）。');
insert into unit_enemy_data values (305707, '巨型哥布林', 305707, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】领主的左膀右臂，哥布林大战士。\\n豪迈地挥舞起巨大的战斧，击碎眼前的敌人。\\n\\n·战斗开始时，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，使用伤害上限技能，\\n\u3000当一次行动对其施加的伤害超过一定数值，\\n\u3000伤害量将根据施加的伤害值而减少。\\n·巨型哥布林的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对前方一名角色造成物理伤害\\n\u3000（特大），小幅提升自身的物理攻击力和物理\\n\u3000防御力。\\n·根据角色的最大生命值，对前方一名角色造成\\n\u3000一定比例的物理伤害。');
insert into unit_enemy_data values (305800, '海蛞蝓', 305800, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】海神的使者，一种神秘的软体魔物。\\n据闻几乎全身都是由带有魔力的海水构成。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海蛞蝓的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并大幅降低其物理攻击力和\\n\u3000魔法攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·无视敌人的挑衅，以前方第四名角色\\n\u3000为中心，对小范围内的敌人持续造成\\n\u3000魔法伤害（小）。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·普通攻击会无视敌人的挑衅，对前方第四名角色\\n\u3000造成魔法伤害（特大），\\n\u3000并以该角色为中心，\\n\u3000大幅降低小范围内敌人的行动速度。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (305801, '海神使者', 305801, 0, 16, 270, 1000, 2, 2.6, 0, 0, 0, '【魔法】在露娜之塔肆虐的海兽。曾侍奉远古的海神，\\n如今却成为了舍弃正义的破坏者。\\n\\n·对敌方全体造成魔法伤害。\\n·在一定范围内，降低敌方的物理攻击力和\\n\u3000魔法防御力，并展开造成持续\\n\u3000魔法伤害的领域。\\n·对前方第二名、第三名敌人\\n\u3000造成魔法伤害（大）。');
insert into unit_enemy_data values (305802, '海蛞蝓', 305802, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】侍奉海神的奇妙魔物。\\n当看到有入侵者威胁水域的和平时，\\n它便会行使神使之力将其一举消灭。\\n\\n·对敌方全体造成魔法伤害（大）。\\n\\n·对前方第二名角色造成魔法伤害\\n\u3000（特大），对前方第三名角色造成\\n\u3000魔法伤害（中）。\\n\\n·在一定范围内，展开降低魔法防御\\n\u3000力和持续造成魔法伤害的领域。');
insert into unit_enemy_data values (305803, '海蛞蝓', 305803, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】海神的使者，一种神秘的软体魔物。\\n据闻几乎全身都是由带有魔力的海水构成。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海蛞蝓的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并大幅降低其物理攻击力和\\n\u3000魔法攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·无视敌人的挑衅，以前方第四名角色\\n\u3000为中心，对小范围内的敌人持续造成\\n\u3000魔法伤害（小）。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·普通攻击会无视敌人的挑衅，对前方第四名角色\\n\u3000造成魔法伤害（特大），\\n\u3000并以该角色为中心，\\n\u3000特大幅度降低小范围内敌人的行动速度。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (305900, '极彩鸟', 305900, 0, 16, 270, 1000, 2, 2.2, 0, 0, 0, '【魔法】能散发出惊人的吸引力，有着缤纷色彩的艳丽鸟类。\\n一旦被它的美艳所困，便会成为不惜袭击同伴的仆人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000同时，大幅降低其物理攻击力和\\n\u3000魔法攻击力。\\n\u3000当角色的生命值小于最大生命值的60％时，\\n\u3000赋予魅惑状态。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对前方两名角色造成魔法伤害（中），\\n\u3000并赋予魅惑状态。\\n\u3000同时，大幅降低其物理攻击力和\\n\u3000魔法攻击力。\\n\u3000该技能所造成的物理攻击力和魔法攻击力降低效果，\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (305901, '极彩鸟', 305901, 0, 16, 270, 1000, 2, 2.2, 0, 0, 0, '【魔法】能散发出惊人的吸引力，有着缤纷色彩的艳丽鸟类。\\n一旦被它的美艳所困，便会成为不惜袭击同伴的仆人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000同时，大幅降低其物理攻击力和\\n\u3000魔法攻击力。\\n\u3000当角色的生命值小于最大生命值的60％时，\\n\u3000赋予魅惑状态。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对前方两名角色造成魔法伤害（中），\\n\u3000并赋予魅惑状态。\\n\u3000同时，大幅降低其物理攻击力和\\n\u3000魔法攻击力。\\n\u3000该技能所造成的物理攻击力和魔法攻击力降低效果，\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (306000, '邪糕神伽米', 306000, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【物理】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·必杀技能对敌方全体造成\\n\u3000物理伤害（大）。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000降低物理攻击力。\\n·在前方中距离召唤倒计时式炸弹年糕。\\n·炸弹年糕会在召唤后\\n\u30008秒产生大爆炸，对范围内的敌方全体造成\\n\u3000物理伤害（中）。\\n\u3000但是，在爆炸前将炸弹年糕的生命值\\n\u3000降到50%，\\n\u3000则小幅回复敌方全体生命值，并消失。\\n·邪糕神伽米自身的生命值\\n\u3000降到50%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (306001, '邪糕神伽米', 306000, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【物理】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·必杀技能对敌方全体造成\\n\u3000物理伤害（大）。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000降低物理攻击力。');
insert into unit_enemy_data values (306002, '邪糕神伽米', 306000, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【物理】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·必杀技能对敌方全体造成\\n\u3000物理伤害（大）。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000降低物理攻击力。\\n·在前方中距离召唤倒计时式炸弹年糕。\\n·炸弹年糕会在召唤后\\n\u30008秒产生大爆炸，对范围内的敌方全体造成\\n\u3000物理伤害（中）。\\n\u3000但是，在爆炸前将炸弹年糕的生命值\\n\u3000降到50%，\\n\u3000则小幅回复敌方全体生命值，并消失。\\n·邪糕神伽米自身的生命值\\n\u3000降到50%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (306003, '邪糕神伽米', 306000, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【物理】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·必杀技能对敌方全体造成\\n\u3000物理伤害（大）。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000降低物理攻击力。\\n·在前方中距离召唤倒计时式炸弹年糕。\\n·炸弹年糕会在召唤后\\n\u30008秒产生大爆炸，对范围内的敌方全体造成\\n\u3000物理伤害（中）。\\n\u3000但是，在爆炸前将炸弹年糕的生命值\\n\u3000降到50%，\\n\u3000则小幅回复敌方全体生命值，并消失。\\n·邪糕神伽米自身的生命值\\n\u3000降到50%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (306004, '邪糕神伽米', 306004, 0, 16, 270, 3000, 1, 0.0, 0, 0, 0, '【MODE1】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·邪糕神伽米自身收到攻击时，\\n 小幅回复己方全体的生命值，\\n\u3000回复邪糕神伽米自身的全部生命值。');
insert into unit_enemy_data values (306005, '邪糕神伽米', 306005, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【MODE2】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·对前方一名角色造成物理伤害。\\n·对前方范围内造成魔法伤害（中），\\n\u3000并大幅击飞。\\n·注入6份邪念，召唤一只烦恼（红）。\\n·注入3份邪念，召唤一只烦恼（紫）。\\n·注入1份邪念，召唤一只烦恼（蓝）。');
insert into unit_enemy_data values (306006, '邪糕神伽米', 306006, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【MODE3】汇聚了邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·对前方一名角色造成物理伤害。\\n·必杀技能对敌方全体造成\\n\u3000物理伤害（大）。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000降低物理攻击力。\\n·在前方中距离召唤倒计时式炸弹年糕。\\n·炸弹年糕会在召唤后\\n\u30008秒产生大爆炸，对范围内的敌方全体造成\\n\u3000物理伤害（中）。\\n\u3000但是，在爆炸前将炸弹年糕的生命值\\n\u3000降到50%，\\n\u3000则小幅回复敌方全体生命值，并消失。');
insert into unit_enemy_data values (306007, '邪糕神伽米', 306000, 0, 16, 270, 2160, 1, 1.6, 0, 0, 0, '【物理】由汇集的邪念残渣化身而成的邪神。\\n净化所有显现出的烦恼，迎接新年吧！\\n\\n·连结爆发时，根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000并小幅度降低其物理攻击力。\\n·在前方中距离召唤倒计时式炸弹年糕。\\n·炸弹年糕会在被召唤\\n8秒后爆炸，对范围内的全体敌方造成\\n\u3000物理伤害（中）。\\n\u3000然而，若炸弹年糕的生命值在爆炸前\\n\u3000低于50%，\\n\u3000则小幅度回复敌方全体生命值，并就此消失。');
