insert into unit_enemy_data values (306100, '偷盗豺狼', 306100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】潜藏在黑暗之中，在村落偷盗财物的\\n魔物头领。\\n是一个会毫不留情地使用蒙骗回来的赃物攻击敌人，\\n并妨碍开拓的流氓。\\n\\n·对前方三名角色造成\\n\u3000物理伤害（中），\\n\u3000并使其麻痹。\\n\\n·对敌方全体造成物理伤害（小），\\n\u3000并降低其行动速度。\\n\u3000同时，使前方小范围\\n\u3000陷入毒状态。');
insert into unit_enemy_data values (306101, '偷盗豺狼', 306101, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】潜藏在黑暗之中，在村落中偷盗财物的魔物头领。\\n是一个会灵巧地使用蒙骗回来的赃物攻击敌人的流氓。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·偷盗豺狼的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成物理伤害（大），\\n\u3000并使其在短时间内陷入麻痹状态。\\n\u3000若敌人处于中毒状态，则使其陷入长时间麻痹状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对敌方全体造成物理伤害（中），\\n\u3000并小幅降低其行动速度。\\n\u3000当角色的生命值低于90%时，\\n\u3000赋予中毒状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。');
