insert into unit_enemy_data values (306302, '长耳猴首领', 306302, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】使用月神魔力，统领长耳猴的仙猿。自在操纵宝武具之棍，击退所有敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·长耳猴首领的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n对敌方全体造成物理伤害（大），\\n\u3000并使其陷入眩晕状态。该攻击对处于烧伤状态的敌人\\n\u3000所造成的伤害会变为1.5倍。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对前方三名角色造成物理伤害（中）。\\n·对前方两名角色造成魔法伤害（中），\\n\u3000并使其陷入烧伤状态。该技能所造成的烧伤伤害，\\n\u3000无法回复技能值。\\n·对前方一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (306303, '长耳猴首领', 306303, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】使用月神魔力，统领长耳猴的仙猿。自在操纵宝武具之棍，击退所有敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，特大幅度提升我方全体的\\n\u3000物理防御力和魔法防御力。\\n·长耳猴首领的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n对敌方全体造成物理伤害（大），\\n\u3000并使其陷入眩晕状态。该攻击对处于烧伤状态的敌人\\n\u3000所造成的伤害会变为1.5倍。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对前方三名角色造成物理伤害（中）。\\n·对前方两名角色造成魔法伤害（中），\\n\u3000并使其陷入烧伤状态。该技能所造成的烧伤伤害，\\n\u3000无法回复技能值。\\n·对前方一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (306400, '野性飞鸦', 306400, 0, 16, 270, 1000, 1, 3.5, 0, 0, 0, '【物理】作为荒山的捕食者君临此地的凶恶怪鸟兽。\\n利用通过无数次狩猎锻炼而成的惊人怪力与硬爪袭向入侵者。\\n\\n·对敌方全体造成物理伤害（大），并降低其技能值上升量。每次发动都会提升造成的伤害。这个效果最多可以累积三次。\\n\\n·以后方第二名敌人为中心，造成小范围的物理伤害（小），并赋予麻痹状态。');
insert into unit_enemy_data values (306401, '野性飞鸦', 306401, 0, 16, 270, 1000, 1, 3.5, 0, 0, 0, '【物理】作为荒山的捕食者君临此地的\\n凶恶怪鸟兽。利用通过无数次狩猎锻炼而成的惊人怪力与硬爪\\n袭向入侵者。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·野性飞鸦的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成物理伤害（中），\\n\u3000并大幅降低其技能值上升量。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以后方第二名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（大），并使其陷入麻痹状态。');
insert into unit_enemy_data values (306500, '部落兽人', 306500, 0, 16, 270, 1000, 1, 0.9, 0, 0, 0, '【物理】边境森林中的兽人们的头目。四处传播其具有独特民族性的奇怪旋律。那奇怪的声音可令敌人陷入混乱。\\n\\n·对敌方全体造成物理伤害（大），并降低其物理防御力与魔法防御力，同时提升自身的物理攻击力与魔法攻击力。\\n\\n·对前方四名敌人造成魔法伤害（小），当敌人的生命值降至70%以下时，赋予混乱状态。');
insert into unit_enemy_data values (306501, '吵闹兽人', 306501, 0, 16, 270, 1000, 1, 0.9, 0, 0, 0, '【物理】被露娜之塔的力量所魅惑的兽人魁首。\\n以奇特的太鼓旋律在战场上引起骚乱。\\n\\n·对敌方全体造成物理伤害（大），并降低其物理防御力\\n\u3000和魔法防御力。同时提升自身的\\n\u3000物理攻击力和魔法攻击力。\\n·对前方三名角色造成魔法伤害（小），\\n\u3000并将其小幅度击飞。若敌人生命值低于\\n\u300070%，使其陷入混乱状态。\\n·根据剩余的生命值，对敌方全体造成魔法伤害\\n（大），并降低其行动速度。\\n\u3000同时解除敌方全体的\\n\u3000属性值提升效果。');
insert into unit_enemy_data values (306600, '毒液沙罗曼蛇', 306600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】在水边洒满剧毒，以此袭击猎物的\\n蝾螈魔物。积存在其身体里的剧毒十分危险，\\n光是靠近都能令猎物失去意识。\\n\\n·对敌方全体造成魔法伤害（中），\\n\u3000并使其陷入中毒状态。\\n\u3000同时使其陷入猛毒状态。\\n\\n·以最近的敌人为中心，\\n\u3000对范围内的所有敌人造成\\n\u3000魔法伤害（小），并使其陷入猛毒状态。');
insert into unit_enemy_data values (306700, '丑萌巧克力兽', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因被痛骂而暴走的巨大巧克力魔兽。\\n呢喃爱意一不小心也会化作伤害他人的刀刃。\\n\\n·必杀技能会根据敌方全体角色的\\n\u3000最大生命值给予物理伤害（大），\\n\u3000处于烧伤状态时，会追加伤害。\\n·给予前方三名角色物理伤害（中），\\n\u3000使其眩晕。\\n\u3000并且角色的生命值低于50%时，\\n\u3000会使其变成烧伤状态。\\n·丑萌巧克力兽自身的生命值\\n\u3000低于50%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (306701, '丑萌巧克力兽', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因被痛骂而暴走的巨大巧克力魔兽。\\n呢喃爱意一不小心也会化作伤害他人的刀刃。\\n\\n·必杀技能会根据敌方全体角色的\\n\u3000最大生命值给予物理伤害（大），\\n\u3000处于烧伤状态时，会追加伤害。\\n·给予前方三名角色物理伤害（中），\\n\u3000使其眩晕。\\n\u3000并且角色的生命值低于50%时，\\n\u3000会使其变成烧伤状态。');
