insert into unit_enemy_data values (306704, '丑萌巧克力兽', 306700, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE1】猛烈喷射巧克力，将自身覆盖隐藏。\\n不断受到敌人的攻击，爆发愤怒的一击。\\n\\n·给予敌方全体物理伤害（小），使其战斗期间\\n\u3000变为烧伤状态。\\n·藏在巧克力巨蛋之中时，\\n\u3000丑萌巧克力兽自身的物理防御力中幅提升。\\n\u3000解除巧克力巨蛋状态后，会根据在此\\n\u3000期间所受到的伤害量反击\\n\u3000给予物理伤害。\\n\u3000并且丑萌巧克力兽自身的物理防御力\\n\u3000大幅下降。');
insert into unit_enemy_data values (306705, '丑萌巧克力兽', 306700, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【MODE2】可可兽振奋起来，\\n为了从激化的魔物攻击下，\\n 保护重要的人们\\n 投身死地。\\n\\n·必杀技能根据敌方全体角色的\\n\u3000最大生命值给予物理伤害（大），\\n·可可兽会作为角色的同伴出现。\\n\u3000可可兽出现期间，角色全体的物理\\n\u3000攻击力和行动速度中幅提升。\\n·给予敌方全体魔法伤害（小），\\n\u3000大幅降低其行动速度。\\n');
insert into unit_enemy_data values (306706, '丑萌巧克力兽', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【MODE3】轰鸣的咆哮声回响在会场之中。\\n爱恨交织的情人节事变终于迎来终局。\\n\\n·必杀技能根据敌方全体角色的\\n\u3000最大生命值给予物理伤害（大），\\n\u3000烧伤状态下，追加伤害。\\n·给予前方三名角色物理伤害（中），\\n\u3000使其眩晕。\\n\u3000角色的生命值低于50%时，\\n\u3000使其陷入烧伤状态。');
insert into unit_enemy_data values (306708, '丑萌巧克力兽', 306700, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【物理】因恶毒的言语而暴走的巨大巧克力魔物。\\n呢喃的爱意若被误解，也会化作伤害他人的刀刃。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成物理伤害（大），\\n\u3000若处于烧伤状态，则赋予追加伤害。\\n\\n·根据角色的最大生命值，对前方三名角色\\n\u3000造成物理伤害（中），并使其陷入\\n\u3000烧伤状态。');
