insert into unit_enemy_data values (306804, '哀叹女神', 306800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE1】将【自卫团】成员作为暗影召唤。\\n企图斩断羁绊，攻击挑战者内心软弱之处。\\n\\n·让暗影出现的\\n·哀叹女神自身不会做出攻击行动。');
insert into unit_enemy_data values (306805, '哀叹女神', 306800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE2】以永久之黑暗充斥着周围，\\n\u3000可使用诅咒咒语\\n\u3000不断阻碍着冒险者的一切。\\n\\n·必杀技能根据敌方全体角色的\\n\u3000最大生命值造成魔法伤害（大），\\n\u3000降低魔法防御力。\\n·对前方三名角色造成魔法伤害（小），\\n\u3000降低物理防御力，减少技能值，\\n\u3000降低获取率。\\n\u3000并且使其变成诅咒、混乱状态，降低行动速度。\\n\u3000即使被赋予了异常状态免疫效果，\\n\u3000该技能也会生效。\\n·哀叹女神自身的物理防御力下降。\\n\u3000并且变成诅咒状态，减少技能值。');
insert into unit_enemy_data values (306806, '哀叹女神', 306800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【MODE3】充满狂气的悲哀，让女王解放了真正的身姿。被诅咒的愿望容不下一丝的希望。\\n\\n·必杀技能根据敌方全体角色的\\n\u3000最大生命值造成魔法伤害（大），\\n\u3000降低魔法防御力。\\n·对前方第二名、第三名角色\\n\u3000造成魔法伤害（中），使其眩晕。\\n·对前方两名角色造成魔法伤害（小），\\n\u3000降低物理攻击力，减少技能值。\\n\u3000并且使其陷入诅咒状态。\\n\u3000即使被赋予了异常状态免疫效果，\\n\u3000该技能也会生效。');
insert into unit_enemy_data values (306807, '哀叹女神', 306807, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因失意和绝望而堕落成魔的悲哀公主。\\n渴求恸哭，用恐怖和诅咒填满一切。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成魔法伤害（大），\\n\u3000并降低其魔法防御力。\\n·对前方第二名、第三名的角色\\n\u3000造成魔法伤害（中），并使其眩晕。\\n·对前方两名角色造成魔法伤害（小），\\n\u3000降低其物理攻击力并减少技能值。\\n\u3000同时使其陷入诅咒状态。\\n·哀叹女神自身的生命值\\n\u3000低于40%时，会展开更为激烈\\n\u3000凶暴的攻击。');
insert into unit_enemy_data values (306808, '哀叹女神', 306807, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因失意和绝望而堕落成魔的悲哀公主。\\n渴求恸哭，用恐怖和诅咒填满一切。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成魔法伤害（大），\\n\u3000并降低其魔法防御力。\\n·根据角色的最大生命值，对前方第二名、第三名的角色\\n\u3000造成魔法伤害（中），\\n\u3000并使其眩晕。');
