insert into unit_enemy_data values (306812, '哀叹女神', 306800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】为了让四周充满永久之暗，\\n\u3000持续地用诅咒和咒语阻挡着\\n\u3000所有冒险者。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成魔法伤害（大），\\n\u3000并降低其魔法防御力。\\n·对前方三名角色造成魔法伤害（小），\\n\u3000降低其物理防御力，在减少其技能值的同时\\n\u3000降低技能值的上升速度。\\n\u3000并且使其陷入诅咒、混乱状态，降低行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·降低哀叹女神自身的物理防御力。\\n\u3000并进入诅咒状态，减少技能值。');
insert into unit_enemy_data values (306813, '哀叹女神', 306800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】充满狂气的悲哀，让女王解放了\u3000真正的身姿。被诅咒的愿望容不下\u3000一丝的希望。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成魔法伤害（大），\\n\u3000并降低其魔法防御力。\\n·对前方第二名、第三名的角色\\n\u3000造成魔法伤害（中），并使其眩晕。\\n·对前方两名角色造成魔法伤害（小），\\n\u3000降低其物理攻击力并减少技能值。\\n\u3000同时使其陷入诅咒状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·战斗开始后，提升自身的魔法攻击力\\n\u3000和行动速度。');
insert into unit_enemy_data values (306900, '三战士图腾', 306900, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】过去曾被人供奉的三名战士的石像。\\n会提升各自的能力，释放出必杀的一击。\\n\\n·更容易受到降低物理攻击力与\\n魔法攻击力的效果影响。\\n·极大幅度提升自身的物理攻击力与\\n\u3000魔法攻击力，对敌方全体造成物理伤害（中）\\n\u3000与魔法伤害（中）。\\n·对前方范围内的敌人造成魔法伤害（小）。\\n·对敌方全体造成物理伤害（中）。');
insert into unit_enemy_data values (306901, '三战士图腾', 306901, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】过去曾被人供奉的三战士石像。\\n相互提升各自的能力，释放出必杀的一击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·三战士图腾的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会大幅提升自身的物理攻击力和魔法\\n\u3000攻击力，并对敌方全体造成物理伤害（中）\\n\u3000和魔法伤害（中）。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·以最近一名敌人为中心，对小范围内的最多三名敌人\\n\u3000造成魔法伤害（中）。\\n·对敌方全体造成物理伤害（中），并特大幅度\\n\u3000降低其物理攻击力和魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
