insert into unit_enemy_data values (307012, '魔石巨人', 307008, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】从异界的魔石中诞生的庞然巨人。\\n以强韧的肉体为武器，发动猛烈的攻击。\\n\\n·容易受到物理攻击力提升·下降的\\n效果影响。\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成物理伤害（大）。\\n·对前方三名角色造成物理伤害（中）。\\n·保持防御姿势一段时间后，根据时间内所受到的\\n\u3000伤害量，对敌方全体造成物理伤害，\\n并降低其行动速度。\\n\u3000同时，使前方两名角色麻痹，\\n\u3000并提升自身的物理攻击力。\\n·对敌方全体造成物理伤害（中），\\n\u3000并大幅度地将其击退。\\n·当魔石巨人的生命值低于35%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (307013, '魔石巨人', 307002, 0, 16, 270, 3000, 1, 5.0, 0, 0, 0, '【模式1】将【美食殿堂】成员作为暗影召唤。以旅途的回忆为盾，试探三人\\n的弱点。\\n\\n·对前方三名角色造成物理伤害（中）。\\n·对敌方全体造成物理伤害（中），\\n\u3000并大幅度地将其击退。');
insert into unit_enemy_data values (307014, '魔石巨人', 307002, 0, 16, 270, 3000, 1, 5.0, 0, 0, 0, '【模式2】强韧的肉体能抵挡\\n一切攻击。以愤怒为食粮，巨人会做出\\n怒涛般的强力反击。\\n\\n·容易受到物理攻击力提升·下降的\\n效果影响。\\n·连结爆发时，对敌方全体造成物理伤害（中）。\\n·对前方三名角色造成物理伤害（大）。\\n·保持防御姿势一段时间后，根据时间内所受到的\\n\u3000伤害量，对敌方全体造成物理伤害，\\n并使前方两名角色麻痹。\\n\u3000同时提升自身的物理攻击力。');
insert into unit_enemy_data values (307015, '魔石巨人', 307002, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【模式3】庞然的巨人解放了\\n全身的力量。在强大无比的破坏力前，\\n耍小聪明的攻击无法奏效。\\n\\n·容易受到物理攻击力提升·下降的\\n效果影响。\\n·连结爆发时，对敌方全体造成物理伤害（中）。\\n·对前方三名角色造成物理伤害（大）。\\n·保持防御姿势一段时间后，根据时间内所受到的\\n\u3000伤害量，对敌方全体造成物理伤害，\\n并使前方两名角色麻痹。\\n\u3000同时提升自身的物理攻击力。\\n·对敌方全体造成物理伤害（大），\\n\u3000并大幅度地将其击退。\\n·战斗开始后，提升自身的物理攻击力\\n\u3000和行动速度。');
insert into unit_enemy_data values (307100, '机械莉玛（MODE1）', 307100, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '神秘巨型兵器的第1形态，结构坚硬并拥有多彩的技能。\\n使用苹果型的补给机器填充未知的能量，\\n要注意的是，它的连续攻击可以令大范围地区化作焦土！\\n\\n·连结爆发时，对敌方全体造成物理伤害（大）。\\n·对前方一名角色造成物理伤害（小）。\\n·在一定范围内，展开降低物理攻击力和回避率，\\n\u3000并续赋赋予物理伤害的领域。\\n\u3000同时解除前方范围内的角色的\\n\u3000属性值提升效果。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并提升自身的技能值。');
insert into unit_enemy_data values (307101, '机械莉玛（MODE2）', 307101, 0, 16, 270, 3000, 1, 1.2, 0, 0, 0, '神秘巨型兵器的第2形态，会把所有状态都切换为攻击。\\n释放出的原子炉莉玛光线可以将大地燃烧殆尽。\\n对无防备的巨大兵器造成会心一击吧！\\n\\n·连结爆发时，对前方小范围内的敌人\\n\u3000造成物理伤害（特大），对中范围内的敌人造成物理伤害（大），\\n\u3000并对大范围内的敌人造成物理伤害（中），\\n\u3000同时大幅度将其击飞。\\n·对前方一名角色造成物理伤害（小）。\\n·随机对一名角色造成物理伤害（小）。\\n\\n·在一定范围内，展开大幅度降低物理攻击力和魔法攻击力\\n\u3000并持续赋予魔法伤害的领域。\\n\u3000·同时使魔法攻击力最高的两名角色\\n\u3000陷入沉默状态。');
insert into unit_enemy_data values (307102, '机械莉玛（MODE3）', 307102, 0, 16, 270, 3000, 1, 5.5, 0, 0, 0, '神秘巨型兵器的第3形态，会自我修复损耗。\\n防卫无人机会阻止外界对满身破绽的本体的攻击。\\n使用范围攻击将无人机一一击退，抓住打倒它的好时机！\\n\\n·大幅度击飞敌方全体，\\n\u3000并召唤3台迷你机械莉玛。\\n·迷你机械莉玛会对前方一名角色\\n\u3000造成物理伤害（小）。\\n·迷你机械莉玛会对随机一名角色\\n\u3000造成魔法伤害（中）。\\n·并根据剩余生命值，对敌方全体\\n\u3000造成一定比例的物理伤害（特大）。');
insert into unit_enemy_data values (307200, '巴吉里斯克', 307200, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】追求露娜魔力飞来的大怪鸟。\\n率领雏鸟，挥下石化的咒剑。\\n\\n·对非石化状态的角色全体造成魔法伤害（大）\\n\u3000使其短时间石化。\\n\u3000对石化状态的角色全员造成最大生命值\\n\u300080%的伤害。\\n·对前方三名角色造成魔法伤害（小）。\\n\u3000并且因该技能伤害\\n\u3000陷入中毒状态的角色会长时间石化。\\n·对前方范围内的角色造成魔法伤害（中）\\n\u3000并且使其变为中毒状态。\\n\u3000自身的生命值降到50％以下时，会对敌方全体\\n\u3000做出反击，召唤出可可。\\n·可可会对前方范围内物理攻击力\\n\u3000最高的一名角色造成物理伤害（小），\\n\u3000并且使其陷入中毒状态。\\n\u3000当自身的生命值处于最大生命的35%以下时，\\n\u3000中幅提升物理攻击力最高的己方同伴的技能值，\\n\u3000自身变为无法战斗状态。');
insert into unit_enemy_data values (307201, '巴吉里斯克', 307201, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】随身携带一把石化咒剑的怪鸟。以尾蛇的毒牙\\n腐蚀猎物，以咒剑的石化效果阻止冒险者前进。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·必杀技能会对所有未处于无法行动状态的角色\\n\u3000造成魔法伤害（大），\\n\u3000并使其陷入石化状态。\\n\u3000对所有处于无法行动状态的角色造成\\n\u3000最大生命值80%的魔法伤害。\\n\u3000同时解除敌方全体的属性值提升效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对距离最近的三名敌人造成魔法伤害（中）。\\n\u3000若有处于中毒状态的角色因该技能而受到伤害，\\n\u3000则使其陷入石化状态。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并使其陷入中毒状态。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。\\n·普通攻击会对最近的一名敌人\\n\u3000造成魔法伤害（大）。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。');
insert into unit_enemy_data values (307202, '巴希列斯', 307202, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】阻挡冒险者穿越森林的\\n石化怪鸟。发出有毒瘴气令人石化，\\n并给予致命的刺击。\\n\\n·必杀技能会对敌方全体\\n\u3000造成魔法伤害（中），\\n\u3000并使其陷入石化状态。\\n\u3000同时解除敌方全体的属性值提升效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\\n·对敌方全体造成魔法伤害（中），\\n\u3000并使其陷入中毒状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (307203, '巴吉里斯克', 307203, 0, 16, 270, 3000, 2, 1.8, 0, 0, 0, '【魔法】随身携带一把石化咒剑的怪鸟。以尾蛇的毒牙\\n腐蚀猎物，以咒剑的石化效果阻止冒险者前进。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·必杀技能会对所有未处于无法行动状态的角色\\n\u3000造成魔法伤害（大），\\n\u3000并使其陷入石化状态。\\n\u3000对所有处于无法行动状态的角色造成\\n\u3000最大生命值80%的魔法伤害。\\n\u3000同时解除敌方全体的属性值提升效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对距离最近的三名敌人造成魔法伤害（中）。\\n\u3000若有处于中毒状态的角色因该技能而受到伤害，\\n\u3000则使其陷入石化状态。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并使其陷入中毒状态。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。\\n\u3000该技能所造成的中毒伤害\\n\u3000无法回复技能值。\\n·普通攻击会对最近的一名敌人\\n\u3000造成魔法伤害（大）。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。');
insert into unit_enemy_data values (307300, '恶霸地方官', 307300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】充斥着贪婪欲念的机关人偶。\\n作为大江户镇的暴政者君临此地，\\n极尽蛮横之事。\\n\\n·对敌方全体造成与角色的最大生命值\\n\u3000相应的物理伤害（大），\\n\u3000并中幅提升自身的物理攻击力。');
