insert into unit_enemy_data values (307303, '恶霸地方官', 307300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】充斥着贪婪欲念的机关人偶。\\n作为大江户镇的暴政者君临此地，\\n极尽蛮横之事。\\n\\n·对敌方全体造成与角色的最大生命值\\n\u3000相应的物理伤害（大），\\n\u3000并中幅提升自身的物理攻击力。同时，\\n\u3000令所有已被召唤的家臣的生命值\\n\u3000变为0。\\n·召唤一名会对眼前一名敌人\\n\u3000进行物理攻击的家臣。\\n\u3000当家臣自己的生命值为0时，\\n\u3000会发生大爆炸，对范围内的所有敌人造成\\n\u3000物理伤害（中）。\\n·当恶霸地方官自身的生命值\\n\u3000低于40%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (307304, '恶霸地方官', 307300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】将【纯白之翼\\n兰德索尔分部】的成员作为暗影\\n召唤出来。嘲笑三人坚固情谊的\\n欲望之影将发动强袭。\\n\\n·召唤暗影\\n·对眼前一名敌人进行物理攻击\\n');
insert into unit_enemy_data values (307305, '恶霸地方官', 307300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】以物欲使周围的生物服从自己，\\n其制造出的假象能将任何敌人拖入欲望的旋涡。\\n\\n·对眼前一名敌人进行物理攻击\\n·对敌方全体造成物理伤害（小），\\n\u3000并赋予魅惑状态。\\n');
insert into unit_enemy_data values (307306, '恶霸地方官', 307300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】当内心充满虚荣之时，\\n就会释放出真正的力量，想以此来支配一切。\\n\\n·对敌方全体造成与角色的最大生命值\\n\u3000相应的物理伤害（大），\\n\u3000并中幅提升自身的物理攻击力。同时，\\n\u3000令所有已被召唤的家臣的生命值\\n\u3000变为0。\\n·召唤一名会对眼前一名敌人\\n\u3000进行物理攻击的家臣。\\n\u3000当家臣自己的生命值为0时，\\n\u3000会发生大爆炸，对范围内的所有敌人造成\\n\u3000物理伤害（中）。\\n');
insert into unit_enemy_data values (307307, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】充斥着贪婪欲念的机关人偶。\\n作为大江户镇的暴政者君临此地，\\n极尽蛮横之事。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害，并中幅度提升\\n\u3000自身的物理攻击力。');
insert into unit_enemy_data values (307308, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】充斥着贪婪欲念的机关人偶。\\n作为大江户镇的暴政者君临此地，\\n极尽蛮横之事。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害，并中幅度提升\\n\u3000自身的物理攻击力。');
insert into unit_enemy_data values (307309, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】充斥着贪婪欲念的机关人偶。\\n作为大江户镇的暴政者君临此地，\\n极尽蛮横之事。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害，并中幅度提升\\n\u3000自身的物理攻击力。同时，\\n\u3000令所有已被召唤的家臣的生命值\\n\u3000降至0。\\n·召唤一名会对眼前一名敌人\\n\u3000进行物理攻击的家臣。\\n\u3000当家臣的生命值降为0时，\\n\u3000会引发大爆炸，并根据角色最大的生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·恶霸地方官自身的生命值\\n\u3000低于40%时，会展开更为激烈\\n\u3000凶暴的攻击。');
insert into unit_enemy_data values (307310, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】充斥着贪婪欲念的机关人偶。\\n作为大江户镇的暴政者君临此地，\\n极尽蛮横之事。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害，并中幅度提升\\n\u3000自身的物理攻击力。同时，\\n\u3000令所有已被召唤的家臣的生命值\\n\u3000降至0。\\n·召唤一名会对眼前一名敌人\\n\u3000进行物理攻击的家臣。\\n\u3000当家臣的生命值降为0时，\\n\u3000会引发大爆炸，并根据角色最大的生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·恶霸地方官自身的生命值\\n\u3000低于40%时，会展开更为激烈\\n\u3000凶暴的攻击。');
insert into unit_enemy_data values (307311, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】将【纯白之翼\\n兰德索尔分部】成员作为暗影召唤。嘲笑三人坚固情谊的\\n欲望之影将发动强袭。\\n\\n·对前方一名角色造成物理伤害。');
insert into unit_enemy_data values (307312, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】利用贪念让周围的生物服从自己，\\n并制造出各种假象将对方拖入欲望的漩涡。\\n\\n·对前方一名角色造成物理伤害。\\n·对敌方全体造成物理伤害（小），\\n\u3000并使其陷入诱惑状态。');
insert into unit_enemy_data values (307313, '恶霸地方官', 307307, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】为了主宰一切，充满虚荣的贪欲\\n释放出真正的力量。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害，并中幅度提升\\n\u3000自身的物理攻击力。同时，\\n\u3000令所有已被召唤的家臣的生命值\\n\u3000降至0。\\n·召唤一名会对眼前一名敌人\\n\u3000进行物理攻击的家臣。\\n\u3000当家臣的生命值降为0时，\\n\u3000会引发大爆炸，并根据角色最大的生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·战斗开始后，提升自身的物理攻击力\\n\u3000和行动速度。');
