insert into unit_enemy_data values (307401, '培提尔其乌斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在露格尼卡恶名昭彰的魔女教干部。\\n哪怕到了异世界，也仍然遵从着自己的信念，\\n凭借强大的权能“不可视之手”在暗中活跃。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并降低技能值。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\\n·根据角色的最大生命值，对后方两名角色造成一定比例的\\n\u3000物理伤害。\\n·玩家左侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力小幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力中幅度降低。\\n·中央目标处于击破状态时，\\n\u3000物理防御力和魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力特大幅度降低。\\n·玩家右侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力大幅度降低。');
insert into unit_enemy_data values (307402, '培提尔其乌斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在露格尼卡恶名昭彰的魔女教干部。\\n哪怕到了异世界，也仍然遵从着自己的信念，\\n凭借强大的权能“不可视之手”在暗中活跃。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并降低技能值。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\\n·根据角色的最大生命值，对后方两名角色造成一定比例的\\n\u3000物理伤害。\\n·玩家左侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力小幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力中幅度降低。\\n·中央目标处于击破状态时，\\n\u3000物理防御力和魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力特大幅度降低。\\n·玩家右侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力大幅度降低。\\n·当剩余时间少于40秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (307403, '培提尔其乌斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在露格尼卡恶名昭彰的魔女教干部。\\n哪怕到了异世界，也仍然遵从着自己的信念，\\n凭借强大的权能“不可视之手”在暗中活跃。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并降低技能值。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\\n·根据角色的最大生命值，对后方两名角色造成一定比例的\\n\u3000物理伤害。\\n·玩家左侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力小幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力中幅度降低。\\n·中央目标处于击破状态时，\\n\u3000物理防御力和魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力特大幅度降低。\\n·玩家右侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力大幅度降低。\\n·当剩余时间少于40秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (307404, '培提尔其乌斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】召唤雷姆和拉姆的暗影。\\n被大罪司教的疯狂所吞噬的双胞胎暗影，\\n会化为魔女教的爪牙，阻挡在伙伴的面前。\\n\\n·对眼前一名敌人造成物理伤害。');
insert into unit_enemy_data values (307405, '培提尔其乌斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】威力增强后的权能变得强大无比，\\n将会毫不留情地消灭怠惰的仇敌。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\\n·根据角色的最大生命值，对后方两名角色造成一定比例的\\n\u3000物理伤害。\\n·对剩余的生命值比例低的一名角色\\n\u3000造成魔法伤害（大），\\n\u3000并赋予倒计时。\\n\u3000当倒计时为0时，根据角色的最大生命值，\\n\u3000造成一定比例的魔法伤害。\\n·玩家左侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力小幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力中幅度降低。\\n·中央目标处于击破状态时，\\n\u3000物理防御力中幅度降低，魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力特大幅度降低。\\n·玩家右侧目标处于击破状态时，\\n\u3000物理防御力中幅度降低和魔法防御力大幅度降低，\\n\u3000物理攻击力小幅度降低，魔法攻击力大幅度降低。');
insert into unit_enemy_data values (307406, '培提尔其乌斯', 307400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】失去理性和人类姿态的狂热信徒，\\n只为执念所行动，为忠爱竭尽全力。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并降低技能值。\\n\u3000同时对敌方全体造成魔法伤害（中）。\\n\\n·根据角色的最大生命值，对后方两名角色造成一定比例的\\n\u3000物理伤害。\\n·玩家左侧的目标处于击破状态时，\\n\u3000物理防御力和魔法防御力小幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力中幅度降低。\\n·中央目标处于击破状态时，\\n\u3000物理防御力中幅度降低，魔法防御力大幅度降低，物理攻击力\\n\u3000小幅度降低，魔法攻击力特大幅度降低。\\n·玩家右侧目标处于击破状态时，\\n\u3000物理防御力中幅度降低和魔法防御力大幅度降低，\\n\u3000物理攻击力小幅度降低，魔法攻击力大幅度降低。\\n·当剩余时间少于40秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (307500, '基兹', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】卷起暴风的大怪鸟。\\n从魔物图鉴中逃出，大肆进行破坏。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·以最近的的敌人为中心，对范围内两名敌人造成物理伤害（中），\\n\u3000并使其眩晕。');
insert into unit_enemy_data values (307501, '基兹', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】卷起暴风的大怪鸟。\\n从魔物图鉴中逃出，大肆进行破坏。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·以最近的敌人为中心，对范围内两名敌人造成物理伤害（中），\\n\u3000并使其眩晕。');
insert into unit_enemy_data values (307502, '基兹', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】卷起暴风的大怪鸟。\\n从魔物图鉴中逃出，大肆进行破坏。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·以最近的敌人为中心，对范围内两名敌人造成物理伤害（中），\\n\u3000并使其眩晕。\\n·根据角色的最大生命值，对前方范围内的敌人\\n\u3000造成物理伤害（中）。\\n·基兹自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (307503, '基兹', 307500, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】卷起暴风的大怪鸟。\\n从魔物图鉴中逃出，大肆进行破坏。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·以最近的敌人为中心，对范围内两名敌人造成物理伤害（中），\\n\u3000并使其眩晕。\\n·根据角色的最大生命值，对前方范围内的敌人\\n\u3000造成物理伤害（中）。\\n·基兹自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (307504, '红极鸟', 307504, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】吞噬塔中的魔力，四处肆虐的巨大魔鸟。\\n要是掉以轻心地接近它，便会被暴风与狂雷撕裂。\\n\\n·必杀技能会对敌方全体\\n\u3000造成物理伤害（大）。\\n·以前方第二名角色为中心，\\n\u3000对小范围内的敌人造成物理伤害（小），并使其眩晕。\\n·对前方范围内的敌人造成物理伤害（中）。');
insert into unit_enemy_data values (307505, '基兹', 307505, 0, 3, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】身缠猛烈暴风的大怪鸟。\\n从魔物图鉴中逃出，大肆进行破坏。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并使其眩晕。\\n\\n·根据最大生命值，对前方范围内的敌人\\n\u3000造成物理伤害（中）。\\n·基兹自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (307600, '疯狂小丑', 307600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】意图将露娜之塔作为自己舞台的\\n魔界小丑。面带开朗而疯狂的笑容，玩弄着观众的生命。\\n\\n·对敌方全体造成魔法伤害（大），\\n\u3000并赋予黑暗状态。\\n·随机对一名敌人造成六次物理伤害（小），\\n\u3000并对敌方全体造成物理伤害（大）。\\n\u3000越是位于前列的角色，\\n\u3000越容易受到该攻击。\\n·在前方中距离召唤倒计时式炸弹。\\n\u3000炸弹会在召唤后8秒发生大爆炸，\\n\u3000对范围内的所有敌人造成物理伤害（中）。\\n\u3000但是，若炸弹的生命值在爆炸前\\n\u3000低于1%，则会对疯狂小丑造成\\n\u3000最大生命值15％的物理伤害。');
insert into unit_enemy_data values (307700, '阿尔玛', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成魔法伤害（特大）。\\n·以最近的敌人为中心，对其身边三名敌人造成\\n\u3000物理伤害（中）。');
insert into unit_enemy_data values (307701, '阿尔玛', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成魔法伤害（特大）。\\n·以最近的敌人为中心，对其身边三名敌人造成\\n\u3000物理伤害（中）。');
insert into unit_enemy_data values (307702, '阿尔玛', 307700, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】从远古开始守护迷宫的防卫兵器。在最深处静静等待着跨越所有陷阱而来的入侵者。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成魔法伤害（特大）。\\n·以最近的敌人为中心，对其身边三名敌人造成\\n\u3000物理伤害（中）。\\n·对残余生命值最高的敌人，根据其最大生命值，\\n\u3000造成魔法伤害（中），并使其麻痹。\\n·阿尔玛自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。');
