insert into unit_enemy_data values (307704, '诅咒·阿尔玛', 307704, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】因露娜之塔的魔力而失控的古代兵器。\\n被魔力强化后的守护之力变作摧毁万物的灾难。\\n\\n·对敌方全体造成物理伤害（特大）。\\n\\n·随机3次对前方小范围内的敌人\\n\u3000造成物理伤害（大）。\\n·对魔法攻击力最高的一名角色\\n\u3000造成物理伤害（中），并使其麻痹。\\n\u3000同时，对该角色周围的小范围内的敌人\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (307800, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】自古以来便守护着岛上气候的守护神。\\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\\n为无垠的大地降下哀泣的雨。\\n\\n·必杀技能对敌方全体造成\\n\u3000魔法伤害（大），同时击飞敌人。\\n\u3000并对前方范围内的敌人造成\\n\u3000追加的魔法伤害（中）。\\n·对最远的一名敌人造成魔法伤害（中），\\n\u3000并小幅度拉近目标。');
insert into unit_enemy_data values (307801, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】自古以来便守护着岛上气候的守护神。\\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\\n用哀泣的雨吞没无垠的大地。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000并将其击飞。\\n\u3000同时以最近的敌人为中心，\\n\u3000根据角色的最大生命值对范围内的敌人\\n\u3000追加造成一定比例的魔法伤害。\\n·对距离最远的一名敌人造成魔法伤害（中），\\n\u3000并将其小幅度拉近。');
insert into unit_enemy_data values (307802, '雨神大人', 307800, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】自古以来便守护着岛上气候的守护神。\\n对友人的思念令其忧心忡忡，最终变成了令人害怕的模样。\\n用哀泣的雨吞没无垠的大地。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000并将其击飞。\\n\u3000同时以最近的敌人为中心，\\n\u3000根据角色的最大生命值对范围内的敌人\\n\u3000追加造成一定比例的魔法伤害。\\n·对前方范围内的所有敌人造成魔法伤害（中），\\n\u3000并将其小幅度击飞。同时对距离最近的一名敌人\\n\u3000造成追加的魔法伤害（中）。\\n·对距离最远的一名敌人造成魔法伤害（中），\\n\u3000并将其小幅度拉近。\\n·雨神大人自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
