insert into unit_enemy_data values (308201, '剧毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】生长异常的碧的曼德拉草。\\n会用强韧的根茎与坚硬的种子子弹压制眼前的敌人。\\n\\n·根据最大生命值，对前方两名角色造成一定比例\\n\u3000的物理伤害。\\n\\n·连结爆发时，对敌方全体造成物理伤害（大），\\n\u3000并根据造成的\\n\u3000累计伤害量，回复自身的生命值。\\n·持续对敌方全体造成物理伤害（小），\\n\u3000并降低其物理防御力3次。\\n·提升自身的物理攻击力的同时，\\n\u3000增加技能值。');
insert into unit_enemy_data values (308202, '剧毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】生长异常的碧的曼德拉草。\\n会用强韧的根茎与坚硬的种子子弹压制眼前的敌人。\\n\\n·根据最大生命值，对前方两名角色造成一定比例\\n\u3000的物理伤害。\\n\\n·连结爆发时，对敌方全体造成物理伤害（大），\\n\u3000并根据造成的\\n\u3000累计伤害量，回复自身的生命值。\\n·持续对敌方全体造成物理伤害（小），\\n\u3000并降低其物理防御力3次。\\n·提升自身的物理攻击力的同时，\\n\u3000增加技能值。');
insert into unit_enemy_data values (308203, '剧毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】生长异常的碧的曼德拉草。\\n会用强韧的根茎与坚硬的种子子弹压制眼前的敌人。\\n\\n·根据最大生命值，对前方两名角色造成一定比例\\n\u3000的物理伤害。\\n\\n·连结爆发时，对敌方全体造成物理伤害（大），\\n\u3000并根据造成的\\n\u3000累计伤害量，回复自身的生命值。\\n·持续对敌方全体造成物理伤害（小），\\n\u3000并降低其物理防御力3次。\\n·提升自身的物理攻击力的同时，\\n\u3000增加技能值。\\n·曼德拉草的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (308204, '剧毒曼德拉草', 308204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】释放剧毒孢子，将大地\\n化为荒野。\\n以无数的尸骸为温床，遍地绽放\\n剧毒的鲜花。\\n\\n按顺序重复以下行动。\\n·埋入地面期间，获得不会受到\\n\u3000伤害的状态。\\n·对敌方全体赋予强大的毒状态。\\n·解除自身的属性值降低效果后，\\n\u3000极大幅度降低自身的物理防御力\\n\u3000与魔法防御力。');
insert into unit_enemy_data values (308205, '剧毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】带有剧毒的粘液从触手上滴落而下。\\n仅仅一滴便能让泉水干涸，令森林腐化，威胁生命。\\n\\n·对前方两名角色造成物理伤害，\\n\u3000并使其陷入毒状态。\\n\\n·连结爆发时，对敌方全体造成物理伤害（大），\\n\u3000并根据造成的\\n\u3000累计伤害量，回复自身的生命值。\\n\u3000大幅提升处于毒状态中的敌人的伤害量。\\n·持续对敌方全体造成物理伤害（小），\\n\u3000并降低其物理防御力3次。\\n·提升自身的物理攻击力的同时，\\n\u3000增加技能值。');
insert into unit_enemy_data values (308206, '剧毒曼德拉草', 308200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】绝命的咆哮，巨腕的蹂躏。\\n如花朵般凋零的生命。为自己即将迎来的终结献上摇篮曲，\\n亡骸就此永眠。\\n\\n·根据最大生命值，对前方两名角色造成一定比例的\\n\u3000物理伤害。\\n\\n·连结爆发时，对敌方全体造成物理伤害（大），\\n\u3000并根据造成的\\n\u3000累计伤害量，回复自身的生命值。\\n·持续对敌方全体造成物理伤害（小），\\n\u3000并降低其物理防御力3次。\\n·提升自身的物理攻击力的同时，\\n\u3000增加技能值。\\n·当剩余时间少于40秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (308207, '霸王花曼德拉草', 308207, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】因塔的魔力而开花，会释放剧毒的死亡大花。\\n这种毒会造成一切感觉狂乱，腐蚀所有生命。\\n\\n·对前方两名角色造成物理伤害。\\n\u3000若自身的生命值低于80%，\\n\u3000则使其陷入中毒状态。\\n·必杀技能会对敌方全体造成物理伤害（大），\\n\u3000对陷入中毒状态的角色造成比例伤害。\\n\u3000随后大幅降低敌方全体的物理攻击力，\\n\u3000并大幅提升自身的物理攻击力。\\n·对敌方全体造成物理伤害（中）\\n\u3000，并降低其物理防御力。\\n·提升自身的物理攻击力和技能值。\\n·剩余生命值低于80%时，会展开\\n\u3000更为激烈凶暴的攻击。\\n·埋入地面期间，转变为不会受到伤害的状态，\\n\u3000对敌方全体造成物理伤害（中），\\n\u3000并使其陷入长时间的中毒状态。同时，让处于中毒状态的\\n\u3000角色陷入猛毒状态。\\n\u3000随后，钻出地面时会极大幅度\\n\u3000降低敌人的物理防御力和魔法防御力。');
insert into unit_enemy_data values (308300, '岛鲸', 308300, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】吞噬了大量探险家的幻岛巨鲸。冷酷无情地送葬深陷欲望与幻想的愚者。\\n\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理伤害（大）。\\n·对敌方全体造成魔法伤害（中），并极大幅度提升\\n\u3000岛鲸自身的物理防御力和魔法防御力。\\n·对前方三名角色造成魔法伤害（大），\\n\u3000并将其大幅度击飞。\\n·岛鲸自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
