insert into unit_enemy_data values (308403, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】被束缚在对自己孩子的执念中的亡父之灵。\\n为了享受与女儿共度的虚假时光，\\n可以毫不留情地屠戮阻挡在自己面前的敌人。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000并降低其魔法防御力。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n\u3000同时根据角色的最大生命值，\\n\u3000对前方三名角色造成一定比例的魔法伤害，\\n\u3000并大幅度将其击飞。\\n\\n·对距离最远的一名敌人造成物理伤害（中），\\n\u3000并将其拉至距离最近的角色的位置，\\n\u3000同时使其眩晕。\\n·幻影男爵的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (308404, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】召唤【小小甜心】的\\n暗影。\\n天真少女们引领而至的终点，是亡者涌动的永夜国度。\\n\\n·对前方一名敌人造成魔法伤害。');
insert into unit_enemy_data values (308405, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】冥府之门悄然敞开，\\n生者之魂堕入深渊。再也无法回来的灵魂，\\n只能以思念为印记，游荡在黑暗之中。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000并降低其魔法防御力。\\n\\n·对距离最远的一名敌人造成物理伤害（中），\\n\u3000并将其拉至距离最近的角色的位置，\\n\u3000同时使其眩晕。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000中幅度降低其技能值的同时\\n\u3000中幅度回复幻影男爵的技能值。\\n\u3000并且解除使用物理攻击的前三名角色的\\n\u3000属性值提升效果，\\n\u3000对使用魔法攻击的前三名角色造成\\n\u3000追加的魔法伤害（大）。');
insert into unit_enemy_data values (308406, '幻影男爵', 308400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】与挚爱离别的时刻正在逼近。\\n为了反抗悲哀的宿命，\\n怨灵发出了临死前的恸哭。\\n\\n·连结爆发时，根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000并降低其魔法防御力。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n\u3000同时根据角色的最大生命值，\\n\u3000对前方三名角色造成一定比例的魔法伤害，\\n\u3000并大幅度将其击飞。\\n\\n·对距离最远的一名敌人造成物理伤害（中），\\n\u3000并将其拉至距离最近的角色的位置，\\n\u3000同时使其眩晕。\\n·战斗开始后，提升自身的魔法攻击力\\n\u3000和行动速度。');
insert into unit_enemy_data values (308408, '狂乱幻影', 308408, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】为塔的魔力聚集而来的怨灵变化而成的亡灵。\\n会伸出邪恶的手呼唤生者，将其拽入冥府。\\n\\n·对敌方全体造成魔法伤害（大），\\n\u3000并降低其魔法防御力。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n\u3000同时对前方三名角色造成魔法伤害（中），\\n\u3000并将其大幅度击飞。\\n·对距离最远的一名敌人造成物理伤害（中），\\n\u3000并将其拉至距离最近的角色的位置，\\n\u3000同时使其眩晕。');
insert into unit_enemy_data values (308500, '水瑚麟', 308500, 0, 16, 270, 1000, 1, 2.8, 0, 0, 0, '【物理】被月神魔力所魅惑，变得狂暴的水之神兽。\\n忘记了自己的使命，化为吞噬万物的狂兽。\\n\\n·对敌方全体造成魔法伤害（大），并赋予畏惧状态。\\n\u3000陷入畏惧状态的角色，在一段时间内\\n\u3000必杀技能的攻击力与回复生命值效果都将被减半。\\n·对前方范围内的敌人造成魔法伤害（中），\\n\u3000并根据击中次数，小幅提升自身的\\n\u3000物理防御力。同时，展开一个能造成\\n\u3000魔法伤害的领域。\\n·对前方一名角色造成魔法伤害（大），\\n\u3000并消耗自身的生命值，对敌方全体造成追加的\\n\u3000魔法伤害（中）。\\n\u3000同时，小幅提升自身的物理攻击力与\\n\u3000魔法攻击力。');
