insert into unit_enemy_data values (308601, '类龙生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】如龙？一样展翅飞舞在空中，\\n如龙？一样以灼热的吐息点燃敌人。\\n\\n·在战斗开始时，获得70个“火焰果”。\\n·连结爆发时，根据类龙生物\\n\u3000保有的“火焰果”个数，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n\u3000随后减少60个“火焰果”。\\n\\n·根据角色的最大生命值，对前方三名角色造成一定比例的\\n\u3000物理伤害，并使其眩晕。\\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\\n\u3000在类龙生物吃东西时，\\n\u3000最多可持续获得60个“火焰果”。\\n\u3000如果在吃东西的时候，\\n\u3000类龙生物受到了7500以上的伤害，该技能中断，\\n\u3000并减少30个“火焰果”。');
insert into unit_enemy_data values (308602, '类龙生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】如龙？一样展翅飞舞在空中，\\n如龙？一样以灼热的吐息点燃敌人。\\n\\n·在战斗开始时，获得70个“火焰果”。\\n·连结爆发时，根据类龙生物\\n\u3000保有的“火焰果”个数，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n\u3000随后减少60个“火焰果”。\\n\\n·根据角色的最大生命值，对前方三名角色造成一定比例的\\n\u3000物理伤害，并使其眩晕。\\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\\n\u3000在类龙生物吃东西时，\\n\u3000最多可持续获得60个“火焰果”。\\n\u3000如果在吃东西的时候，\\n\u3000类龙生物受到了20000以上的伤害，该技能中断，\\n\u3000并减少30个“火焰果”。');
insert into unit_enemy_data values (308603, '类龙生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】如龙？一样展翅飞舞在空中，\\n如龙？一样以灼热的吐息点燃敌人。\\n\\n·在战斗开始时，获得70个“火焰果”。\\n·连结爆发时，根据类龙生物\\n\u3000保有的“火焰果”个数，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n\u3000随后减少60个“火焰果”。\\n\\n·根据角色的最大生命值，对前方三名角色造成一定比例的\\n\u3000物理伤害，并使其眩晕。\\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\\n\u3000在类龙生物吃东西时，\\n\u3000最多可持续获得60个“火焰果”。\\n\u3000如果在吃东西的时候，\\n\u3000类龙生物受到了160000以上的伤害，该技能中断，\\n\u3000并减少30个“火焰果”。\\n·类龙生物的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (308604, '类龙生物', 308600, 0, 16, 270, 2160, 1, 1.8, 0, 0, 0, '【模式1】自苍天降下的咆哮撕裂了天空，\\n伪龙在阳光下展开自傲的双翼，降临于此。\\n\\n·类龙生物的生命值低于80%时，\\n\u3000会进入下一模式。\\n\\n·在战斗开始时，获得70个“火焰果”。\\n·连结爆发时，根据类龙生物\\n\u3000保有的“火焰果”个数，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n\u3000随后减少60个“火焰果”。\\n\\n·根据角色的最大生命值，对前方三名角色造成一定比例的\\n\u3000物理伤害，并使其眩晕。\\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\\n\u3000在类龙生物吃东西时，\\n\u3000最多可持续获得60个“火焰果”。\\n\u3000如果在吃东西的时候，\\n\u3000类龙生物受到了150000以上的伤害，该技能中断，\\n\u3000并减少30个“火焰果”。\\n·召唤4只怪物，并小幅度击飞\\n\u3000敌方全体。');
insert into unit_enemy_data values (308605, '类龙生物', 308600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【模式2】在伪龙的呼唤下，森林里的\\n魔物集结于此，拼尽全力地保护它。\\n\\n·打倒魔像、树精、\\n\u3000毒食花后，进入下一模式。\\n\\n·类龙生物对眼前的一名敌人进行\\n\u3000物理攻击。');
insert into unit_enemy_data values (308606, '类龙生物', 308606, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【模式3】对龙的憧憬使蜥蜴觉醒了。空中霸者的吐息将化为焚毁这座小岛的\\n劫火。\\n\\n·在战斗开始时，获得70个“火焰果”。\\n·连结爆发时，根据类龙生物\\n\u3000保有的“火焰果”个数，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n\u3000随后减少60个“火焰果”。\\n\\n·根据角色的最大生命值，对前方三名角色造成一定比例的\\n\u3000物理伤害，并使其眩晕。\\n·一定时间内，物理防御力和魔法防御力会大幅度降低，\\n\u3000在类龙生物吃东西时，\\n\u3000最多可持续获得60个“火焰果”。\\n\u3000如果在吃东西的时候，\\n\u3000类龙生物受到了250000以上的伤害，该技能中断，\\n\u3000并减少30个“火焰果”。\\n·战斗开始后，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (309000, '陆生树懒', 309000, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】贪睡在梦境与现实之间的树懒。\\n被打扰清梦时会大发雷霆，用超能力大肆破坏。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·陆生树懒的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并中幅降低\\n\u3000其物理攻击力和魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。');
insert into unit_enemy_data values (309001, '陆生树懒', 309001, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】贪睡在梦境与现实之间的树懒。\\n被打扰清梦时会大发雷霆，用超能力大肆破坏。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·陆生树懒的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并中幅降低\\n\u3000其物理攻击力和魔法攻击力，\\n\u3000使其陷入眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（小），\\n\u3000并赋予眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (309002, '慵懒的马马尔', 309002, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】栖息在广阔密林的巨大\\n野兽。遇到生命危险会激发其\\n自卫反应，释放出强大的野性。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并大幅降低\\n\u3000其物理攻击力和魔法攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\\n·对敌方全体造成魔法伤害（小），\\n\u3000并使其陷入眩晕状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (309100, '鳄龙', 309100, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】栖息于大河中的巨鳄魔物。\\n看似呆笨，却会用那柄奇异的长枪溶解大地，封锁冒险者的行动。\\n\\n·对敌方全体造成物理伤害（大），并降低其物理防御力与行动速度。\\n\\n·以前方第三名敌人为中心，造成小范围的物理伤害（小），并赋予束缚状态。');
insert into unit_enemy_data values (309101, '鳄龙', 309101, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】栖息于大河中的巨鳄魔物。外表呆呆的，\\n能用神奇的枪溶解大地，封住敌人的行动。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·鳄龙的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会无视挑衅，以前方第三名敌人为中心，\\n\u3000对小范围内的敌人\\n\u3000造成物理伤害（大）。同时大幅降低敌方全体的行动速度，\\n\u3000根据该技能所伤敌人的数量\\n\u3000降低其技能值。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·无视挑衅，以前方第三名敌人为中心，\\n\u3000对小范围内的敌人\\n\u3000造成物理伤害（小），并赋予其束缚状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (309102, '鳄龙', 309102, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】栖息于大河中的巨鳄魔物。外表呆呆的，\\n能用神奇的枪溶解大地，封住敌人的行动。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·鳄龙的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会无视挑衅，以前方第三名敌人为中心，\\n\u3000对小范围内的敌人\\n\u3000造成物理伤害（大）。同时大幅降低敌方全体的行动速度，\\n\u3000根据该技能所伤敌人的数量\\n\u3000降低其技能值。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·无视挑衅，以前方第三名敌人为中心，\\n\u3000对小范围内的敌人\\n\u3000造成物理伤害（小），并赋予其束缚状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (309200, '天空女武神', 309200, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】飞舞于战场上空的女武神。\\n会使出让人应接不暇的连射，阻止敌人的前进。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·天空女武神的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对前方范围内最多四名角色\\n\u3000造成物理伤害（大），中幅降低其技能值，\\n\u3000并大幅度降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n对前方三名角色造成物理伤害（中），\\n\u3000并大幅提升自身的行动速度。');
insert into unit_enemy_data values (309201, '天空女武神', 309201, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】飞舞于战场上空的女武神。\\n会使出让人应接不暇的连射，阻止敌人的前进。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·天空女武神的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对前方范围内最多四名角色\\n\u3000造成物理伤害（大），中幅降低其技能值，\\n\u3000并大幅度降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方四名角色造成物理伤害（中），\\n\u3000并大幅降低其物理攻击力和魔法攻击力。\\n\u3000同时大幅提升自身的行动速度。');
