insert into unit_enemy_data values (309301, '复仇驯鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】下定决心背叛主人的精英驯鹿。\\n对贪得无厌的圣诞老人们降下随从真心实意的制裁。\\n\\n·必杀技能会在召唤角色被消灭后，\\n\u3000根据角色的最大生命值\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000承受该伤害后，生命值低于40%的角色\\n\u3000在一段时间内变成驯鹿，\\n\u3000且物理暴击率\\n\u3000和魔法暴击率降低。变成驯鹿后的角色，\\n\u3000只能使用普通攻击。\\n\u3000一段时间内，自身的物理防御力大幅降低。\\n·以最前方的角色为中心，根据角色的最大生命值\\n\u3000对一定范围内的角色\\n\u3000造成物理比例伤害。并提升自己的\\n\u3000物理攻击力。\\n·对最前方的角色造成物理伤害，\\n\u3000将其击退的同时使其眩晕。');
insert into unit_enemy_data values (309302, '复仇驯鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】下定决心背叛主人的精英驯鹿。\\n对贪得无厌的圣诞老人们降下随从真心实意的制裁。\\n\\n·必杀技能会在召唤角色被消灭后，\\n\u3000根据角色的最大生命值\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000承受该伤害后，生命值低于40%的角色\\n\u3000在一段时间内变成驯鹿，\\n\u3000且物理暴击率\\n\u3000和魔法暴击率降低。变成驯鹿后的角色，\\n\u3000只能使用普通攻击。\\n\u3000一段时间内，自身的物理防御力大幅降低。\\n·以最前方的角色为中心，根据角色的最大生命值\\n\u3000对一定范围内的角色\\n\u3000造成物理比例伤害。并提升自己的\\n\u3000物理攻击力。\\n·对最前方的角色造成物理伤害，\\n\u3000将其击退的同时使其眩晕。');
insert into unit_enemy_data values (309303, '复仇驯鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】下定决心背叛主人的精英驯鹿。\\n对贪得无厌的圣诞老人们降下随从真心实意的制裁。\\n\\n·必杀技能会在召唤角色被消灭后，\\n\u3000根据角色的最大生命值\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000承受该伤害后，生命值低于40%的角色\\n\u3000在一段时间内变成驯鹿，\\n\u3000且物理暴击率\\n\u3000和魔法暴击率降低。变成驯鹿后的角色，\\n\u3000只能使用普通攻击。\\n\u3000一段时间内，自身的物理防御力大幅降低。\\n·以最前方的角色为中心，根据角色的最大生命值\\n\u3000对一定范围内的角色\\n\u3000造成物理比例伤害。并提升自己的\\n\u3000物理攻击力。\\n·对最前方的角色造成物理伤害，\\n\u3000将其击退的同时使其眩晕。\\n·复仇驯鹿的生命值低于50%时，\\n\u3000 会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (309304, '复仇驯鹿', 309300, 0, 16, 270, 3000, 1, 4.2, 0, 0, 0, '【模式1】召唤克莉丝提娜、望、\\n伊莉亚的暗影。三人扭曲的欲望与多管闲事的心，\\n将化为实体，令圣夜陷入混沌。\\n\\n·对最前方的角色造成物理伤害。');
insert into unit_enemy_data values (309305, '复仇驯鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式2】绝望如雪花般洒落在\\n圣夜的街道上。积蓄已久的憎恶将溢出\\n充满欲望的礼物袋。\\n\\n·必杀技能会在召唤角色被消灭后，\\n\u3000根据角色的最大生命值\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000承受该伤害后，生命值低于40%的角色\\n\u3000在一段时间内变成驯鹿，\\n\u3000且物理暴击率\\n\u3000和魔法暴击率降低。变成驯鹿后的角色，\\n\u3000只能使用普通攻击。\\n\u3000一段时间内，自身的物理防御力大幅降低。\\n·以最前方的角色为中心，根据角色的最大生命值\\n\u3000对一定范围内的角色\\n\u3000造成物理比例伤害。并提升自己的\\n\u3000物理攻击力。\\n·在前方展开一个持续一段时间的领域。\\n\u3000在这个领域内的角色，\\n\u3000物理攻击力、物理暴击率、\\n\u3000技能值上升量会大幅降低。\\n·对最前方的角色造成物理伤害，\\n\u3000将其击退的同时使其眩晕。');
insert into unit_enemy_data values (309306, '复仇驯鹿', 309300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式3】负伤的巨兽点燃了\\n反击的狼烟。完全陷于憎恶与愤怒中，将变得\\n残忍无比。\\n\\n·必杀技能会在召唤角色被消灭后，\\n\u3000根据角色的最大生命值\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000承受该伤害后，生命值低于35%的角色\\n\u3000在一定时间内变成驯鹿，\\n\u3000且物理暴击率\\n\u3000和魔法暴击率降低。变成驯鹿后的角色，\\n\u3000只能使用普通攻击。\\n\u3000一段时间内，自身的物理防御力大幅降低。\\n·以最前方的角色为中心，根据角色的最大生命值\\n\u3000对一定范围内的角色\\n\u3000造成物理比例伤害。并提升自己的\\n\u3000物理攻击力。\\n·对最前方的角色造成物理伤害，\\n\u3000将其击退的同时使其眩晕。\\n·战斗开始后，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (309400, '侍从·刀刃（物理）', 309400, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】霸瞳皇帝创造的移动力量储藏库。\\n掌管力量的权能，其巨大的剑戟可斩破一切。\\n\\n·眩晕与击飞有效。\\n\\n·必杀技能会对敌方全体造成与角色\\n\u3000最大生命值相应的物理百分比伤害。\\n\\n·以眼前一名敌人为中心，对范围内的所有敌人\\n\u3000造成与角色的最大生命值相应的\\n\u3000物理百分比伤害。\\n\\n·当侍从·刀刃（物理）的生命值\\n\u3000低于40%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (309500, '侍从·魔能（魔法）', 309500, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】霸瞳皇帝创造的移动力量储藏库。\\n掌管魔法的权能，其无穷的魔力可毁灭一切。\\n\\n·必杀技能会对敌方全体造成与角色\\n\u3000最大生命值相应的魔法百分比伤害。\\n\u3000同时，小幅降低其物理攻击力与魔法攻击力。\\n\\n·对随机一名敌人造成魔法伤害，\\n\u3000并赋予诅咒、麻痹状态，大幅降低其技能值。\\n\\n·当侍从·魔能（魔法）的生命值\\n\u3000低于40%时，会变得更加凶暴，\\n\u3000攻击更为猛烈。');
insert into unit_enemy_data values (309600, '侍从·洛克斯（多部位）', 309600, 0, 16, 270, 1000, 1, 1.8, 0, 0, 0, '【物理】霸瞳皇帝创造的移动力量储藏库。\\n掌管责任与义务的权能，可屠戮广阔领域中的一切。\\n\\n·必杀技能会以眼前一名敌人为中心，\\n\u3000对范围内的所有敌人造成与角色最大生命值\\n\u3000相应的物理百分比伤害，\\n\u3000并对眼前一名敌人造成物理伤害（小）。\\n\\n·以眼前一名敌人为中心，造成与\\n\u3000角色最大生命值相应的\\n物理百分比伤害。\\n\\n·当侍从·洛克斯（多部位）的生命值\\n\u3000低于40%时，会变得更加顽强，\\n\u3000进一步提升自身的防御力。');
insert into unit_enemy_data values (309700, '侍从·铁壁（防卫）', 309700, 0, 16, 270, 1000, 2, 1.8, 0, 0, 0, '【魔法】霸瞳皇帝创造的移动力量储藏库。\\n掌管守护的权能，其坚固的铁壁可防御一切。\\n\\n·必杀技能会大幅提升自身的\\n\u3000物理防御力与魔法防御力。\\n\\n·以眼前一名敌人为中心，对范围内的所有敌人\\n\u3000造成魔法伤害。同时，小幅提升自身的物理防御力\\n\u3000和魔法防御力。\\n\\n·当侍从·铁壁（防卫）的生命值\\n\u3000低于40%时，会变得更加顽强，\\n\u3000进一步提升自身的防御力。');
insert into unit_enemy_data values (309800, '霸瞳皇帝', 309800, 0, 16, 270, 1000, 1, 3.6, 309800, 309800, 0, '王都最终决战_基座单元');
insert into unit_enemy_data values (309801, '霸瞳皇帝', 309801, 0, 16, 270, 1000, 1, 2.4, 309800, 309800, 0, '【模式1】抗拒着一切的悲伤公主。\\n以神自居，沉浸于不详的力量之中。\\n并向整个阿斯特朗举起了狂乱的刀刃。\\n就用无数的剑刃，来穿破守护着自欺欺人的公主的铁壁吧。\\n\\n·展开屏障，完全防御敌人的攻击。\\n\u3000屏障的耐久与伤害量无关，\\n\u3000只要被攻击命中就会减少耐久度，\\n\u3000受到280次攻击后，屏障就会被\\n\u3000破坏。\\n\\n·必杀技能会根据敌方全体的剩余生命值\\n\u3000造成特大百分比伤害。\\n\\n·对敌方全体造成物理伤害（小）与\\n\u3000魔法伤害（小）。');
insert into unit_enemy_data values (309802, '霸瞳皇帝', 309800, 0, 16, 270, 1000, 1, 1.8, 309800, 309800, 0, '【模式2】对渺小之人的反抗表现出些许的急躁。\\n将以自己的大义去制裁那些心怀希望、阻挡自己的人。\\n举起那柄悲愤之剑，将其灵魂化为尘土。\\n以正义的魔法，去对抗伪神所挥出的\\n漆黑之刃吧。\\n\\n·必杀技能会根据敌方全体的剩余生命值\\n\u3000造成特大百分比伤害。\\n\\n·对眼前一名敌人造成物理伤害（特大），\\n\u3000并对其周围的所有敌人造成魔法伤害（中），\\n\u3000使其昏迷。昏迷的角色\\n\u3000在受到下次攻击前，都无法行动。\\n\\n·对敌方全体造成物理伤害（小）与\\n\u3000魔法伤害（小）。');
insert into unit_enemy_data values (309803, '霸瞳皇帝', 309800, 0, 16, 270, 1000, 1, 1.6, 309800, 309800, 0, '【模式3】创世神发出了临终的呼声。\\n那因怨恨而吐露的拒绝之语将化为诅咒，\\n折磨众人的身体、精神、灵魂，让一切回归黑暗。\\n此刻即为决战。各位，举起剑来，用自己的双手\\n开拓明天吧。\\n\\n·必杀技能会根据敌方全体的剩余生命值\\n\u3000造成特大百分比伤害。\\n\\n·对眼前一名敌人造成物理伤害（特大），\\n\u3000并对其周围的所有敌人造成魔法伤害（中），\\n\u3000使其昏迷。昏迷的角色\\n\u3000在受到下次攻击前，都无法行动。\\n\\n·对敌方全体造成物理伤害（小）与\\n\u3000魔法伤害（小）。\\n\\n·以最后一名敌人为中心，展开中幅降低\\n\u3000物理防御力与与魔法防御力的领域，\\n\u3000并造成魔法伤害（小），\\n\u3000同时赋予诅咒状态。诅咒所造成的持续伤害\\n\u3000会逐秒增加。\\n\\n·在战斗开始后，大幅提升自身的\\n\u3000行动速度，并小幅提升自身的\\n\u3000魔法防御力。');
insert into unit_enemy_data values (309804, '霸瞳皇帝', 309804, 0, 16, 270, 1000, 1, 1.8, 309800, 309800, 0, '显示地图用');
insert into unit_enemy_data values (309900, '专家哥布林', 309900, 0, 17, 270, 1100, 2, 1.8, 0, 0, 0, '【魔法】企图将塔的魔力占为己有的哥布林学者。\\n用禁术操纵泥塑人偶，强行掠夺敌人的自由。\\n\\n·对敌方全体造成魔法伤害（大），\\n\u3000让所有召唤体、分身即死。\\n·对前方大范围造成与生命值相应的\\n\u3000百分比物理伤害（中），并大幅击飞敌人。\\n\u3000同时，召唤1只污泥人偶。\\n\u3000污泥人偶会对前方第一名或第二名敌人\\n\u3000造成物理伤害，并降低其行动速度。\\n·以前方第二名敌人为中心，造成小范围的\\n\u3000物理伤害（中），并赋予猛毒、眩晕状态。');
insert into unit_enemy_data values (309907, '专家哥布林', 309907, 0, 16, 270, 2000, 2, 1.8, 0, 0, 0, '【魔法】王的左膀右臂，哥布林大魔导士。\\n施放禁忌的魔术，消灭一切反抗的敌人。\\n\\n·战斗开始时，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，使用伤害上限技能，\\n\u3000当一次行动对其施加的伤害超过一定数值，\\n\u3000伤害量将根据施加的伤害值而减少。\\n·专家哥布林的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·每次受到伤害时，增加1个\\n\u3000“小鬼的纹章”。“小鬼的纹章”为10时，\\n\u3000消耗10个“小鬼的纹章”炸掉魔法之壶，\\n\u3000对巨型哥布林和专家哥布林造成魔法伤害（特大），\\n\u3000并特大幅度降低其物理防御力和\\n\u3000魔法防御力。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并破坏所有的召唤体。\\n·根据角色的最大生命值，对前方一名角色\\n\u3000造成一定比例的魔法伤害。\\n·剩余生命值在2400万以下时，特大幅度提升\\n\u3000魔法攻击力和行动速度，变更攻击行动。\\n·以前方第二名角色为中心，\\n\u3000对一定范围内的角色造成魔法伤害（中），并使其陷入\\n\u3000麻痹状态和猛毒状态。');
insert into unit_enemy_data values (310000, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】传说拥有迅捷速度的山中土地神。\\n自太古时代便为了寻求劲敌，而每日奔波着。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其击退。\\n\u3000对最前方的角色追加物理伤害（特大），\\n\u3000并大幅击飞对方，\\n\u3000使其眩晕。\\n\u3000山神大人每使用一次必杀技能，\\n\u3000自身的物理防御力都会降低。\\n\u3000这个效果可以累积。\\n·对魔法攻击力最高的1名角色，\\n\u3000根据其最大生命值造成魔法比例伤害，\\n\u3000并大幅度降低其技能值。\\n·提升自身的技能值和行动速度。\\n·山神大人的生命值低于55%时，\\n\u3000会展开更为激烈凶暴的攻击，\\n\u3000但自身的物理防御力也会降低。');
insert into unit_enemy_data values (310001, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】传说拥有迅捷速度的山中土地神。\\n自太古时代便为了寻求劲敌，而每日奔波着。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其击退。\\n\u3000对最前方的角色追加物理伤害（特大），\\n\u3000并大幅击飞对方，\\n\u3000使其眩晕。\\n\u3000山神大人每使用一次必杀技能，\\n\u3000自身的物理防御力都会降低。\\n\u3000这个效果可以累积。\\n·提升自身的技能值和行动速度。');
