insert into unit_enemy_data values (310004, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】试探冒险者的实力，召唤内在的\\n影子。寒风凛冽，天下险峻，自身的脆弱\\n将受到考验。\\n\\n·对最前方的角色造成物理伤害。');
insert into unit_enemy_data values (310005, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】男子汉的疾驰可震撼大地。\\n仿佛是为了庆祝这幅盛景，目出鲷也飞舞于高山之上。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其击退。\\n\u3000对最前方的角色追加物理伤害（特大），\\n\u3000并大幅击飞对方，\\n\u3000使其眩晕。\\n\u3000山神大人每使用一次必杀技能，\\n\u3000自身的物理防御力都会降低。\\n\u3000这个效果可以累积。\\n·对魔法攻击力最高的2名角色，\\n\u3000根据其最大生命值造成魔法比例伤害，\\n\u3000并大幅度降低技能值。\\n·在后方展开一个降低物理防御力的领域，\\n\u3000并对敌方全体造成物理伤害（中）。\\n\u3000目出鲷离开后背期间，特大幅度降低\\n\u3000山神大人的魔法防御力。');
insert into unit_enemy_data values (310006, '山神大人', 310000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】给予向神发起竞争的强者最后的试炼。\\n拼上彼此的一切，奔向思念之人。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其击退。\\n\u3000对最前方的角色追加物理伤害（特大），\\n\u3000并大幅击飞对方，\\n\u3000使其眩晕。\\n\u3000山神大人每使用一次必杀技能，\\n\u3000自身的物理防御力都会降低。\\n\u3000这个效果可以累积。\\n·对魔法攻击力最高的1名角色，\\n\u3000根据其最大生命值造成魔法比例伤害，\\n\u3000并大幅度降低其技能值。\\n·提升自身的技能值和行动速度。\\n·战斗开始后，会展开更为激烈凶暴的攻击，\\n\u3000同时会降低自身的\\n\u3000物理防御力。');
insert into unit_enemy_data values (310100, '愤怒巨龙', 310100, 0, 14, 270, 1000, 1, 2.6, 0, 0, 0, '【物理】栖息在忘却的秘境里的年轻巨龙。\\n用灼热的业火烧光闯入禁忌的冒险者。\\n\\n·必杀技能会对敌方全体造成\\n\u3000物理比例伤害，并降低其技能值。\\n·战斗开始时，特大幅度降低自身的技能值。\\n·以前方的1名角色为中心，对一定范围内的角色\\n\u3000造成魔法伤害，\\n\u3000并降低其物理防御力。\\n·对敌方全体造成物理伤害，\\n\u3000并使其陷入烧伤状态。\\n·对敌方全体造成物理伤害，击退对方\\n\u3000并解除属性值提升效果。\\n\\n·所有目标被击破时，特大幅度降低\\n\u3000自身技能值。\\n·愤怒巨龙自身剩余的生命值低于10%时，\\n\u3000会展开更为激烈凶暴的攻击，\\n\u3000且无法被击破。\\n·若战斗超时，会使敌方全体陷入\\n\u3000无法战斗状态。\\n');
insert into unit_enemy_data values (310101, '愤怒巨龙', 310101, 0, 14, 270, 1000, 1, 2.6, 0, 0, 0, '【物理】栖息在忘却的秘境里的年轻巨龙。\\n用灼热的业火烧光闯入禁忌的冒险者。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·愤怒巨龙的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000同时小幅降低其技能值。\\n·以最近的敌人为中心，根据角色的最大生命值对范围内的所有敌人造成一定比例的物理伤害，\\n\u3000并大幅降低其物理防御力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成物理伤害（中），\\n\u3000并使其陷入烧伤状态。\\n\u3000该技能所造成的烧伤伤害\\n\u3000不会回复技能值。\\n·解除敌方全体的屏障，\\n\u3000对敌方全体造成物理伤害（中），并将其击退，\\n\u3000同时消除其属性值提升效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·玩家左侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。\\n·中央的目标处于击破状态时，\\n\u3000大幅降低自身的物理防御力和魔法防御力。\\n·右侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力大幅降低。\\n·所有目标被击破时，特大幅度降低\\n\u3000自身技能值。\\n·愤怒巨龙自身剩余的生命值低于30%时，\\n\u3000会展开更为激烈凶暴的攻击，\\n\u3000虽无法被击破，却会大幅降低自身的物理防御力和魔法防御力。\\n·对距离最近的一名敌人造成物理伤害（大）。');
insert into unit_enemy_data values (310200, '硬头鲨', 310200, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】作为海蚀洞的支配者君临于此的鲨鱼魔物。\\n将凶恶的欲望倾注到捕食中的海中大恶徒。\\n\\n·对敌方全体造成物理伤害（特大），并降低其物理防御力与魔法攻击力。\\n\\n·对前方范围内的所有敌人造成魔法伤害（小），并降低其技能值与技能值上升量。');
insert into unit_enemy_data values (310201, '贪婪鲨', 310201, 0, 16, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】吸收塔中的魔力，暴饮暴食的巨大鲨鱼。\\n用声波扰乱感官，把迟钝的猎物吃个精光。\\n\\n·对敌方全体造成物理伤害（特大），降低其物理防御力\\n\u3000和魔法攻击力。\\n·对技能值最高的1名敌人造成魔法伤害（中），\\n\u3000使其麻痹。\\n\u3000并且使其陷入挑衅状态。\\n·无视挑衅，对生命值最高的1名敌人\\n\u3000造成每秒都会增加的持续伤害。');
