insert into unit_enemy_data values (310304, '反物质兽', 310304, 0, 16, 270, 3000, 1, 4.2, 0, 0, 0, '【模式1】被黑暗的力量所污染，\\n以爱与欲望构成的负面魔法生物。开始残酷地\\n扭曲整个世界。\\n\\n·必杀技能会从异次元取出道具\\n\u3000发动攻击。根据道具的不同\\n\u3000效果也会有所不同。\\n\u3000若使用锤子进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并大幅降低其物理攻击力。\\n\u3000若使用铜钹进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并大幅度降低其魔法攻击力。\\n\u3000若使用炸弹进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并使其陷入诅咒状态。\\n·以最前方的敌人为中心，\\n\u3000对一定范围内的敌人造成物理伤害（中），\\n\u3000并将其击退。\\n·对一定范围内的所有敌人\\n\u3000造成物理伤害（大）。');
insert into unit_enemy_data values (310305, '反物质兽', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式2】召唤魔法少女的暗影。\\n扭曲而负面的绝望阴影，将考验魔法少女们\\n爱与希望的力量。\\n\\n·默默地守望暗影的战斗。');
insert into unit_enemy_data values (310306, '反物质兽', 310300, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式3】愤怒至极的负面魔法生物\\n终于解放了真正的力量。势要彻底摧毁魔法少女的心，\\n以扭曲的爱之力令一切屈服。\\n\\n·必杀技能会从异次元取出道具\\n\u3000发动攻击。根据道具的不同\\n\u3000效果也会有所不同。\\n\u3000若使用锤子进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并大幅降低其物理攻击力。\\n\u3000若使用铜钹进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并大幅度降低其魔法攻击力。\\n\u3000若使用炸弹进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并使其陷入诅咒状态。\\n·以最前方的敌人为中心，\\n\u3000对一定范围内的敌人造成物理伤害（中），\\n\u3000并将其击退。\\n·对一定范围内的所有敌人\\n\u3000造成物理伤害（大）。\\n·战斗开始后，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (310307, '圣洁魔术师', 310307, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】受到塔的魔力引导的无邪魔术师。\\n其展示的魔术并非是为了取乐，而是凶恶的兵器。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·圣洁魔术师的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会从异次元取出道具\\n\u3000发动攻击。根据道具的不同\\n\u3000效果也会有所不同。\\n\u3000若使用锤子进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并大幅降低其物理攻击力。\\n\u3000若使用铜钹进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并大幅降低其魔法攻击力。\\n\u3000若使用炸弹进行攻击，则根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并使其陷入眩晕状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最前方的敌人为中心，\\n\u3000对一定范围内的敌人造成物理伤害（中），\\n\u3000并将其击退。\\n·对一定范围内的所有敌人\\n\u3000造成物理伤害（中）。\\n·圣洁魔术师自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (310400, '变色龙', 310400, 0, 11, 270, 1000, 1, 2.4, 0, 0, 0, '【物理】潜藏于沼泽林，暗暗瞄准猎物的\\n 变色龙魔物。性格胆小而狡猾，\\n但被惹怒时也会发动冲动的突击。\\n\\n必杀技能会对敌方全体造成物理伤害（大），\\n\u3000并大幅度降低\\n\u3000其物理防御力。\\n\\n·随机对1名角色\\n\u3000造成物理伤害（中）。\\n\\n·变色龙在一定时间内根据背景做出拟态，\\n\u3000极大幅度提升回避率，特大幅度提升行动速度，\\n\u3000大幅降低物理防御力。\\n\\n·对前方3名角色造成\\n\u3000物理伤害（中）。');
