insert into unit_enemy_data values (310502, '喧闹巨兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】拥有时尚体色的巨兔魔物。\\n与外表相反，令人厌烦至极的歌声\\n会震动大地，挫伤冒险者的心。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其击退。随后，\\n\u3000在恢复之前，大幅降低自身的物理防御力\\n\u3000和魔法防御力。\\n·以最前方的1名角色为中心，对一定范围内的角色\\n\u3000造成物理伤害（中），\\n\u3000并降低其行动速度。\\n·对前方3名角色造成物理伤害（小），\\n\u3000并大幅降低其物理防御力。');
insert into unit_enemy_data values (310503, '喧闹巨兔', 310500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】拥有时尚体色的巨兔魔物。\\n与外表相反，令人厌烦至极的歌声\\n会震动大地，挫伤冒险者的心。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其击退。随后，\\n\u3000在恢复之前，大幅降低自身的物理防御力\\n\u3000和魔法防御力。\\n·以最前方的1名角色为中心，对一定范围内的角色\\n\u3000造成物理伤害（中），\\n\u3000并降低其行动速度。\\n·对前方3名角色造成物理伤害（小），\\n\u3000并大幅降低其物理防御力。\\n·喧闹巨兔自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
