insert into unit_enemy_data values (310601, '纠葛的盆栽宝宝', 310600, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【魔法】不知是魔物还是精灵，\\n拥有巨型身躯的谜团重重的生物。带着纠结的表情喷着迷之花粉，\\n将城市引向一片混乱。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并降低其物理防御力和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害，\\n\u3000并将其击退。\\n·左右的叶子被击破时，会在一定时间内\\n\u3000消失。\\n·击破左侧的叶子时，小幅降低其物理防御力\\n\u3000和魔法防御力。\\n·击破本体时，大幅降低其物理防御力\\n\u3000和魔法防御力。\\n·击破右侧的叶子时，小幅降低其物理防御力\\n\u3000和魔法防御力。');
insert into unit_enemy_data values (310602, '纠葛的盆栽宝宝', 310600, 0, 16, 270, 3000, 1, 1.6, 310600, 310600, 0, '【魔法】不知是魔物还是精灵，\\n拥有巨型身躯的谜团重重的生物。带着纠结的表情喷着迷之花粉，\\n将城市引向一片混乱。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并降低其物理防御力和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害，\\n\u3000并将其击退。\\n·召唤球根酱。\\n\u3000球根酱不会成为攻击对象，\\n\u3000在一段时间后会自爆，对前方范围内的角色\\n\u3000造成魔法伤害。\\n·左右的叶子被击破时，会在一定时间内\\n\u3000消失。\\n·击破左侧的叶子时，小幅降低其物理防御力\\n\u3000和魔法防御力。\\n·击破本体时，大幅降低其物理防御力\\n\u3000和魔法防御力。\\n·击破右侧的叶子时，小幅降低其物理防御力\\n\u3000和魔法防御力。');
