insert into unit_enemy_data values (310701, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【物理】从破格的创意和扭曲的热情中诞生的\\n钢铁巨人。用异常的理论\\n重建基础者的特性，破坏敌人的语言中枢和精神。\\n\\n·自身受到毒、猛毒、诅咒、烧伤效果后，\\n\u3000会大幅降低自身的物理防御力、魔法防御力，\\n\u3000和行动速度。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n·提升自己的物理攻击力后，\\n\u3000对敌方全体造成物理伤害。\\n·根据最大生命值，对前方3名角色造成魔法比例伤害，\\n\u3000并降低其行动速度。');
insert into unit_enemy_data values (310702, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【物理】从破格的创意和扭曲的热情中诞生的\\n钢铁巨人。用异常的理论\\n重建基础者的特性，破坏敌人的语言中枢和精神。\\n\\n·自身受到毒、猛毒、诅咒、烧伤效果后，\\n\u3000会大幅降低自身的物理防御力、魔法防御力，\\n\u3000和行动速度。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n·提升自己的物理攻击力后，\\n\u3000对敌方全体造成物理伤害。\\n·根据最大生命值，对前方3名角色造成魔法比例伤害，\\n\u3000并降低其行动速度。');
insert into unit_enemy_data values (310703, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【物理】从破格的创意和扭曲的热情中诞生的\\n钢铁巨人。用异常的理论\\n重建基础者的特性，破坏敌人的语言中枢和精神。\\n\\n·自身受到毒、猛毒、诅咒、烧伤效果后，\\n\u3000会大幅降低自身的物理防御力、魔法防御力，\\n\u3000和行动速度。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n·提升自己的物理攻击力后，\\n\u3000对敌方全体造成物理伤害。\\n·根据最大生命值，对前方3名角色造成魔法比例伤害，\\n\u3000并降低其行动速度。\\n·好朋友X自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (310704, '好朋友X', 310700, 0, 16, 270, 3000, 1, 4.2, 0, 0, 0, '【模式1】召唤【墨丘利财团】的\\n暗影。就像在嘲笑自己的错误一样，\\n用影子的姿态愚弄大家。\\n\\n·自身受到毒、猛毒、诅咒、烧伤效果后，\\n\u3000会大幅降低自身的物理防御力、魔法防御力，\\n\u3000和行动速度。\\n·对眼前的1名敌人造成物理伤害。');
insert into unit_enemy_data values (310705, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【模式2】钢铁球体用完美的切噜语\\n低语。「切噜」。这些牢牢抓住人心的\\n洗脑话语，确确实实地引诱着人们进入切噜的世界。\\n\\n·随机对1名敌人造成魔法伤害（中），\\n\u3000并使其陷入沉默和诱惑状态。');
insert into unit_enemy_data values (310706, '好朋友X', 310700, 0, 16, 270, 3000, 1, 1.4, 0, 0, 0, '【模式3】钢铁的装置再次矗立在大地上。\\n解锁双刃剑·决胜模式，\\n将好朋友社的3人逼入绝境。\\n\\n·自身受到毒、猛毒、诅咒、烧伤效果后，\\n\u3000会大幅降低自身的物理防御力、魔法防御力，\\n\u3000和行动速度。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n·提升自己的物理攻击力后，\\n\u3000对敌方全体造成物理伤害。\\n·根据最大生命值，对前方3名角色造成魔法比例伤害，\\n\u3000并降低其行动速度。\\n·战斗开始后，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (310800, '忍者头领半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被恶毒监察官雇佣的忍者头领。\\n失去仁义的忍者，为金钱而堕落。然而，\\n熟练的身手依然健在，阻碍大家的前进。\\n\\n·战斗开始后，监察官登场。监察官受到\\n\u3000伤害时，会大幅降低\\n\u3000忍者头领半藏的物理防御力和\\n\u3000魔法防御力。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·变成大蛤蟆吐出毒液，使敌方全体进入\\n\u3000中毒状态。\\n\u3000此攻击必定命中。\\n·对敌方全体造成物理伤害（中）。');
insert into unit_enemy_data values (310801, '忍者头领半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被恶毒监察官雇佣的忍者头领。\\n失去仁义的忍者，为金钱而堕落。然而，\\n熟练的身手依然健在，阻碍大家的前进。\\n\\n·战斗开始后，监察官登场。监察官受到\\n\u3000伤害时，会大幅降低\\n\u3000忍者头领半藏的物理防御力和\\n\u3000魔法防御力。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·变成大蛤蟆吐出毒液，使敌方全体进入\\n\u3000中毒状态。此攻击必定命中。\\n·对敌方全体造成物理伤害（中）。');
insert into unit_enemy_data values (310802, '忍者头领半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被恶毒监察官雇佣的忍者头领。\\n失去仁义的忍者，为金钱而堕落。然而，\\n熟练的身手依然健在，阻碍大家的前进。\\n\\n·战斗开始后，监察官登场。监察官受到\\n\u3000伤害时，会大幅降低\\n\u3000忍者头领半藏的物理防御力和\\n\u3000魔法防御力。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·变成大蛤蟆吐出毒液，使敌方全体进入\\n\u3000中毒状态。此攻击必定命中。\\n·对敌方全体造成物理伤害（中）。');
insert into unit_enemy_data values (310803, '忍者头领半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】被恶毒监察官雇佣的忍者头领。\\n失去仁义的忍者，为金钱而堕落。然而，\\n熟练的身手依然健在，阻碍大家的前进。\\n\\n·战斗开始后，监察官登场。监察官受到\\n\u3000伤害后，会特大幅度降低\\n\u3000忍者头领半藏的物理防御力和\\n\u3000魔法防御力。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·变成大蛤蟆吐出毒液，使敌方全体进入\\n\u3000中毒状态。自身的生命值低于40%时，敌方全体\\n\u3000陷入猛毒状态。此攻击必定命中。\\n·对敌方全体造成物理伤害（中）。自身的生命值\\n\u3000低于40%时，使敌方全体\\n\u3000陷入眩晕状态。\\n·忍者头领半藏自身的生命值低于40%时，\\n\u3000技能的效果会发生变化。');
insert into unit_enemy_data values (310804, '忍者头领半藏', 310801, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【模式1】召唤【伊丽莎白牧场】的\\n暗影。用幻术制造出的四农士的幻影\\n再次使大家的归宿陷入困境。\\n\\n·对眼前的1名敌人造成物理伤害。');
insert into unit_enemy_data values (310805, '忍者头领半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【模式2】用秘传的通灵术变身成忍蛙，\\n向周围喷射蛤蟆的猛毒。这种猛毒\\n只要吸一口就会腐蚀全身，慢慢地使身体溃烂。\\n\\n·战斗开始后，监察官登场。监察官受到\\n\u3000伤害后，会特大幅度降低\\n\u3000忍者头领半藏的物理防御力和\\n\u3000魔法防御力。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·变成大蛤蟆吐出猛毒，使敌方全体陷入\\n\u3000猛毒状态。此攻击必定命中。\\n·对敌方全体造成物理伤害（中），\\n\u3000并使其陷入眩晕。');
insert into unit_enemy_data values (310806, '忍者头领半藏', 310800, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【模式3】最后，找回身为忍者的尊严，\\n发挥自己真正的力量。紫电一闪，\\n不等发出惨叫就化作了尸体。\\n\\n·战斗开始后，提升自身的技能威力。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·变成大蛤蟆吐出猛毒，使敌方全体陷入\\n\u3000猛毒状态。此攻击必定命中。\\n·对敌方全体造成物理伤害（中），\\n\u3000并使其陷入眩晕。');
insert into unit_enemy_data values (310900, '袭击兰德索尔各地的神秘飞碟', 310900, 0, 16, 270, 3000, 1, 0.1, 0, 0, 0, '【物理】突袭和平的丰收节的神秘飞行物。\\n突然出现在兰德索尔各地，进行破坏和掠夺。\\n\\n·必杀技能会对敌方全体造成物理伤害（大）。\\n·对前方1名角色造成物理伤害（小）。\\n·对剩余生命值最高的1名角色造成物理伤害（小）。\\n·随机对1名角色造成物理伤害（小）。');
insert into unit_enemy_data values (311000, '城堡树精', 311000, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】因塔的魔力发生变异而化为要塞的巨大魔树。\\n把大地的力量变成炮弹，以坚固的身躯\\n将一切破坏殆尽。\\n\\n·对敌方全体造成物理伤害（大）。\\n·对一定范围内的所有敌人造成持续的魔法伤害，\\n\u3000并展开可以降低物理防御力的领域。\\n·以后方第2名角色为中心，对其小范围内的角色\\n\u3000造成物理伤害（中），并降低其技能值。\\n\u3000之后，回复己方全体的技能值。');
insert into unit_enemy_data values (311001, '城堡树精', 311001, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】因塔的魔力发生变异而化为要塞的巨大魔树。\\n把大地的力量变成炮弹，以坚固的身躯\\n将一切破坏殆尽。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·城堡树精的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并对敌方全体造成物理伤害（大）。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·展开特大幅度降低后方范围内所有敌人\\n\u3000物理防御力的领域。\\n·以最远的一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害，并中幅降低其技能值。随后，\\n\u3000根据该技能所伤敌人的数量\\n\u3000回复自身的技能值。\\n·对前方一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (311002, '城堡树精', 311002, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】因塔的魔力发生变异而化为要塞的巨大魔树。\\n把大地的力量变成炮弹，以坚固的身躯\\n将一切破坏殆尽。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·城堡树精的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并对敌方全体造成物理伤害（大）。并赋予\\n\u3000眩晕状态。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·展开特大幅度降低后方范围内所有敌人\\n\u3000物理防御力的领域。\\n·以最远的一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害，并中幅降低其技能值。随后，\\n\u3000根据该技能所伤敌人的数量\\n\u3000回复自身的技能值。\\n·对前方一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (311100, '黑之王炸脖龙', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】从扭曲世界的狭界中显现的黑之王。\\n身上缠绕着黑暗的瘴气，企图蹂躏不可思议之国。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害，\\n\u3000使其陷入中毒状态并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方2名角色造成物理伤害（中），\\n\u3000并降低其物理防御力和技能值。');
insert into unit_enemy_data values (311101, '黑之王炸脖龙', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】从扭曲世界的狭界中显现的黑之王。\\n身上缠绕着黑暗的瘴气，企图蹂躏不可思议之国。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000使其陷入中毒状态并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方两名角色造成物理伤害（中），\\n\u3000并降低其物理防御力和技能值。');
insert into unit_enemy_data values (311102, '黑之王炸脖龙', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】从扭曲世界的狭界中显现的黑之王。\\n身上缠绕着黑暗的瘴气，企图蹂躏不可思议之国。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000使其陷入中毒状态并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方两名角色造成物理伤害（中），\\n\u3000并降低其物理防御力和技能值。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并大幅降低其魔法防御力。\\n\u3000当角色的生命值小于最大生命值的50%时，\\n\u3000降低其行动速度。');
insert into unit_enemy_data values (311103, '黑之王炸脖龙', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【物理】从扭曲世界的狭界中显现的黑之王。\\n身上缠绕着黑暗的瘴气，企图蹂躏不可思议之国。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000使其陷入中毒状态并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方两名角色造成物理伤害（中），\\n\u3000并降低其物理防御力和技能值。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并大幅降低其魔法防御力。\\n\u3000当角色的生命值小于最大生命值的50%时，\\n\u3000降低其行动速度。\\n·黑之王炸脖龙自身的生命值低于35%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311104, '黑之王炸脖龙', 311101, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【模式1】身披瘴气黑幕，黑之王\\n再度降临。聚合了黑暗与混沌的障壁，\\n据说也能承受救世主之光的一击。\\n\\n·展开屏障，完全防御敌人的攻击。\\n\u3000屏障在每次受到伤害时\\n\u3000逐渐失去耐久性，受到6500000伤害后\\n\u3000屏障就会破裂。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害，\\n\u3000使其陷入中毒状态并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并大幅降低其魔法防御力。');
insert into unit_enemy_data values (311105, '黑之王炸脖龙', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【模式2】召唤璃乃、步美、日和莉的\\n暗影。内心的黑暗具象化，暴露\\n自己的弱点。能找回希望之心吗?现在正被质问。\\n\\n·对眼前的1名敌人造成物理伤害。');
insert into unit_enemy_data values (311106, '黑之王炸脖龙', 311100, 0, 16, 270, 1000, 1, 2.1, 0, 0, 0, '【模式3】黑之王解放了终之力，世界渐渐堕入\\n永久的黑暗。大部分书页都染成了黑色，\\n救世主的故事即将迎来终结。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害，\\n\u3000使其陷入中毒状态并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方2名角色造成物理伤害（中），\\n\u3000并降低其物理防御力和技能值。\\n·对前方范围内的角色造成魔法伤害（中），\\n\u3000并大幅降低其魔法防御力。\\n\u3000角色的生命值低于50%时，\\n\u3000降低其行动速度。\\n·黑之王炸脖龙自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311200, '拟花骑士', 311200, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】拟态成巨大花朵的魔物。\\n在被它的美丽所吸引的粗心猎物面前\\n绽放出绝望的光芒。\\n\\n·对前方3名角色造成物理伤害\\n\u3000（大），并对敌方全体造成物\\n\u3000理伤害（大）。\\n\\n·对前方2名角色造成物理伤害\\n\u3000（小），并中幅提升自身的物\\n\u3000理攻击力。\\n\\n·对最前方的角色造成物理伤害\\n\u3000（中），并中幅降低其物理防\\n\u3000御力。\\n\\n·对前方第2名角色造成物理伤害\\n\u3000（中），并中幅降低其物理防\\n\u3000御力。');
insert into unit_enemy_data values (311201, '拟花骑士', 311200, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】拟态成巨大花朵的魔物。\\n在被它的美丽所吸引的粗心猎物面前\\n绽放出绝望的光芒。\\n\\n·对前方3名角色造成物理伤害（大），\\n\u3000并对敌方全体\\n\u3000造成物理伤害（大）。\\n\\n·对前方2名角色造成物理伤害（小），\\n\u3000并中幅提升自身的\\n\u3000物理攻击力。\\n\\n·对最前方的角色造成物理伤害（中），\\n\u3000并中幅降低其\\n\u3000物理防御力。\\n\\n·对前方第2名角色造成物理伤害（中），\\n\u3000并中幅降低其\\n\u3000物理防御力。');
insert into unit_enemy_data values (311202, '拟花骑士', 311202, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】拟态成巨大花朵的魔物。\\n在被它的美丽所吸引的粗心猎物面前\\n绽放出绝望的光芒。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·拟花骑士的所有伤害都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成物理伤害（特大）。\\n\u3000随后根据该技能所造成的伤害量\\n\u3000对敌方全体造成物理伤害。\\n·特大幅度提升自身的物理攻击力和\\n\u3000魔法防御力，\\n\u3000并中幅提升技能值。');
insert into unit_enemy_data values (311203, '拟花骑士', 311203, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】拟态成巨大花朵的魔物。\\n在被它的美丽所吸引的粗心猎物面前\\n绽放出绝望的光芒。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·拟花骑士的所有伤害都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成物理伤害（特大）。\\n\u3000随后根据该技能所造成的伤害量\\n\u3000对敌方全体造成物理伤害。\\n·特大幅度提升自身的物理攻击力和\\n\u3000魔法防御力，\\n\u3000并中幅提升技能值。');
