insert into unit_enemy_data values (311402, '海滨暴君·鲛吉', 311400, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】统治海滩一带的近海的海族暴君。\\n从不逃避对方的挑衅，\\n绝对不允许对方在领地撒野。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并降低其物理防御力，提升\\n\u3000物理攻击力。\\n·对前方范围内的所有敌人造成物理伤害（大），\\n\u3000并降低其物理防御力。\\n·击退敌方全体。之后的一段时间内，\\n\u3000身披雷电，极大幅度提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000鲛吉陷入中毒、猛毒、诅咒、烧伤的效果时，\\n\u3000提升自身的物理防御力和魔法防御力的时间\\n\u3000会变短。\\n·鲛吉自身的生命值低于15%时，根据角色最大\\n\u3000生命值，对敌方全体造成物理比例伤害，\\n\u3000在一定时间内使其处于唯独不可使用\\n\u3000连结爆发的状态，降低其魔法攻击力。\\n\u3000并且会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311403, '海滨暴君·鲛吉', 311400, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】统治海滩一带的近海的海族暴君。\\n从不逃避对于对方的挑衅，\\n绝对不允许对方在领地撒野。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并降低其物理防御力，提升\\n\u3000物理攻击力。\\n·对前方范围内的所有敌人造成物理伤害（大），\\n\u3000并降低其物理防御力。\\n·击退敌方全体。之后的一段时间内，\\n\u3000身披雷电，极大幅度提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000鲛吉陷入中毒、猛毒、诅咒、烧伤的效果时，\\n\u3000提升自身的物理防御力和魔法防御力的时间\\n\u3000会变短。\\n·鲛吉自身的生命值低于15%时，根据角色最大\\n\u3000生命值，对敌方全体造成物理比例伤害，\\n\u3000在一定时间内使其处于唯独不可使用\\n\u3000连结爆发的状态，降低其魔法攻击力。\\n\u3000并且会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311404, '海滨暴君·鲛吉', 311404, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【模式1】对于扰乱领地的无礼之徒，\\n历经千百次抗争的钢铁肉体，\\n会使出浑身解数进行阻挡。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并降低其物理防御力，提升\\n\u3000物理攻击力。\\n·对前方范围内的所有敌人造成物理伤害（大），\\n\u3000并降低其物理防御力。\\n·对前方3名角色造成物理伤害（大）。并对自身\\n\u3000造成物理伤害，一段时间内特大幅度降低物理防\\n\u3000御力和魔法防御力。');
insert into unit_enemy_data values (311405, '海滨暴君·鲛吉', 311405, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【模式2】小弟们追随绝对的领袖魅力和绝望的\\n狂暴力量，集结在一起。由海族精锐\\n发起的互殴于此刻拉开帷幕。\\n\\n·鲛吉对眼前的1名敌人进行物理攻击。\\n·对前方3名角色造成物理伤害（大）。并对自身\\n\u3000造成物理伤害，一段时间内特大幅度降低物理防\\n\u3000御力和魔法防御力。');
insert into unit_enemy_data values (311406, '海滨暴君·鲛吉', 311404, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【模式3】伴随着凶猛的吼声，暴君\\n开始追击。倾注而下的拳脚风暴绝不会停止，\\n直到将刀刃相向的人打得体无完肤。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并降低其物理防御力，提升\\n\u3000物理攻击力。\\n·对前方范围内的所有敌人造成物理伤害（大），\\n\u3000并降低其物理防御力。\\n·击退敌方全体。之后的一段时间内，\\n\u3000身披雷电，极大幅度提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000鲛吉陷入中毒、猛毒、诅咒、烧伤的效果时，\\n\u3000提升自身的物理防御力和魔法防御力的时间\\n\u3000会变短。\\n·鲛吉自身的生命值低于4%时，根据角色最大生\\n\u3000命值，对敌方全体造成物理比例伤害，\\n\u3000在一定时间内使其处于唯独不可使用\\n\u3000连结爆发的状态，降低其魔法攻击力。\\n\u3000并且会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311407, '拳王鲨', 311407, 0, 16, 270, 3000, 1, 2.8, 0, 0, 0, '【物理】因塔的魔力而狂暴化的海洋族狂战士。伴随着凶猛的吼声，\\n挥出凶猛的拳击。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并大幅降低其物理防御力，中幅提升\\n\u3000物理攻击力。\\n·以最近的一名角色为中心，对小范围内的所有敌人\\n\u3000造成物理伤害（大），\\n\u3000大幅降低其物理防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·击退敌方全体。之后的一段时间内，\\n\u3000身披雷电，极大幅度提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000拳王鲨陷入中毒、猛毒、诅咒、毒咒、烧伤的效果时，\\n\u3000提升自身的物理防御力和魔法防御力的时间\\n\u3000会变短。\\n·拳王鲨自身的生命值低于40%时，根据角色最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000在一定时间内使其处于唯独不可使用\\n\u3000连结爆发的状态。\\n\u3000同时，大幅降低其魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000并会展开更为激烈凶暴的攻击。');
