insert into unit_enemy_data values (311501, '失控的圣洁之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【魔法】由于过大的纯粹愿望而暴走的圣弓。\\n如果被它射出的箭射穿，就会化为无力的天使。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害，\\n\u3000对天使状态的角色赋予诅咒。\\n\u3000该效果所造成的诅咒伤害，\\n\u3000无法回复技能值。\\n·对生命值最低的3名角色造成魔法比例伤害，\\n\u3000使生命值低于60%的角色在一定时间内\\n\u3000变成天使。\\n\u3000变成天使的角色只能\\n\u3000使用普通攻击，身上的属性值提升效果\\n\u3000被解除。');
insert into unit_enemy_data values (311502, '失控的圣洁之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【魔法】由于过度纯粹的愿望而失控的圣弓。\\n如果被它射出的箭击穿，就会化为无力的天使。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000对处于天使状态的角色赋予诅咒。\\n\u3000该效果所造成的诅咒伤害，\\n\u3000不会回复技能值。\\n·根据角色的最大生命值，对生命值最低的\\n\u3000三名角色造成一定比例的魔法伤害，并使生命值低于60%的角色\\n\u3000在一定时间内处于天使状态。\\n\u3000变成天使的角色\\n\u3000只能使用普通攻击，身上的属性值提升效果\\n\u3000将被解除。\\n·对生命值最高的两名角色造成魔法伤害（中），\\n\u3000并中幅降低其行动速度。\\n·圣洁之弓自身的生命值低于50%时，\\n\u3000会小幅提升魔法攻击力，并展开\\n\u3000更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311503, '失控的圣洁之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【魔法】由于过度纯粹的愿望而失控的圣弓。\\n如果被它射出的箭击穿，就会化为无力的天使。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，\\n\u3000对处于天使状态的角色赋予诅咒。\\n\u3000该效果所造成的诅咒伤害，\\n\u3000不会回复技能值。\\n·根据角色的最大生命值，对生命值最低的\\n\u3000三名角色造成一定比例的魔法伤害，并使生命值低于60%的角色\\n\u3000在一定时间内处于天使状态。\\n\u3000变成天使的角色\\n\u3000只能使用普通攻击，身上的属性值提升效果\\n\u3000将被解除。\\n·对生命值最高的两名角色造成魔法伤害（中），\\n\u3000并中幅降低其行动速度。\\n·圣洁之弓自身的生命值低于50%时，\\n\u3000会中幅提升魔法攻击力和技能值，并展开\\n\u3000更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311504, '失控的圣洁之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【模式1】扭曲的愿望变成结晶，让人们\\n疯狂。这结晶将封杀一切进攻。\\n\\n·战斗开始时唤出邪念水晶。\\n·邪念水晶在战斗中存在的期间，\\n\u3000会大幅度提升圣洁之弓的物理防御力\\n\u3000和魔法防御力。\\n·邪念水晶一旦被破坏，\\n\u3000圣洁之弓的物理防御力和魔法防御力\\n\u3000会大幅度降低。\\n·对生命值最高的2名角色造成魔法伤害（中），\\n\u3000并降低其行动速度。');
insert into unit_enemy_data values (311505, '失控的圣洁之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【模式2】召唤依里和茜里的暗影。\\n自己的负面形象，考验着双胞胎之间真正的羁绊。\\n\\n·对生命值最低的3名角色造成魔法比例伤害，\\n\u3000使生命值低于60%的角色在一定时间内\\n\u3000变成天使。\\n\u3000变成天使的角色只能\\n\u3000使用普通攻击，身上的属性值提升效果\\n\u3000被解除。');
insert into unit_enemy_data values (311506, '失控的圣洁之弓', 311500, 0, 16, 270, 3000, 2, 2.4, 0, 0, 0, '【模式3】受困于无法实现的愿望，圣弓的权能\\n达到了极致。解放的力量震撼天地，\\n消灭一切邪念。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害，\\n\u3000对天使状态的角色赋予诅咒。\\n\u3000该效果所造成的诅咒伤害，\\n\u3000无法回复技能值。\\n·对生命值最低的3名角色造成魔法比例伤害，\\n\u3000使生命值低于60%的角色在一定时间内\\n\u3000变成天使。\\n\u3000变成天使的角色只能\\n\u3000使用普通攻击，身上的属性值提升效果\\n\u3000被解除。\\n·对生命值最高的2名角色造成魔法伤害（中），\\n\u3000降低其行动速度，并降低其\\n\u3000魔法防御力。\\n·剩余时间少于35秒时，会提升魔法攻击力\\n\u3000和技能值，并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311600, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】让人类因恐惧而战栗的鬼怪们的首领。\\n煽动手下，秘密策划扩大领地的规模。\\n\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (311601, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】让人类因恐惧而战栗的鬼怪们的首领。\\n煽动手下，秘密策划扩大领地的规模。\\n\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (311602, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】让人类因恐惧而战栗的鬼怪们的首领。\\n煽动手下，秘密策划扩大领地的规模。\\n\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·幽灵教父自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311603, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【魔法】让人类因恐惧而战栗的鬼怪们的首领。\\n煽动手下，秘密策划扩大领地的规模。\\n\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·幽灵教父自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311604, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【模式1】为了寻找美味的灵魂，鬼怪们的首领\\n再次现身。带着残暴的家族，\\n蹂躏着人们，使城市充满了尖锐的悲鸣。\\n\\n·战斗开始时，召唤3只\\n\u3000幽灵手下。\\n·每打倒1只幽灵手下，\\n\u3000剩下的幽灵手下数量就减少1只。\\n\u3000之后会再次召唤幽灵手下。\\n·幽灵手下会减少眼前1名角色的\\n\u3000技能值，并回复幽灵教父的\\n\u3000技能值。\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n\u3000且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。');
insert into unit_enemy_data values (311605, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '【模式2】对破坏领地的一切进行制裁。\\n一旦踏入他的领地，就会有魑魅魍魉\\n蛀蚀他的生命，让他遭受人间地狱之苦。\\n\\n·战斗开始时，角色的最大生命值\\n\u3000减少35%。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n\u3000且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。');
insert into unit_enemy_data values (311606, '叫怖的幽灵教父', 311600, 0, 16, 270, 3000, 2, 4.0, 0, 0, 0, '【模式3】将积蓄的力量全部倾吐出来，释放\\n真正的力量。为了统治这个世界，他残暴无道至极，\\n将其渲染成地狱。\\n\\n·必杀技能会根据角色原本的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n\u3000且减少敌方全体角色10%的最大生命值。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n·为自身展开物理/魔法无效化屏障。\\n·幽灵教父自身的生命值低于37.5%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (311700, '幽灵兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '幽灵教父的贴身手下。\\n以自身为盾，保护首领免受一切攻击。');
insert into unit_enemy_data values (311701, '幽灵兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '幽灵教父的贴身手下。\\n用锻炼过的炮弹向城市发起特攻。血气方刚地\\n冲锋陷阵，让四处逃窜的人们陷入恐怖的深渊。\\n\\n·幽灵兄弟不会成为攻击的对象。');
insert into unit_enemy_data values (311702, '幽灵兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '幽灵教父的贴身手下。\\n把和炮弹们收取来的优质精气献给首领。\\n\\n·幽灵教父不会成为攻击的对象。\\n·回复幽灵教父的技能值。\\n\u3000幽灵教父陷入无法行动状态时，\\n\u3000不回复技能值。');
insert into unit_enemy_data values (311703, '幽灵兄弟', 311700, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '终于到了出场的时候，气喘吁吁地登场。\\n气势十足的最强盾牌，挫败了对手的心。');
insert into unit_enemy_data values (311800, '幽灵兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '幽灵教父的贴身手下。\\n将收取的精气交给首领，负责辅佐。\\n\\n·小幅回复幽灵教父的技能值。');
insert into unit_enemy_data values (311801, '幽灵兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '幽灵教父的贴身手下。\\n守护在首领的附近，不露出一丝破绽。在背地里\\n暗中活动，试图将城市中的人逼入绝望之中。\\n\\n·幽灵兄弟不会成为攻击的对象。');
insert into unit_enemy_data values (311802, '幽灵兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '幽灵教父的贴身手下。\\n把和部下收取来的优质精气献给首领。\\n\\n·幽灵教父不会成为攻击的对象。\\n·回复幽灵教父的技能值。\\n\u3000幽灵教父陷入无法行动状态时，\\n\u3000不回复技能值。');
insert into unit_enemy_data values (311803, '幽灵兄弟', 311800, 0, 16, 270, 3000, 2, 999.0, 0, 0, 0, '为了守护家族的危机，冷静地\\n观察战况。作为首领忠实的左膀右臂，直到死前\\n都贯彻辅佐任务。\\n\\n·回复幽灵教父的技能值。');
insert into unit_enemy_data values (311900, '洁白独角兽', 311900, 0, 13, 270, 1000, 1, 2.0, 0, 0, 0, '【物理】吞食雾霭，自身的姿态也如\\n雾霭一般难以捉摸。讨厌人类，\\n会毫不留情地消灭靠近的人。\\n\\n·对敌方全体造成魔法伤害（大）。\\n\u3000此外，提升对烧伤状态的敌人\\n\u3000造成的伤害。\\n\\n·以物理攻击力最高的敌人为中心，\\n\u3000对一定范围内造成魔法伤害\\n\u3000（中），并使其陷入烧伤状态。');
insert into unit_enemy_data values (312000, '黑曜双足飞龙', 312000, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】宿有魔力的黑曜石的结晶龙。据说其美丽身躯的光辉\\n是彷徨在冥府的灵魂的哀叹。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·黑曜双足飞龙的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并大幅降低\\n\u3000其技能值。这个技能的异常状态在伤害免疫的情况下\\n\u3000也可以降低技能值。\\n·特大幅度降低敌方全体的生命值吸收，并大幅降低\\n\u3000其物理防御力。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名角色造成物理伤害（大）。\\n\u3000并以该角色为中心，对小范围内的敌人\\n\u3000造成物理伤害（中），并中幅降低其技能值。\\n\u3000这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。');
