insert into unit_enemy_data values (312100, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】夺走人们梦想的负面魔法生物。\\n阴郁的黑雾笼罩着希望，腐蚀着人们的心灵。');
insert into unit_enemy_data values (312101, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】夺走人们梦想的负面魔法生物。\\n阴郁的黑雾笼罩着希望，腐蚀着人们的心灵。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成魔法伤害（大）。并大幅降低其物理防御力\\n\u3000和魔法防御力，一定时间内生命值回复量\\n\u3000降低70%。\\n·对前方2名角色造成物理伤害（中）。');
insert into unit_enemy_data values (312102, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】夺走人们梦想的负面魔法生物。\\n阴郁的黑雾笼罩着希望，腐蚀着人们的心灵。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成魔法伤害（大）。并大幅降低其物理防御力\\n\u3000和魔法防御力，一定时间内生命值回复量\\n\u3000降低70%。\\n·对前方2名角色造成物理伤害（中）。');
insert into unit_enemy_data values (312103, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】夺走人们梦想的负面魔法生物。\\n阴郁的黑雾笼罩着希望，腐蚀着人们的心灵。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成魔法伤害（大）。并大幅降低其物理防\\n\u3000御力和魔法防御力，一定时间内生命值回复量\\n\u3000降低70%。\\n·对前方2名角色造成物理伤害（中）。\\n·反物质兽 de M自身的生命值低于25%\\n\u3000时，小幅提升自身的技能值，\\n\u3000并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (312104, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式1】从城市中吸取爱和希望的\\n反物质兽。想要打倒魔法少女们，\\n吸收城市里所有闪耀的力量。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成魔法伤害（大）。并大幅降低其物理防\\n\u3000御力和魔法防御力，一定时间内生命值回复量\\n\u3000降低70%。\\n·根据最后方的1名敌人的生命值\\n\u3000对其造成物理比例伤害（特大），使其陷入束缚状态，\\n\u3000特大幅度降低其技能值。\\n\u3000并小幅提升反物质兽 de M自身的\\n\u3000技能值。');
insert into unit_enemy_data values (312105, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式2】吸收了魔法少女们的猛攻，\\n反物质兽的欲望变得更加膨胀。甚至，\\n保护人们的勇气也被溶蚀。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成魔法伤害（大）。并大幅降低其物理防\\n\u3000御力和魔法防御力，一定时间内生命值回复量\\n\u3000降低70%。\\n·解除敌方全体的屏障，根据最大生命值\\n\u3000对其造成魔法比例伤害，并使其陷入中毒状态。');
insert into unit_enemy_data values (312106, '反物质兽 de M', 312100, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式3】负面的情绪达到了极点，扭曲的\\n欲望包围了这一带。挫伤了魔法少女们的信念，\\n想要将没有守护者的城市吸收为自己的一部分。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成魔法伤害（大）。并大幅降低其物理防\\n\u3000御力和魔法防御力，一定时间内生命值回复量\\n\u3000降低70%。\\n·根据最大生命值，对前方2名角色造成\\n\u3000物理比例伤害。\\n·反物质兽 de M自身的生命值低于8%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (312200, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】胖乎乎的目光锐利的最可怕的雪人。\\n浑身笼罩着狂气，在圣夜的街道上兜兜转转。\\n一年一度的特别夜晚变为黑色圣诞节。');
insert into unit_enemy_data values (312201, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 4.0, 0, 0, 0, '【物理】胖乎乎且目光锐利的最可怕的雪人。\\n浑身笼罩着狂气，在圣夜的街道上兜兜转转。\\n一年一度的特别夜晚变为黑色圣诞节。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并将其大幅度击飞。此外，自身的目标没有被击破时，\\n\u3000中幅提升物理防御力，\\n\u3000小幅提升魔法防御力。这个技能的效果\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害。自身的目标没有被击破时，\\n\u3000追加赋予眩晕效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·剩余时间少于20秒时，会拿出礼物型炸弹，\\n\u3000经过一段时间后，根据角色的最大生命值\\n\u3000对敌方全体造成\\n\u3000物理比例伤害。\\n\u3000并根据至尊雪人自身剩余生命值\\n\u3000对其造成一定比例的物理伤害，\\n\u3000特大幅度降低其技能值。\\n·击破头部时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·击破躯体时，小幅降低其\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (312202, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 3.6, 0, 0, 0, '【物理】胖乎乎且目光锐利的最可怕的雪人。\\n浑身笼罩着狂气，在圣夜的街道上兜兜转转。\\n一年一度的特别夜晚变为黑色圣诞节。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并将其大幅度击飞。此外，自身的目标没有被击破时，\\n\u3000中幅提升物理防御力，\\n\u3000小幅提升魔法防御力。这个技能的效果\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害。自身的目标没有被击破时，\\n\u3000追加赋予眩晕效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·剩余时间少于20秒时，会拿出礼物型炸弹，\\n\u3000经过一段时间后，根据角色的最大生命值\\n\u3000对敌方全体造成\\n\u3000物理比例伤害。\\n\u3000并根据至尊雪人自身剩余生命值\\n\u3000对其造成一定比例的物理伤害，\\n\u3000特大幅度降低其技能值。\\n·击破头部时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·击破躯体时，小幅降低其\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (312203, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】胖乎乎且目光锐利的最可怕的雪人。\\n浑身笼罩着狂气，在圣夜的街道上兜兜转转。\\n一年一度的特别夜晚变为黑色圣诞节。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并将其大幅度击飞。此外，自身的目标没有被击破时，\\n\u3000中幅提升物理防御力，\\n\u3000小幅提升魔法防御力。这个技能的效果\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害。自身的目标没有被击破时，\\n\u3000追加赋予眩晕效果。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·剩余时间少于30秒时，\\n\u3000会展开更为激烈凶暴的攻击。\\n·剩余时间少于20秒时，会拿出礼物型炸弹，\\n\u3000经过一段时间后，根据角色的最大生命值\\n\u3000对敌方全体造成\\n\u3000物理比例伤害。\\n\u3000并根据至尊雪人自身剩余生命值\\n\u3000对其造成一定比例的物理伤害，\\n\u3000特大幅度降低其技能值。\\n·击破头部时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·击破躯体时，小幅降低其\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (312204, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式1】使派对会场陷入混乱的\\n巨大的雪不倒翁。原本是深受人们喜爱的\\n善良的吉祥物。大家的支持是进攻的关键。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其大幅击飞。自身的目标没有被击破时，\\n\u3000中幅提升物理防御力，\\n\u3000小幅提升魔法防御力。\\n·对敌方全体造成魔法伤害（大），\\n\u3000并使其陷入冻结状态。\\n\u3000同时大幅降低其行动速度和\\n\u3000物理防御力。\\n·击破头部时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·击破躯体时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·战斗中，根据对至尊雪人施加的\\n\u3000状态异常效果和\\n\u3000属性值下降效果的数量，受到的\\n\u3000伤害也大幅提升。\\n');
insert into unit_enemy_data values (312205, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 20.0, 0, 0, 0, '【模式2】召唤在圣夜里兴致勃勃的3名女孩的\\n暗影。那是巨大雪不倒翁制作的幻象。\\n为了自己的名誉，现在就站起来吧，少女们！\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，\\n\u3000并将其大幅击飞。自身的目标没有被击破时，\\n\u3000中幅提升物理防御力，\\n\u3000小幅提升魔法防御力。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理比例伤害。自身的目标没有被击破时，\\n\u3000追加赋予眩晕效果。\\n·击破头部时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·击破躯体时，小幅降低其\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (312206, '至尊雪人', 312200, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式3】为了将所有的想法碾杀\\n而不断滚动的巨大雪不倒翁。向所有寂寞的人们，\\n温柔地送出爆炎的礼物给你?\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害，并大幅度\\n\u3000将其击飞。自身的目标没有被击破时，\\n\u3000中幅提升物理防御力，\\n\u3000小幅提升魔法防御力。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成物理比例伤害。自身的目标\\n\u3000没有被击破时，追加赋予\\n\u3000眩晕效果。\\n·剩余时间少于30秒时，会展开更为激烈凶暴的\\n\u3000攻击。\\n·剩余时间少于20秒时，会拿出礼物型炸弹，\\n\u3000过一段时间后，根据角色的最大生命值\\n\u3000对敌方全体造成\\n\u3000物理比例伤害。\\n\u3000并且也会根据至尊雪人自身剩余生命值\\n\u3000对其造成物理比例伤害，\\n\u3000大幅降低其技能值。\\n·击破头部时，小幅降低其\\n\u3000物理防御力和魔法防御力。\\n·击破躯体时，小幅降低其\\n\u3000物理防御力和魔法防御力。');
