insert into unit_enemy_data values (312301, '巨大吞拿鱼', 312300, 0, 16, 270, 3000, 1, 4.0, 0, 0, 0, '【物理】自古在大间的渔民之间传诵的梦幻巨鱼。\\n巨大的身躯极速冲锋，让所有船只都化为海里的渣滓。\\n但是，它身上的肉似乎是世间罕见的极品。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000造成的伤害会\\n\u3000根据自身的物理攻击力而增加。');
insert into unit_enemy_data values (312302, '巨大吞拿鱼', 312302, 0, 16, 270, 3000, 1, 3.3, 0, 0, 0, '【物理】自古在大间的渔民之间传诵的梦幻巨鱼。\\n巨大的身躯极速冲锋，让所有船只都化为海里的渣滓。\\n但是，它身上的肉似乎是世间罕见的极品。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000造成的伤害会\\n\u3000根据自身的物理攻击力而增加。\\n·剩余时间少于35秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312303, '巨大吞拿鱼', 312302, 0, 16, 270, 3000, 1, 1.1, 0, 0, 0, '【物理】自古在大间的渔民之间传诵的梦幻巨鱼。\\n巨大的身躯极速冲锋，让所有船只都化为海里的渣滓。\\n但是，它身上的肉似乎是世间罕见的极品。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000造成的伤害会\\n\u3000根据自身的物理攻击力而增加。\\n·剩余时间少于35秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312304, '巨大吞拿鱼', 312302, 0, 16, 270, 3000, 1, 0.0, 0, 0, 0, '【模式1】让无数渔民沉入波浪的海上之王。\\n金光闪闪的鱼鳍可以压断大渔船的龙骨。\\n那样子就像大海的解体秀一样\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000并将其击退。\\n·对前方范围内的所有敌人造成物理伤害（大），\\n\u3000并对后方2名角色造成\\n\u3000物理伤害（中）。同时提升自身的物理攻击力。');
insert into unit_enemy_data values (312305, '巨大吞拿鱼', 312305, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '【模式2】在海里疾驰的怪鱼力量变强。\\n仿佛鼓舞自己一般，随意游动一下便掀起巨大的\\n旋涡，化为分割大海的巨大炮弹猛攻敌人。\\n\\n·必杀技能会对前方3名角色造成\\n\u3000物理攻击（特大），\\n\u3000并将其击退。\\n·提升自身的物理攻击力、行动速度\\n\u3000和技能值。');
insert into unit_enemy_data values (312306, '巨大吞拿鱼', 312302, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '【模式3】即使无数次尝试用鱼叉叉住那黑色的巨大身体，\\n也无法阻挡传说中的巨鱼的猛冲。\\n与长久以来在海上奋斗的男儿们的决胜时刻到了。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n\u3000并将其击退。\\n·提升自身的物理攻击力、行动速度\\n\u3000和技能值。\\n·剩余时间少于35秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312400, '寒冰格里芬', 312400, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】展开冰翼，威吓冒险者的\\n大鹫。闯入地盘的人，\\n会被无情地冻住，成为孩子们的\\n祭品。\\n\\n·必杀技能会对前方小范围内\\n\u3000造成物理伤害（大），\\n\u3000并大幅度将其击飞，\\n\u3000使其陷入冻结状态。');
insert into unit_enemy_data values (312401, '寒冰格里芬', 312400, 0, 11, 270, 1000, 1, 1.6, 0, 0, 0, '【物理】展开冰翼，威吓冒险者的\\n大鹫。闯入地盘的人，\\n会被无情地冻住，成为孩子们的\\n祭品。\\n\\n·必杀技能会对前方小范围内\\n\u3000造成物理伤害（大），\\n\u3000并大幅度将其击飞，\\n\u3000使其陷入冻结状态。');
insert into unit_enemy_data values (312500, '厄勒克特拉夫人', 312500, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】积蓄塔的魔力转化为雷电释放的怪鸟。\\n与雷云一起出现，用天灾般的雷葬送敌人。\\n\\n·对敌方全体造成魔法伤害（大），并小幅提升\\n\u3000自身的魔法攻击力和行动速度。\\n\u3000对于进行物理攻击的敌人追加伤害。\\n·对前方范围内的所有敌人造成魔法伤害（中），\\n\u3000并中幅降低其物理防御力和魔法防御力。\\n·对物理攻击力最高的1名敌人造成\\n\u3000魔法伤害（大），使其长时间陷入麻痹状态。\\n\u3000自身的生命值低于85%时，对魔法攻击力\\n\u3000最高的敌人也进行此攻击。');
insert into unit_enemy_data values (312501, '厄勒克特拉夫人', 312501, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】积蓄魔力转化为雷电释放的怪鸟。\\n与雷云一起出现，用天灾般的雷葬送敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·厄勒克特拉夫人的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会小幅提升自身的魔法攻击力和\\n\u3000魔法防御力，并对最近的一名敌人造成\\n\u3000魔法伤害（特大）。');
insert into unit_enemy_data values (312502, '厄勒克特拉夫人', 312502, 0, 6, 270, 1000, 2, 1.6, 0, 0, 0, '【魔法】积蓄魔力转化为雷电释放的怪鸟。\\n与雷云一起出现，用天灾般的雷葬送敌人。\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·厄勒克特拉夫人的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低厄勒克特拉夫人的\\n\u3000技能值回复量。\\n·必杀技能会小幅提升自身的魔法攻击力和\\n\u3000魔法防御力，并对最近的一名敌人造成\\n\u3000魔法伤害（特大），赋予其麻痹状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成魔法伤害（特大），\\n\u3000并赋予麻痹状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
