insert into unit_enemy_data values (312601, '碎岩角鹿', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】拥有漂亮麋角的大型魔物。用可以轻松粉碎巨大岩石的大角\\n把冒险者一扫而尽。\\n\\n·普通攻击时，以前方一名角色为中心，\\n\u3000对一定范围内的角色造成物理伤害（小）。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并中幅降低自身的物理和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并使其麻痹。同时大幅降低自身的\\n\u3000物理和魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (312602, '碎岩角鹿', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】拥有漂亮麋角的大型魔物。用可以轻松粉碎巨大岩石的大角\\n把冒险者一扫而尽。\\n\\n·普通攻击时，以前方一名角色为中心，\\n\u3000对一定范围内的角色造成物理伤害（小）。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并中幅降低自身的物理和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并使其麻痹。同时大幅降低自身的\\n\u3000物理和魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312603, '碎岩角鹿', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【物理】拥有漂亮麋角的大型魔物。用可以轻松粉碎巨大岩石的大角\\n把冒险者一扫而尽。\\n\\n·普通攻击时，以前方一名角色为中心，\\n\u3000对一定范围内的角色造成物理伤害（小）。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并中幅降低自身的物理和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并使其麻痹。同时大幅降低自身的\\n\u3000物理和魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312604, '碎岩角鹿', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式1】首领的叫声使雄鹿们\\n成群涌来。能阻挡住奋不顾身的\\n冲锋吗？\\n\\n·战斗开始时，召唤3只野性角鹿，\\n\u3000并将其击退。\\n·每打倒1只野性角鹿，\\n\u3000剩下的野性角鹿数量就减少1只。\\n\u3000之后再次召唤野性角鹿，\\n\u3000并将其击退。\\n·普通攻击时，以前方1名角色为中心，对一定范围内的角色造成\\n\u3000物理伤害（小）。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n\u3000并中幅降低自身的物理和\\n\u3000魔法防御力。');
insert into unit_enemy_data values (312605, '碎岩角鹿', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式2】役使朽木魔物，\\n将冒险者逼入绝境。一旦失去了共生的朽木，其身躯也会\\n一同走向毁灭。消灭朽木是进攻的关键。\\n\\n·战斗开始时，召唤2只树精，\\n\u3000并将其击退。\\n·前方的树精生命值变为0时，\\n\u3000小幅降低碎岩角鹿的物理和\\n\u3000魔法防御力。\\n·后方的树精生命值变为0时，\\n\u3000极大幅度降低碎岩角鹿的\\n\u3000物理和魔法防御力。\\n·普通攻击时，以前方一名角色为中心，对一定范围内的角色\\n\u3000造成物理伤害（小）。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并中幅降低自身的物理和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并使其麻痹。同时大幅降低自身的\\n\u3000物理和魔法防御力。');
insert into unit_enemy_data values (312606, '碎岩角鹿', 312600, 0, 16, 270, 3000, 1, 2.7, 0, 0, 0, '【模式3】受伤的角兽拼尽全力。\\n赌上性命的冲锋使身体变成紫电，如同一道闪光\\n瓦解身经百战的冒险者一行人。\\n\\n·普通攻击时，以前方一名角色为中心，\\n\u3000对一定范围内的角色造成物理伤害（小）。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并中幅降低自身的物理和\\n\u3000魔法防御力。\\n·对前方范围内的敌人造成物理伤害（中），\\n\u3000并使其麻痹。同时大幅降低自身的\\n\u3000物理和魔法防御力。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
