insert into unit_enemy_data values (312701, '黑风的精灵', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【魔法】考验使役者真正力量的黑风精灵。\\n它没有恶意，纯粹是在考验隐藏在对方内心的\\n想法和情谊。\\n\\n·必杀技能会根据黑风的精灵的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000魔法比例伤害。同时，\\n\u3000对剩余生命值最低的一名角色赋予倒计时，\\n\u30005秒后对其造成最大生命值80%的\\n\u3000魔法比例伤害。\\n\u3000黑风的精灵使用必杀技能之后\\n\u3000在一定时间内\\n\u3000不会再次使用必杀技能。');
insert into unit_enemy_data values (312702, '黑风的精灵', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【魔法】考验使役者真正力量的黑风精灵。\\n它没有恶意，纯粹是在考验隐藏在对方内心的\\n想法和情谊。\\n\\n·必杀技能会根据黑风的精灵的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000魔法比例伤害。同时，\\n\u3000对剩余生命值最低的一名角色赋予倒计时，\\n\u30005秒后对其造成最大生命值80%的\\n\u3000魔法比例伤害。\\n\u3000黑风的精灵使用必杀技能之后\\n\u3000在一定时间内\\n\u3000不会再次使用必杀技能。\\n·使敌方全体陷入猛毒状态，\\n\u3000并大幅降低其物理和魔法攻击力。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312703, '黑风的精灵', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【魔法】考验使役者真正力量的黑风精灵。\\n它没有恶意，纯粹是在考验隐藏在对方内心的\\n想法和情谊。\\n\\n·必杀技能会根据黑风的精灵的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000魔法比例伤害。同时，\\n\u3000对剩余生命值最低的一名角色赋予倒计时，\\n\u30005秒后对其造成最大生命值80%的\\n\u3000魔法比例伤害。\\n\u3000黑风的精灵使用必杀技能之后\\n\u3000在一定时间内\\n\u3000不会再次使用必杀技能。\\n·使敌方全体陷入猛毒状态，\\n\u3000并大幅降低其物理和魔法攻击力。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (312704, '黑风的精灵', 312700, 0, 16, 270, 3000, 2, 2.7, 0, 0, 0, '【模式1】第一试炼。只要能看清黑风精灵暴风的缝隙，\\n就能自然而然地开拓出\\n自己前进的道路。\\n\\n·对敌方全体造成物理伤害，使其眩晕\\n\u3000并将其击退。\\n\u3000黑风的精灵沉睡时，会大幅降低\\n\u3000自身的物理防御力，如果在此期间对其造成\\n\u3000100000以上的伤害，可延长其防御力降低的时间，\\n\u3000并降低其行动速度。');
