insert into unit_enemy_data values (312801, '魔物公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】坐在边境城堡里的，孤高的魔物公主。\\n满怀悲哀的爱慕，击溃挑衅自己的人。\\n\\n·必杀技能会根据前方两名角色的最大生命值，\\n\u3000造成一定比例的物理伤害。\\n\u3000蓄力成功时，会提升伤害，\\n\u3000使敌方全体陷入畏惧状态。\\n\u3000陷入畏惧状态的角色，在一定时间内\\n\u3000攻击及回复生命值的连结爆发\\n\u3000和技能效果会降低。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·大幅降低物理防御力和魔法防御力后，\\n\u3000在一定时间内蓄力。\\n\u3000蓄力后，特大幅度回复技能值。\\n\u3000在蓄力期间，如果魔物公主受到\\n\u30007000以上的伤害，则蓄力失败，\\n\u3000技能值大幅降低。\\n·对前方一名敌人造成物理伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·根据最大生命值，对前方三名角色造成\\n\u3000物理比例伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。');
insert into unit_enemy_data values (312802, '魔物公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】坐在边境城堡里的，孤高的魔物公主。\\n满怀悲哀的爱慕，击溃挑衅自己的人。\\n\\n·必杀技能会根据前方两名角色的最大生命值，\\n\u3000造成一定比例的物理伤害。\\n\u3000蓄力成功时，会提升伤害，\\n\u3000使敌方全体陷入畏惧状态。\\n\u3000陷入畏惧状态的角色，在一定时间内\\n\u3000攻击及回复生命值的连结爆发\\n\u3000和技能效果会降低。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·大幅降低物理防御力和魔法防御力后，\\n\u3000在一定时间内蓄力。\\n\u3000蓄力后，特大幅度回复技能值。\\n\u3000在蓄力期间，如果魔物公主受到\\n\u300035000以上的伤害，则蓄力失败，\\n\u3000技能值大幅降低。\\n·对前方一名敌人造成物理伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·根据最大生命值，对前方三名角色造成\\n\u3000物理比例伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。');
insert into unit_enemy_data values (312803, '魔物公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【物理】坐在边境城堡里的，孤高的魔物公主。\\n满怀悲哀的爱慕，击溃挑衅自己的人。\\n\\n·必杀技能会根据前方两名角色的最大生命值，\\n\u3000造成一定比例的物理伤害。\\n\u3000蓄力成功时，会提升伤害，\\n\u3000使敌方全体陷入畏惧状态。\\n\u3000陷入畏惧状态的角色，在一定时间内\\n\u3000攻击及回复生命值的连结爆发\\n\u3000和技能效果会降低。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·大幅降低物理防御力和魔法防御力后，\\n\u3000在一定时间内蓄力。\\n\u3000蓄力后，特大幅度回复技能值。\\n\u3000在蓄力期间，如果魔物公主受到\\n\u3000180000以上的伤害，则蓄力失败，\\n\u3000技能值大幅降低。\\n·对前方一名敌人造成物理伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·根据最大生命值，对前方三名角色造成\\n\u3000物理比例伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·魔物公主自身的生命值低于25%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (312804, '魔物公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式1】高洁的公主带领士兵\\n对抗敌人。仿佛在掩饰内心的叹息。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对前方2名角色造成物理比例伤害。\\n·对眼前的1名敌人造成物理伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降的影响。\\n·根据最大生命值，对前方3名角色造成\\n\u3000物理比例伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降的影响。');
insert into unit_enemy_data values (312805, '魔物公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式2】公主释放出更大的力量。\\n用扭曲的强烈爱慕，拒绝一切，屠杀一切。\\n\\n·对来自敌人的攻击造成的麻痹、眩晕、束缚、冻结、\\n\u3000时间停止、行动速度降低效果\\n\u3000失去抗性。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对前方2名角色造成物理比例伤害。\\n·对眼前的1名敌人造成物理伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降的影响。\\n·根据最大生命值，对前方3名角色造成\\n\u3000物理比例伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降的影响。\\n·短时间内变为无敌状态，解除敌方全体的属性值提升\\n\u3000效果后，在一定时间内展开\\n\u3000战斗领域。\\n\\n【战斗领域效果】\\n·敌方全体的物理攻击力和魔法攻击力降低100%后，\\n\u3000特大幅度提升\\n\u3000物理攻击力和魔法攻击力。\\n·特大幅度提升魔物公主的物理攻击力。');
insert into unit_enemy_data values (312806, '魔物公主', 312800, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式3】与莫大的悲哀相呼应，公主的进攻\\n极其猛烈。以少女的热情，\\n无法拯救那悲伤。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对前方2名角色造成物理比例伤害。\\n\u3000蓄力成功时，会提高伤害，\\n\u3000使敌方全体陷入畏惧状态。\\n\u3000陷入畏惧状态的角色，在一定时间内，\\n\u3000攻击及生命值回复的连结爆发\\n\u3000和技能效果降低。\\n·大幅降低物理防御力和魔法防御力后，\\n\u3000在一定时间内积蓄力量。\\n\u3000力量蓄满时，特大幅度回复技能值。\\n\u3000在蓄力期间，如果魔物公主受到\\n\u30001500000以上的伤害，则蓄力失败，\\n\u3000技能值大幅降低。\\n·对眼前的1名敌人造成物理伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降的影响。\\n·根据最大生命值，对前方3名角色造成\\n\u3000物理比例伤害。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降的影响。\\n魔物公主自身的生命值低于25%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (312900, '黑月的守护石像', 312900, 0, 16, 270, 3000, 1, 2.5, 0, 0, 0, '【物理】守护天上圣域，被黑月祝福的雕像。\\n将闯入圣域的冒险者埋葬在黄泉的黑暗中。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成物理比例伤害。\\n·对敌方全体造成物理伤害（大），\\n\u3000并解除属性值提升效果。在伤害免疫的情况下，\\n\u3000该技能也会生效。\\n·大幅提升自身物理攻击力和\\n\u3000魔法攻击力。白阳的守护石像被讨伐时，\\n\u3000使用此技能。\\n·黑月的守护石像的生命值低于50%时，\\n\u3000大幅提升其物理攻击力，中幅提升其\\n\u3000物理防御力。\\n·黑月的守护石像被击破时，\\n\u3000小幅降低其物理防御力\\n\u3000和魔法防御力。\\n·白阳的守护石像被讨伐时，\\n\u3000战斗开始后，或是在黑月的守护石像进行\\n\u3000普通攻击时，改变黑月的守护石像的\\n\u3000攻击行动。');
insert into unit_enemy_data values (313000, '白阳的守护石像', 313000, 0, 16, 270, 3000, 2, 2.5, 0, 0, 0, '【物理】守护天上圣域，被白阳祝福的雕像。\\n用净化一切的白雷，审判冒险者。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。\\n·对敌方全体造成魔法伤害（大），\\n\u3000并解除属性值提升效果。白阳的守护石像\\n\u3000大幅提升魔法攻击力。在伤害免疫的情况下，\\n\u3000该技能也会生效。黑月的\\n\u3000守护石像被讨伐时，使用\\n\u3000此技能。\\n·中幅提升我方全体的物理防御力和魔法\\n\u3000防御力。\\n·白阳的守护石像的生命值低于50%时，\\n\u3000特大幅度提升其魔法攻击力，小幅提升其\\n\u3000魔法防御力。\\n·白阳的守护石像被击破时，\\n\u3000小幅降低其物理防御力和魔法防御力。\\n·黑月的守护石像被讨伐时，\\n\u3000战斗开始后，或是在白阳的守护石像进行\\n\u3000普通攻击时，改变白阳的守护石像的\\n\u3000攻击行动。');
