insert into unit_enemy_data values (313103, '爆裂·时间星盘', 313100, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】帆稀收藏的魔法道具，超越临界点\\n暴走时的样子。忘记了原本的任务，化身为引领冒险者\\n进入混沌的领航人。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度、\\n\u3000魔法防御力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·剩余时间少于25秒时，会大幅提升自身的技能值，\\n\u3000 并展开更为激烈凶暴的攻击。\\n·左侧目标处于击破状态时，\\n\u3000物理/魔法防御力中幅降低。\\n·中央下方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·中央上方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·右侧目标处于击破状态时，\\n\u3000物理/魔法防御力特大幅度降低。');
insert into unit_enemy_data values (313104, '爆裂·时间星盘', 313104, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【模式1】暴走的魔法道具想把所有的东西\\n留在自己的领域。不屈服于重重障碍，\\n相信自己，开拓未来。\\n\\n·此战斗中，所有目标\\n\u3000击破无效。\\n·展开屏障，完全防御敌人的攻击。\\n\u3000屏障在每次受到伤害时逐渐失去耐久性，\\n\u3000受到一定程度的伤害后\\n\u3000屏障就会破裂。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度、\\n\u3000魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。');
insert into unit_enemy_data values (313105, '爆裂·时间星盘', 313100, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【模式2】召唤祈梨和嘉夜的暗影。\\n从自己的「如果」的身影中，两个人看到了什么。\\n打破虚构，证明自己道路正确性的时候到了。\\n\\n·此战斗中，所有目标\\n\u3000击破无效。\\n·中幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。');
insert into unit_enemy_data values (313106, '爆裂·时间星盘', 313100, 0, 16, 270, 3000, 2, 0.8, 0, 0, 0, '【模式3】失去了主人的魔法道具失去了控制，\\n持续暴走。只要坚持抵抗不断降临的绝望，\\n就会开辟出一条希望的光明之路。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度、\\n\u3000魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·剩余时间少于25秒时，会大幅提升自身的技能值，\\n\u3000 并展开更为激烈凶暴的攻击。\\n·左侧目标处于击破状态时，\\n\u3000物理/魔法防御力中幅降低。\\n·中央下方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·中央上方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·右侧目标处于击破状态时，\\n\u3000物理/魔法防御力特大幅度降低。');
