insert into unit_enemy_data values (313301, '究极玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】因遭到遗忘而失控，悲伤的玩具之王。\\n因第三次被孩子们遗忘的恐惧与悲伤而\\n发抖。来吧！如今正是BB团崛起的时候！\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并特大幅度降低最近一名敌人的\\n\u3000物理攻击力和魔法攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据角色的最大生命值，\\n\u3000对最近一名敌人造成一定比例的物理伤害。\\n·根据角色的最大生命值，\\n\u3000对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000两名角色造成一定比例的物理伤害。');
insert into unit_enemy_data values (313302, '究极玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】因遭到遗忘而失控，悲伤的玩具之王。\\n因第三次被孩子们遗忘的恐惧与悲伤而\\n发抖。来吧！如今正是BB团崛起的时候！\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并特大幅度降低最近一名敌人的\\n\u3000物理攻击力和魔法攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据角色的最大生命值，\\n\u3000对最近一名敌人造成一定比例的物理伤害。\\n·根据角色的最大生命值，\\n\u3000对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000两名角色造成一定比例的物理伤害。');
insert into unit_enemy_data values (313303, '究极玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】因遭到遗忘而失控，悲伤的玩具之王。\\n因第三次被孩子们遗忘的恐惧与悲伤而\\n发抖。来吧！如今正是BB团崛起的时候！\\n\\n·究极玩具王自身的生命值\\n\u3000低于40%时，双臂会发出光芒，\\n\u3000并展开更为激烈凶暴的攻击。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并特大幅度降低最近一名敌人的\\n\u3000物理攻击力和魔法攻击力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。在双臂发光时，\\n\u3000将提升造成的伤害，\\n\u3000同时会对最近的一名敌人造成物理伤害（大）。\\n·根据角色的最大生命值，\\n\u3000对最近一名敌人造成一定比例的物理伤害。\\n\u3000在双臂发光时，将提升造成的伤害，\\n\u3000该技能造成的伤害\\n\u3000不会回复技能值。\\n·根据角色的最大生命值，\\n\u3000对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000两名角色造成一定比例的物理伤害。在双臂发光时，\\n\u3000该技能所造成的伤害\\n\u3000不会回复技能值。');
insert into unit_enemy_data values (313304, '究极玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【模式1】面对失控的朋友，小小的英雄\\n挺身而出。能将他从悲伤中拯救出来吗？\\n与“我的朋友”共同面对吧，BB团！\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并特大幅度降低前方一名敌人的\\n\u3000物理攻击力和魔法攻击力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据角色的最大生命值，\\n\u3000对前方一名敌人造成一定比例的物理伤害。\\n·根据角色的最大生命值，\\n\u3000对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000两名角色造成一定比例的物理伤害。\\n·“我的朋友”在一定时间内作为我方伙伴出现，\\n\u3000究极玩具王将无法行动。\\n\u3000并以“我的朋友”为中心展开领域，特大幅度提升\\n\u3000范围内角色的物理攻击力、物理暴击和\\n\u3000技能值上升量。');
insert into unit_enemy_data values (313305, '究极玩具王', 313305, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【模式2】悲伤的玩具王所呈现的幻象。直面自我\\n是一件很可怕的事。我不是孤军奋战！\\n心怀这样的强烈意志，不要输，BB团！\\n\\n·召唤碧、美冬、珠希的暗影。\\n·根据角色的最大生命值，\\n\u3000对前方一名敌人造成一定比例的物理伤害。');
insert into unit_enemy_data values (313306, '究极玩具王', 313300, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【模式3】悲伤的玩具王最后的抵抗。\\n被遗忘的孤独感所支配的力量非常强大，且充满破坏性。\\n将对朋友的挂念化为力量，救人吧，BB团！\\n\\n·究极玩具王自身的生命值\\n\u3000低于15%时，双臂会发出光芒。特大幅度降低\\n\u3000物理防御力和魔法防御力，\\n\u3000并展开更为激烈凶暴的攻击。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并特大幅度降低前方一名敌人的\\n\u3000物理攻击力和魔法攻击力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000在双臂发光时，将提升造成的伤害，\\n\u3000并对前方一名敌人造成物理伤害（大）。\\n·根据角色的最大生命值，\\n\u3000对前方一名敌人造成一定比例的物理伤害。\\n\u3000在双臂发光时，将提升造成的伤害，\\n\u3000该技能造成的伤害\\n\u3000不会回复技能值。\\n·根据角色的最大生命值，\\n\u3000对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000两名角色造成一定比例的物理伤害。\\n\u3000在双臂发光时，该技能造成的伤害\\n\u3000不会回复技能值。');
insert into unit_enemy_data values (313400, '赤剑毒蛇', 313400, 0, 16, 270, 3000, 1, 3.0, 0, 0, 0, '【物理】将赤红的邪剑藏于尾部，凶暴的大毒蛇。\\n利用毒息让猎物变得虚弱，然后用剑使出必杀一击葬送其生命。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·赤剑毒蛇的所有伤害\\n\u3000都会小幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对攻击力\\n\u3000（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成物理伤害（特大）。当处于猛毒状态时，\\n\u3000追加造成物理伤害。之后大幅降低\\n\u3000敌方全体的生命值吸收。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并赋予中毒状态。\\n\u3000对已经处于中毒状态的敌人赋予猛毒状态。\\n·普通攻击对最近的一名敌人造成物理伤害（中），\\n\u3000并赋予中毒状态。\\n赤剑毒蛇造成的中毒或猛毒伤害\\n\u3000不会使角色的技能值回复。');
insert into unit_enemy_data values (313500, '卡利萨', 313500, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '肆意操纵和强化魔物、擅长使鞭的少年。\\n率领着红蓝黄史莱姆，攻击方式灵活而猛烈。\\n\\n·必杀技能会根据卡利萨的\\n\u3000物理攻击力，对前方范围内的敌人造成\\n\u3000一定比例的物理伤害。\\n·大幅提升卡利萨的物理攻击力。\\n·根据卡利萨的物理攻击力，对前方第一名角色\\n\u3000造成一定比例的物理伤害。');
insert into unit_enemy_data values (313501, '卡利萨', 313501, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '肆意操纵和强化魔物、擅长使鞭的少年。\\n率领着红蓝黄史莱姆，攻击方式灵活而猛烈。\\n\\n·必杀技能会根据卡利萨的\\n\u3000物理攻击力，对前方范围内的敌人造成\\n\u3000一定比例的物理伤害。\\n·大幅提升卡利萨的物理攻击力。\\n·根据卡利萨的物理攻击力，对前方第一名角色\\n\u3000造成一定比例的物理伤害。');
insert into unit_enemy_data values (313600, '兰法', 313600, 0, 16, 270, 3001, 2, 3.0, 0, 0, 0, '为自身天赋之才与过分敏锐的感觉而苦恼的幽静歌姬。\\n不爱与人相争，但会为了愿望和同伴而奋战。\\n\\n·必杀技能会对前方范围内的敌人造成魔法伤害（中），\\n\u3000并使其陷入结晶化状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000处于结晶化状态的敌人将无法行动，\\n\u3000并受到持续伤害。\\n\u3000完全回复生命值或经过一定时间\\n\u3000即可解除结晶化状态。\\n·对前方范围内的敌人赋予猛毒状态。\\n\u3000对处于结晶化状态的敌人造成\\n\u3000追加的魔法伤害（大）。\\n·对前方第一名角色造成魔法伤害（小）。');
insert into unit_enemy_data values (313601, '兰法', 313601, 0, 16, 270, 3001, 2, 3.0, 0, 0, 0, '为自身天赋之才与过分敏锐的感觉而苦恼的幽静歌姬。\\n不爱与人相争，但会为了愿望和同伴而奋战。\\n\\n·必杀技能会对前方范围内的敌人造成魔法伤害（中），\\n\u3000并使其陷入结晶化状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000处于结晶化状态的敌人将无法行动，\\n\u3000并受到持续伤害。\\n\u3000完全回复生命值或经过一定时间\\n\u3000即可解除结晶化状态。\\n·对前方范围内的敌人赋予猛毒状态。\\n\u3000对处于结晶化状态的敌人造成\\n\u3000追加的魔法伤害（大）。\\n·对前方第一名角色造成魔法伤害（小）。');
insert into unit_enemy_data values (313700, '美空', 313700, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '总是活泼开朗，能和任何人成为朋友的活力少女。\\n爱用转轮式枪械，单枪匹马展开饱和攻击。\\n\\n·战斗开始时，召唤潘坡克林。\\n\u3000召唤时击退前方范围内的所有敌人。\\n\u3000潘坡克林会对前方两名角色造成\\n\u3000物理伤害（大）。\\n·必杀技能会对敌方全体造成一定比例的物理伤害。\\n\u3000同时，大幅提升潘坡克林的物理攻击力和\\n\u3000行动速度。\\n·回复潘坡克林的生命值。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (313701, '美空', 313701, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '总是活泼开朗，能和任何人成为朋友的活力少女。\\n爱用转轮式枪械，单枪匹马展开饱和攻击。\\n\\n·战斗开始时，召唤潘坡克林。\\n\u3000召唤时击退前方范围内的所有敌人。\\n\u3000潘坡克林会对前方两名角色造成\\n\u3000物理伤害（大）。\\n·必杀技能会对敌方全体造成一定比例的物理伤害。\\n\u3000同时，大幅提升潘坡克林的物理攻击力和\\n\u3000行动速度。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。\\n·回复潘坡克林的生命值。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (313800, '阿索尔德', 313800, 0, 16, 270, 3000, 1, 1.5, 0, 0, 0, '总是藏着好几个杀手锏的大胃王绅士。\\n驱使万能人偶，靠智谋战略将敌人逼入绝境。\\n\\n·必杀技能会根据阿索尔德的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n\u3000同时，大幅降低阿索尔德的物理防御力\\n\u3000和魔法防御力。\\n·召唤万能人偶，击退敌方全体。\\n\u3000万能人偶对前方第一名角色造成\\n\u3000物理伤害（小）。\\n\u3000若万能人偶的生命值降至一定值以下，\\n\u3000则大幅降低阿索尔德的物理防御力和\\n\u3000魔法防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (313801, '阿索尔德', 313801, 0, 16, 270, 3000, 1, 1.5, 0, 0, 0, '总是藏着好几个杀手锏的大胃王绅士。\\n驱使万能人偶，靠智谋战略将敌人逼入绝境。\\n\\n·必杀技能会根据阿索尔德的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n\u3000同时，大幅降低阿索尔德的物理防御力\\n\u3000和魔法防御力。\\n·召唤万能人偶，击退敌方全体。\\n\u3000万能人偶对前方第一名角色造成\\n\u3000物理伤害（小）。\\n\u3000若万能人偶的生命值降至一定值以下，\\n\u3000则大幅降低阿索尔德的物理防御力和\\n\u3000魔法防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (313900, '泽恩', 313900, 0, 16, 270, 3000, 1, 1.0, 313900, 313900, 0, '拥有一骑当千、万夫莫开的实力，沉默寡言的剑士。\\n觉醒之后，龙将翱翔于更高的天际。\\n\\n·必杀技能会根据泽恩的物理攻击力和\\n\u3000剩余生命值，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n\u3000该技能造成的伤害，\\n\u3000技能值回复量将减少。\\n·对敌方全体造成物理伤害（中），\\n\u3000并使其眩晕。\\n·大幅提升泽恩的技能值上升量、\\n\u3000行动速度、物理攻击力。\\n\u3000这个技能的效果每次发动时都会提升。\\n\u3000第3次发动时，技能值会再提升1000。\\n·对前方第一名角色造成物理伤害（中），\\n\u3000并将其小幅度击飞。');
